INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (1, 'Fragaria × ananassa', 'Sweet, juicy berries perfect for jams and preserves', '{"tart": 6, "sweet": 8, "acidic": 4, "floral": 3}', '["jam", "preserves", "compote", "syrup"]', 'Summer', 'Store in refrigerator for up to 5 days. Freeze for longer storage.', '{"fiber": "2g", "sugar": "4.9g", "calories": 32, "vitamin_c": "58mg"}', '2025-09-21 22:17:57.542551');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (15, 'Prunus armeniaca', 'Apricots are small, golden-orange stone fruits with velvety skin and sweet, slightly tart flesh. They have a distinctive aroma and are often dried for preservation. The pit contains a kernel that is sometimes used in cooking.', '{"tart": 5, "notes": "Sweet with subtle tartness and distinctive aroma", "sweet": 7, "acidic": 5, "earthy": 3, "floral": 4}', '{"fiber": 2.0, "acidity": 3.3, "sugar_content": 9.2, "water_content": 86.4, "pectin_content": 25}', 'Summer (June-August in most regions)', 'Store unripe apricots at room temperature until slightly soft. Once ripe, refrigerate for up to 5 days. Handle gently as they bruise easily. For longer storage, dry or freeze.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash gently and remove the pit by cutting around it. Can be used with or without skin. For jams, peel if desired and slice the flesh. The natural sweetness makes excellent jams with minimal added sugar.", "jam_properties": {"fiber": 2.0, "acidity": 3.3, "sugar_content": 9.2, "water_content": 86.4, "pectin_content": 25}, "country_of_origin": "China"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (2, 'Vaccinium corymbosum', 'Blueberries are small, round berries with a deep blue-purple color and a sweet, slightly tart flavor. They grow on shrubs and are known for their high antioxidant content and distinctive blue skin.', '{"tart": 5, "notes": "Sweet with subtle tartness and floral undertones", "sweet": 7, "acidic": 3, "earthy": 1, "floral": 2}', '{"fiber": 2.4, "acidity": 3.2, "sugar_content": 10.0, "water_content": 84.2, "pectin_content": 25}', 'Summer (June-August in most regions)', 'Store unwashed in the refrigerator for up to 10 days. Spread in a single layer to prevent crushing. For longer storage, freeze on a baking sheet, then transfer to freezer bags. Frozen blueberries work excellently for jams.', '{"image_url": "https://images.unsplash.com/photo-1498557850523-fd3d118b962e", "preparation": "Wash gently just before using. Remove any stems or leaves. For jams, can be used whole or lightly crushed to release juices. Small berries work well whole in jams.", "jam_properties": {"fiber": 2.4, "acidity": 3.2, "sugar_content": 10.0, "water_content": 84.2, "pectin_content": 25}, "country_of_origin": "North America"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (9, 'Prunus avium', 'Cherries are small, round stone fruits that grow in clusters. Sweet cherries have firm, sweet flesh, while sour cherries are more tart. They have a single pit and come in various colors from deep red to yellow.', '{"tart": 6, "notes": "Sweet with subtle tartness and floral notes", "sweet": 8, "acidic": 5, "earthy": 2, "floral": 4}', '{"fiber": 2.1, "acidity": 3.2, "sugar_content": 12.8, "water_content": 82.2, "pectin_content": 25}', 'Summer (June-July in most regions)', 'Store fresh cherries in the refrigerator for up to 1 week. Keep stems attached to maintain freshness. For longer storage, pit and freeze. Handle gently as they bruise easily.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash gently and remove stems. Pit the cherries by cutting around the stone or using a cherry pitter. For jams, can be used whole or halved. The natural sweetness makes excellent jams with good color.", "jam_properties": {"fiber": 2.1, "acidity": 3.2, "sugar_content": 12.8, "water_content": 82.2, "pectin_content": 25}, "country_of_origin": "Europe and Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (12, 'Vaccinium macrocarpon', 'Cranberries are small, round, red berries that grow on low-lying vines in bogs. They have a very tart, astringent flavor and are known for their high antioxidant content and traditional use in sauces and beverages.', '{"tart": 10, "notes": "Very tart and astringent, requires sweetening for most uses", "sweet": 2, "acidic": 9, "earthy": 2, "floral": 1}', '{"fiber": 4.6, "acidity": 2.3, "sugar_content": 4.0, "water_content": 87.1, "pectin_content": 40}', 'Fall (September-November in most regions)', 'Store fresh cranberries in the refrigerator for up to 2 months. They can also be frozen for up to a year. Cranberries are excellent for jams when combined with sweeteners and other fruits.', '{"image_url": "https://images.unsplash.com/photo-1578662996442-48f60103fc96", "preparation": "Wash thoroughly and sort, removing any soft or damaged berries. For jams, can be used whole or chopped. Often combined with sweeter fruits or significant amounts of sugar due to their extreme tartness.", "jam_properties": {"fiber": 4.6, "acidity": 2.3, "sugar_content": 4.0, "water_content": 87.1, "pectin_content": 40}, "country_of_origin": "North America"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (14, 'Ribes rubrum', 'Currants are small, round berries that grow in clusters on bushes. They come in red, black, and white varieties, each with distinct flavors. Red currants are tart and bright, while black currants are more intense and earthy.', '{"tart": 9, "notes": "Very tart with bright, acidic flavor and subtle floral notes", "sweet": 4, "acidic": 8, "earthy": 3, "floral": 3}', '{"fiber": 4.3, "acidity": 2.9, "sugar_content": 7.4, "water_content": 83.9, "pectin_content": 45}', 'Summer (June-August in most regions)', 'Store fresh currants in the refrigerator for 3-5 days. Handle gently as they are delicate. For longer storage, freeze on a baking sheet, then transfer to freezer bags. Frozen currants work well for jams.', '{"image_url": "https://images.unsplash.com/photo-1578662996442-48f60103fc96", "preparation": "Wash gently and remove stems. For jams, can be used whole or lightly crushed. The high tartness makes them excellent for jams when combined with sugar and other fruits.", "jam_properties": {"fiber": 4.3, "acidity": 2.9, "sugar_content": 7.4, "water_content": 83.9, "pectin_content": 45}, "country_of_origin": "Europe"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (23, 'Hylocereus undatus', 'Dragon fruit, also known as pitaya, is a tropical fruit with a bright pink or yellow rind and white or red flesh dotted with small black seeds. It has a mild, sweet flavor and is known for its striking appearance and high water content.', '{"tart": 2, "notes": "Mild and sweet with subtle flavor", "sweet": 6, "acidic": 3, "earthy": 1, "floral": 2}', '{"fiber": 0.9, "acidity": 3.5, "sugar_content": 7.7, "water_content": 87.0, "pectin_content": 10}', 'Year-round in most regions', 'Store at room temperature until slightly soft to touch. Once ripe, refrigerate for up to 5 days. Handle gently as they bruise easily. The flesh can be frozen for longer storage.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Cut the fruit in half and scoop out the flesh. The seeds are edible and add texture. For jams, use the flesh and seeds. The mild flavor makes excellent jams when combined with other fruits or significant amounts of sugar.", "jam_properties": {"fiber": 0.9, "acidity": 3.5, "sugar_content": 7.7, "water_content": 87.0, "pectin_content": 10}, "country_of_origin": "Central America"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (13, 'Ribes uva-crispa', 'Gooseberries are small, round berries that can be green, yellow, or red when ripe. They have a tart, slightly sweet flavor and are covered in fine hairs. They grow on thorny bushes and are popular in European cuisine.', '{"tart": 8, "notes": "Tart with subtle sweetness, becoming sweeter as they ripen", "sweet": 5, "acidic": 7, "earthy": 2, "floral": 2}', '{"fiber": 4.3, "acidity": 2.9, "sugar_content": 8.1, "water_content": 87.9, "pectin_content": 35}', 'Summer (June-August in most regions)', 'Store unripe gooseberries at room temperature until they reach desired ripeness. Ripe berries can be refrigerated for up to 2 weeks. For longer storage, freeze on a baking sheet, then transfer to freezer bags.', '{"image_url": "https://images.unsplash.com/photo-1578662996442-48f60103fc96", "preparation": "Wash thoroughly and remove stems and any remaining flower remnants. For jams, can be used whole or halved. The tart flavor makes them excellent for jams when combined with sugar.", "jam_properties": {"fiber": 4.3, "acidity": 2.9, "sugar_content": 8.1, "water_content": 87.9, "pectin_content": 35}, "country_of_origin": "Europe"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (4, 'Citrus limon', 'Lemons are oval citrus fruits with a bright yellow rind and very tart, acidic juice. They are primarily used for their juice and zest in cooking and baking, and are known for their high vitamin C content and preservative properties.', '{"tart": 10, "notes": "Very tart and acidic, primarily used for juice and zest", "sweet": 2, "acidic": 10, "earthy": 1, "floral": 2}', '{"fiber": 2.8, "acidity": 2.0, "sugar_content": 2.5, "water_content": 89.0, "pectin_content": 60}', 'Year-round in most regions', 'Store at room temperature for up to 1 week or refrigerate for up to 1 month. Lemons can be juiced and frozen for longer storage. The juice can be frozen in ice cube trays for convenient use.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash the rind before using. Can be juiced, zested, or sliced. For jams, use the juice and zest for flavoring and acidity. Lemons are rarely made into jam alone due to extreme tartness, but are excellent for adding acidity to other fruit jams.", "jam_properties": {"fiber": 2.8, "acidity": 2.0, "sugar_content": 2.5, "water_content": 89.0, "pectin_content": 60}, "country_of_origin": "Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (18, 'Citrus × aurantiifolia', 'Limes are small, round citrus fruits with a thin, green rind and very tart, acidic juice. They are smaller than lemons and have a more intense, floral flavor. Key limes are particularly prized for their distinctive flavor.', '{"tart": 10, "notes": "Very tart and acidic with floral undertones", "sweet": 1, "acidic": 10, "earthy": 1, "floral": 4}', '{"fiber": 2.8, "acidity": 2.0, "sugar_content": 1.7, "water_content": 88.3, "pectin_content": 60}', 'Year-round in most regions', 'Store at room temperature for up to 1 week or refrigerate for up to 2 weeks. Limes can be juiced and frozen for longer storage. The juice can be frozen in ice cube trays for convenient use.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash the rind before using. Can be juiced, zested, or sliced. For jams, use the juice and zest for flavoring and acidity. Limes are rarely made into jam alone due to extreme tartness, but are excellent for adding acidity and flavor to other fruit jams.", "jam_properties": {"fiber": 2.8, "acidity": 2.0, "sugar_content": 1.7, "water_content": 88.3, "pectin_content": 60}, "country_of_origin": "Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (20, 'Mangifera indica', 'Mangoes are tropical stone fruits with sweet, creamy flesh and a large, flat pit. They have a distinctive sweet flavor and come in various shapes and sizes, with skin colors ranging from green to yellow to red when ripe.', '{"tart": 2, "notes": "Sweet and creamy with tropical floral notes", "sweet": 9, "acidic": 3, "earthy": 2, "floral": 4}', '{"fiber": 1.6, "acidity": 3.4, "sugar_content": 13.7, "water_content": 83.5, "pectin_content": 15}', 'Spring to summer (March-July in most regions)', 'Store unripe mangoes at room temperature until slightly soft to touch. Once ripe, refrigerate for up to 5 days. For longer storage, slice and freeze. Handle gently as they bruise easily.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash the skin before cutting. Cut around the large, flat pit and slice or dice the flesh. For jams, peel and slice the flesh, discarding the pit. The sweet, creamy texture makes excellent smooth jams.", "jam_properties": {"fiber": 1.6, "acidity": 3.4, "sugar_content": 13.7, "water_content": 83.5, "pectin_content": 15}, "country_of_origin": "South Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (17, 'Prunus persica var. nucipersica', 'Nectarines are smooth-skinned stone fruits that are essentially fuzzless peaches. They have sweet, juicy flesh with a more intense flavor than peaches and come in yellow and white varieties.', '{"tart": 4, "notes": "Sweet and floral with more intense flavor than peaches", "sweet": 8, "acidic": 4, "earthy": 2, "floral": 5}', '{"fiber": 1.5, "acidity": 3.4, "sugar_content": 8.4, "water_content": 89.1, "pectin_content": 20}', 'Summer (June-August in most regions)', 'Store unripe nectarines at room temperature until slightly soft to touch. Once ripe, refrigerate for up to 5 days. Handle gently to avoid bruising. For longer storage, slice and freeze.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash gently - no need to peel as the skin is smooth and edible. Cut around the pit and slice or dice. For jams, can be used with skin or peeled. The smooth texture and intense flavor make excellent jams.", "jam_properties": {"fiber": 1.5, "acidity": 3.4, "sugar_content": 8.4, "water_content": 89.1, "pectin_content": 20}, "country_of_origin": "China"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (24, 'Carica papaya', 'Papayas are tropical fruits with a soft, orange flesh and a large central cavity filled with black seeds. They have a sweet, musky flavor and are known for their high vitamin C content and digestive enzymes.', '{"tart": 2, "notes": "Sweet and musky with tropical flavor", "sweet": 7, "acidic": 3, "earthy": 2, "floral": 3}', '{"fiber": 1.7, "acidity": 3.4, "sugar_content": 7.8, "water_content": 88.1, "pectin_content": 15}', 'Year-round in most regions', 'Store unripe papayas at room temperature until slightly soft to touch. Once ripe, refrigerate for up to 3 days. The flesh can be frozen for longer storage. Handle gently as they bruise easily.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Cut the fruit in half lengthwise and scoop out the seeds. The seeds are edible but have a peppery flavor. For jams, use the flesh and discard the seeds. The soft texture makes excellent smooth jams.", "jam_properties": {"fiber": 1.7, "acidity": 3.4, "sugar_content": 7.8, "water_content": 88.1, "pectin_content": 15}, "country_of_origin": "Central America"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (22, 'Passiflora edulis', 'Passion fruits are small, round fruits with a tough, purple or yellow rind and sweet, tart pulp filled with edible seeds. They have a distinctive tropical flavor and are known for their high fiber content and unique appearance.', '{"tart": 8, "notes": "Sweet and tart with tropical floral notes", "sweet": 6, "acidic": 7, "earthy": 2, "floral": 5}', '{"fiber": 10.4, "acidity": 3.0, "sugar_content": 11.2, "water_content": 72.9, "pectin_content": 25}', 'Year-round in most regions', 'Store at room temperature until the rind becomes slightly wrinkled, indicating ripeness. Once ripe, refrigerate for up to 1 week. The pulp can be frozen for longer storage.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Cut the fruit in half and scoop out the pulp and seeds. The seeds are edible and add texture. For jams, use the pulp and seeds. The unique flavor makes excellent jams when combined with sugar.", "jam_properties": {"fiber": 10.4, "acidity": 3.0, "sugar_content": 11.2, "water_content": 72.9, "pectin_content": 25}, "country_of_origin": "South America"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (6, 'Prunus persica', 'Peaches are soft, fuzzy-skinned stone fruits with sweet, juicy flesh surrounding a large pit. They have a delicate aroma and come in yellow and white varieties, each with distinct flavor profiles.', '{"tart": 3, "notes": "Sweet and floral with delicate aroma", "sweet": 8, "acidic": 4, "earthy": 2, "floral": 5}', '{"fiber": 1.5, "acidity": 3.4, "sugar_content": 8.4, "water_content": 89.1, "pectin_content": 20}', 'Summer (June-August in most regions)', 'Store unripe peaches at room temperature until slightly soft to touch. Once ripe, refrigerate for up to 5 days. Handle gently to avoid bruising. For longer storage, slice and freeze.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash gently and remove the fuzzy skin if desired. Cut around the pit and slice or dice. For jams, peel and slice the flesh, discarding the pit. The soft texture makes excellent smooth jams.", "jam_properties": {"fiber": 1.5, "acidity": 3.4, "sugar_content": 8.4, "water_content": 89.1, "pectin_content": 20}, "country_of_origin": "China"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (21, 'Ananas comosus', 'Pineapples are tropical fruits with a tough, spiky exterior and sweet, tangy flesh. They have a distinctive cylindrical shape with a crown of spiky leaves and are known for their high vitamin C content and unique flavor.', '{"tart": 6, "notes": "Sweet and tangy with tropical flavor", "sweet": 7, "acidic": 6, "earthy": 1, "floral": 2}', '{"fiber": 1.4, "acidity": 3.3, "sugar_content": 9.9, "water_content": 86.0, "pectin_content": 20}', 'Year-round in most regions', 'Store whole pineapples at room temperature for up to 2 days or refrigerate for up to 5 days. Once cut, store in the refrigerator for up to 3 days. For longer storage, cut and freeze.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Cut off the crown and base, then slice off the skin. Remove the core and slice or dice the flesh. For jams, cut the flesh into small pieces. The tangy flavor makes excellent jams when combined with sugar.", "jam_properties": {"fiber": 1.4, "acidity": 3.3, "sugar_content": 9.9, "water_content": 86.0, "pectin_content": 20}, "country_of_origin": "South America"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (26, 'Cydonia oblonga', 'Quinces are round or pear-shaped fruits with a golden-yellow skin and hard, astringent flesh. They are rarely eaten raw due to their tartness and hardness, but are excellent for cooking, jams, and preserves. They have a distinctive floral aroma.', '{"tart": 8, "notes": "Tart and floral with distinctive aroma", "sweet": 4, "acidic": 7, "earthy": 2, "floral": 6}', '{"fiber": 1.9, "acidity": 3.1, "sugar_content": 7.0, "water_content": 83.8, "pectin_content": 50}', 'Fall (September-November in most regions)', 'Store at room temperature for up to 2 weeks or refrigerate for up to 2 months. Quinces can be stored for extended periods when properly handled. The hard texture makes them durable.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash thoroughly and remove the core and seeds. The flesh is very hard and astringent when raw, so it must be cooked. For jams, peel and slice the flesh, then cook until soft. The high pectin content makes excellent jams with good gel formation.", "jam_properties": {"fiber": 1.9, "acidity": 3.1, "sugar_content": 7.0, "water_content": 83.8, "pectin_content": 50}, "country_of_origin": "Southwest Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (8, 'Rubus idaeus', 'Raspberries are aggregate fruits composed of many small drupelets around a central core. They have a delicate, sweet-tart flavor and are known for their distinctive hollow center when picked.', '{"tart": 8, "notes": "Delicate sweet-tart flavor with floral notes", "sweet": 6, "acidic": 6, "earthy": 2, "floral": 4}', '{"fiber": 6.5, "acidity": 3.0, "sugar_content": 4.4, "water_content": 85.8, "pectin_content": 35}', 'Summer (June-August in most regions)', 'Store unwashed in the refrigerator for 2-3 days. Handle gently as they are very delicate. For longer storage, freeze on a baking sheet, then transfer to freezer bags. Frozen raspberries are excellent for jams.', '{"image_url": "https://images.unsplash.com/photo-1559181567-c3190ca9959b", "preparation": "Wash very gently just before using. Remove any stems or leaves. For jams, can be used whole or lightly crushed. The delicate structure makes them ideal for smooth jams.", "jam_properties": {"fiber": 6.5, "acidity": 3.0, "sugar_content": 4.4, "water_content": 85.8, "pectin_content": 35}, "country_of_origin": "Europe and Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (3, 'Malus domestica', 'Apples are round, crisp fruits with a thin skin that can be red, green, or yellow. They have sweet, juicy flesh and are one of the most widely cultivated tree fruits worldwide. The flesh surrounds a core containing small seeds.', '{"tart": 5, "notes": "Sweet and crisp with subtle tartness", "sweet": 7, "acidic": 4, "earthy": 2, "floral": 2}', '{"fiber": 2.4, "acidity": 3.3, "sugar_content": 10.4, "water_content": 85.6, "pectin_content": 40}', 'Late summer to early winter (August-December in mo', 'Store in a cool, dry place or refrigerate for up to 2 months. Keep away from strong-smelling foods as apples can absorb odors. Handle gently to avoid bruising.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash thoroughly before eating. Can be eaten with or without skin. For jams, peel if desired and slice the flesh, discarding the core. The high pectin content makes excellent jams with good gel formation.", "jam_properties": {"fiber": 2.4, "acidity": 3.3, "sugar_content": 10.4, "water_content": 85.6, "pectin_content": 40}, "country_of_origin": "Central Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (7, 'Rubus fruticosus', 'Blackberries are aggregate fruits similar to raspberries but with a solid core. They have a deep purple-black color and a sweet-tart flavor that becomes sweeter as they ripen.', '{"tart": 7, "notes": "Sweet-tart flavor with earthy undertones", "sweet": 6, "acidic": 5, "earthy": 3, "floral": 3}', '{"fiber": 5.3, "acidity": 3.3, "sugar_content": 4.9, "water_content": 88.2, "pectin_content": 30}', 'Late summer to early fall (July-September in most ', 'Store unwashed in the refrigerator for 3-5 days. Handle gently to avoid crushing. For longer storage, freeze on a baking sheet, then transfer to freezer bags. Frozen blackberries work well for jams.', '{"image_url": "https://images.unsplash.com/photo-1566385101042-1a0aa0c1268c", "preparation": "Wash gently just before using. Remove any stems or leaves. For jams, can be used whole or lightly crushed. The solid core makes them more robust than raspberries.", "jam_properties": {"fiber": 5.3, "acidity": 3.3, "sugar_content": 4.9, "water_content": 88.2, "pectin_content": 30}, "country_of_origin": "Europe and North America"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (11, 'Sambucus nigra', 'Elderberries are small, dark purple-black berries that grow in clusters on elder trees. They have a tart, slightly bitter flavor and are commonly used in traditional medicine and culinary applications.', '{"tart": 9, "notes": "Tart and slightly bitter with earthy undertones", "sweet": 3, "acidic": 8, "earthy": 4, "floral": 2}', '{"fiber": 7.0, "acidity": 2.8, "sugar_content": 7.4, "water_content": 79.8, "pectin_content": 20}', 'Late summer to early fall (August-September in mos', 'Store fresh elderberries in the refrigerator for 2-3 days. For longer storage, freeze on a baking sheet, then transfer to freezer bags. Elderberries are excellent for jams and syrups when cooked.', '{"image_url": "https://images.unsplash.com/photo-1578662996442-48f60103fc96", "preparation": "Wash thoroughly and remove stems. Elderberries should always be cooked before consumption as raw berries can cause stomach upset. For jams, cook until soft and strain if desired for smoother texture.", "jam_properties": {"fiber": 7.0, "acidity": 2.8, "sugar_content": 7.4, "water_content": 79.8, "pectin_content": 20}, "country_of_origin": "Europe"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (27, 'Ficus carica', 'Figs are small, pear-shaped fruits with a thin, purple or green skin and sweet, soft flesh filled with tiny edible seeds. They have a distinctive sweet flavor and are often dried for preservation. The flesh is soft and can be eaten whole.', '{"tart": 2, "notes": "Very sweet with distinctive flavor", "sweet": 9, "acidic": 3, "earthy": 2, "floral": 3}', '{"fiber": 2.9, "acidity": 3.4, "sugar_content": 16.3, "water_content": 79.1, "pectin_content": 25}', 'Summer to early fall (July-September in most regio', 'Store fresh figs in the refrigerator for up to 3 days. Handle gently as they are very delicate and bruise easily. For longer storage, dry or freeze. The soft texture makes them ideal for jams.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash gently and remove the stem. Can be eaten whole, including the skin. For jams, can be used whole or halved. The soft texture and high sugar content make excellent smooth jams with minimal added sugar.", "jam_properties": {"fiber": 2.9, "acidity": 3.4, "sugar_content": 16.3, "water_content": 79.1, "pectin_content": 25}, "country_of_origin": "Middle East"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (10, 'Vitis vinifera', 'Grapes are small, round fruits that grow in clusters on vines. They come in various colors including green, red, and purple, and can be sweet or tart depending on the variety. They are commonly used for eating fresh, making wine, or drying into raisins.', '{"tart": 4, "notes": "Sweet with subtle tartness", "sweet": 8, "acidic": 4, "earthy": 2, "floral": 2}', '{"fiber": 0.9, "acidity": 3.2, "sugar_content": 15.5, "water_content": 81.0, "pectin_content": 30}', 'Late summer to early fall (August-October in most ', 'Store fresh grapes in the refrigerator for up to 1 week. Keep in a perforated bag to maintain moisture. For longer storage, freeze or dry into raisins. Handle gently as they bruise easily.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash thoroughly and remove any stems. Can be eaten whole, including the skin and seeds. For jams, can be used whole or halved. The natural sweetness and good pectin content make excellent jams with good color and flavor.", "jam_properties": {"fiber": 0.9, "acidity": 3.2, "sugar_content": 15.5, "water_content": 81.0, "pectin_content": 30}, "country_of_origin": "Near East"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (19, 'Citrus × paradisi', 'Grapefruits are large citrus fruits with a thick, yellow or pink rind and segmented flesh that can be white, pink, or red. They have a distinctive bitter-sweet flavor and are known for their high vitamin C content and potential health benefits.', '{"tart": 7, "notes": "Bitter-sweet with distinctive citrus flavor", "sweet": 6, "acidic": 6, "earthy": 1, "floral": 2}', '{"fiber": 1.0, "acidity": 3.0, "sugar_content": 6.9, "water_content": 88.1, "pectin_content": 45}', 'Winter to early spring (December-April in most reg', 'Store at room temperature for up to 1 week or refrigerate for up to 2 weeks. Grapefruits can be stored for extended periods when properly handled. The rind helps protect the fruit.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash the rind before using. Can be peeled and eaten fresh, juiced, or segmented. For jams, use the juice and zest. The bitter flavor makes them interesting for jams when combined with sweeter fruits or significant amounts of sugar.", "jam_properties": {"fiber": 1.0, "acidity": 3.0, "sugar_content": 6.9, "water_content": 88.1, "pectin_content": 45}, "country_of_origin": "Barbados"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (29, 'Actinidia deliciosa', 'Kiwi, also known as Chinese gooseberry, is a small, oval-shaped fruit with a fuzzy brown skin and vibrant green flesh speckled with tiny black seeds. It belongs to the Actinidiaceae family and is native to China.', '{"tart": 8, "notes": "Sweet and tangy flavor with tropical characteristics", "sweet": 7, "acidic": 8, "earthy": 1, "floral": 2}', '{"fiber": 3.0, "acidity": 3.25, "sugar_content": 8.9, "water_content": 83.1, "pectin_content": 15}', 'Fall to early winter (October-December in most reg', 'Store unripe kiwi at room temperature until slightly soft to touch. Once ripe, refrigerate for up to 2 weeks. Cut kiwi should be consumed within 2-3 days.', '{"image_url": "https://images.unsplash.com/photo-1571772996211-2f02c9727629", "preparation": "Wash the skin thoroughly. Can be eaten with skin (after washing) or peeled. For jams, peel and slice or puree the flesh. Remove any hard core if present.", "jam_properties": {"fiber": 3.0, "acidity": 3.25, "sugar_content": 8.9, "water_content": 83.1, "pectin_content": 15}, "country_of_origin": "China"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (5, 'Citrus × sinensis', 'Oranges are round citrus fruits with a thick, orange-colored rind and sweet, juicy flesh divided into segments. They are one of the most popular citrus fruits worldwide and are known for their high vitamin C content.', '{"tart": 4, "notes": "Sweet and tangy with bright citrus flavor", "sweet": 8, "acidic": 5, "earthy": 1, "floral": 3}', '{"fiber": 2.4, "acidity": 3.1, "sugar_content": 9.4, "water_content": 86.8, "pectin_content": 50}', 'Winter to early spring (December-March in most reg', 'Store at room temperature for up to 1 week or refrigerate for up to 2 weeks. Keep in a cool, dry place away from direct sunlight. Oranges can be stored for extended periods when properly handled.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash the rind before using. Can be peeled and eaten fresh, juiced, or zested for flavoring. For jams, use the juice and zest. The high pectin content in the pith makes excellent jams.", "jam_properties": {"fiber": 2.4, "acidity": 3.1, "sugar_content": 9.4, "water_content": 86.8, "pectin_content": 50}, "country_of_origin": "China"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (25, 'Pyrus communis', 'Pears are bell-shaped fruits with a thin skin that can be green, yellow, or red. They have sweet, juicy flesh that becomes softer as they ripen. The flesh surrounds a core containing small seeds.', '{"tart": 3, "notes": "Sweet and floral with delicate flavor", "sweet": 8, "acidic": 3, "earthy": 2, "floral": 4}', '{"fiber": 3.1, "acidity": 3.2, "sugar_content": 9.8, "water_content": 83.7, "pectin_content": 35}', 'Late summer to early winter (August-December in mo', 'Store unripe pears at room temperature until slightly soft to touch. Once ripe, refrigerate for up to 5 days. Handle gently as they bruise easily. The soft texture makes them ideal for jams.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash thoroughly before eating. Can be eaten with or without skin. For jams, peel if desired and slice the flesh, discarding the core. The soft texture and high pectin content make excellent smooth jams.", "jam_properties": {"fiber": 3.1, "acidity": 3.2, "sugar_content": 9.8, "water_content": 83.7, "pectin_content": 35}, "country_of_origin": "Europe and Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (16, 'Prunus domestica', 'Plums are round or oval stone fruits with smooth skin that can be purple, red, yellow, or green. They have sweet, juicy flesh and a single large pit. Different varieties range from very sweet to tart.', '{"tart": 4, "notes": "Sweet and juicy with subtle tartness", "sweet": 7, "acidic": 4, "earthy": 2, "floral": 3}', '{"fiber": 1.4, "acidity": 3.2, "sugar_content": 9.9, "water_content": 87.2, "pectin_content": 30}', 'Summer to early fall (July-September in most regio', 'Store unripe plums at room temperature until slightly soft. Once ripe, refrigerate for up to 5 days. Handle gently to avoid bruising. For longer storage, freeze or dry.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash gently and remove the pit by cutting around it. Can be used with or without skin. For jams, peel if desired and slice the flesh. The natural sweetness and good pectin content make excellent jams.", "jam_properties": {"fiber": 1.4, "acidity": 3.2, "sugar_content": 9.9, "water_content": 87.2, "pectin_content": 30}, "country_of_origin": "Europe and Asia"}', '2025-09-24 09:29:00');
INSERT INTO public.profiles (fruit_id, scientific_name, description, flavor_profile, jam_uses, season, storage_tips, nutrition, created_date) VALUES (28, 'Rheum rhabarbarum', 'Rhubarb is a perennial plant with thick, celery-like stalks that are red, pink, or green. The stalks are tart and are used in cooking and baking, while the leaves are toxic and should never be consumed. It has a distinctive tart flavor.', '{"tart": 10, "notes": "Very tart and acidic, requires sweetening", "sweet": 1, "acidic": 9, "earthy": 3, "floral": 1}', '{"fiber": 1.8, "acidity": 2.8, "sugar_content": 1.1, "water_content": 93.6, "pectin_content": 30}', 'Spring to early summer (April-June in most regions', 'Store fresh rhubarb in the refrigerator for up to 1 week. Wrap in damp paper towels to maintain moisture. For longer storage, cut into pieces and freeze. The stalks can be stored for extended periods when properly handled.', '{"image_url": "https://images.unsplash.com/photo-1571771909219-9a6b2b1b5b0b", "preparation": "Wash thoroughly and remove any leaves (which are toxic). Cut off the ends and slice the stalks. For jams, cut into small pieces and cook until soft. The extreme tartness makes them excellent for jams when combined with significant amounts of sugar and other fruits.", "jam_properties": {"fiber": 1.8, "acidity": 2.8, "sugar_content": 1.1, "water_content": 93.6, "pectin_content": 30}, "country_of_origin": "Asia"}', '2025-09-24 09:29:00');
