INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (857, 'Pineapple-Peach Jam', '[{"item": "8  overripe peaches, peeled and halved", "name": "overripe peaches, peeled and halved", "unit": "", "quantity": "8"}, {"item": "1  ripe pineapple, peeled and cut into 1-inch pieces", "name": "ripe pineapple, peeled and cut into 1-inch pieces", "unit": "", "quantity": "1"}, {"item": "2  lemons, juiced", "name": "lemons, juiced", "unit": "", "quantity": "2"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}, {"item": "7 tablespoons regular pectin", "name": "regular pectin", "unit": "tablespoons", "quantity": "7"}]', '["Inspect 8 to 10 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Cook and stir peaches and pineapple in a large pot over medium heat until fruit juices are released, 6 to 10 minutes; stir in lemon juice. Add sugar, stir, then add pectin; cook and stir until slightly thickened, about 2 minutes.", "Transfer jam into hot sterile jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder.", "Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes."]', 4.70, 6, 'AllRecipes', 'https://www.allrecipes.com/recipe/264327/pineapple-peach-jam/', 'https://www.allrecipes.com/thmb/SCWgDl5L6zW15XooVrXoNKt82ys=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1312847-ce6e98352846447ab5f251f96706ec0e.jpg', '64 servings', '', '', '', '2025-09-27 08:38:28.202416', '2025-09-27 08:38:28.202416');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (858, 'Peach Jam with Amaretto Liqueur', '[{"item": "4 ½ cups finely chopped, peeled peaches", "name": "finely chopped, peeled peaches", "unit": "cups", "quantity": "4 ½"}, {"item": "2 tablespoons lemon juice", "name": "lemon juice", "unit": "tablespoons", "quantity": "2"}, {"item": "3 cups sugar", "name": "sugar", "unit": "cups", "quantity": "3"}, {"item": "1 (1.75 ounce) package fruit pectin (such as Sure-Jell® Pectin Light for less or no sugar)", "name": "fruit pectin (such as Sure-Jell® Pectin Light for less or no sugar)", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "⅓ cup amaretto liqueur, or more to taste", "name": "amaretto liqueur, or more to taste", "unit": "cup", "quantity": "⅓"}]', '["Inspect 5 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Mix peaches with lemon juice in a large pot immediately after chopping to prevent peaches from turning brown. Place pot over medium heat.", "Mix 1/4 cup of the sugar with pectin and add it to the pot with the peaches. Stir well. Slowly bring to a full rolling boil that does not stop bubbling when stirred. Add the remaining sugar and stir well, scraping the bottom of the pot, to fully dissolve the sugar. Cook for 1 minute, stirring constantly.", "Remove from heat and stir in amaretto liqueur.", "Ladle peach jam immediately into the prepared jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any spills. Top with lids and tightly screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart. Let rest for 24 hours without moving the jars. Gently press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 4.30, 9, 'AllRecipes', 'https://www.allrecipes.com/recipe/265387/peach-jam-with-amaretto-liqueur/', 'https://www.allrecipes.com/thmb/u7HkB77TQBt7UsX9ZrLnjjMVRaQ=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/6859796-b8ea9fcd487149fca9d37c97d1704973.jpg', '90 servings', '', '', '', '2025-09-27 08:38:28.203226', '2025-09-27 08:38:28.203226');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (859, 'Aunt Patsi''s Easy Peach Jam', '[{"item": "2 ½ pounds fresh peaches", "name": "fresh peaches", "unit": "pounds", "quantity": "2 ½"}, {"item": "5 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "5"}, {"item": "3 (3 ounce) packages peach-flavored gelatin mix (such as Jell-O)", "name": "peach-flavored gelatin mix (such as Jell-O)", "unit": "(3 ounce) packages", "quantity": "3"}, {"item": "2 teaspoons fresh lemon juice", "name": "fresh lemon juice", "unit": "teaspoons", "quantity": "2"}, {"item": "½ teaspoon almond extract", "name": "almond extract", "unit": "teaspoon", "quantity": "½"}]', '["Inspect five 12-ounce jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm, soapy water.", "Bring a large pot of water to a boil. Add peaches; cook until skins loosen, about 20 seconds. Drain; cool until easy to handle. Peel and pit peaches; cut flesh into chunks.", "Add 5 cups peach chunks to the pot; mash with a potato masher. Add sugar, gelatin mix, lemon juice, and almond extract; mix well. Bring to a rolling boil; boil for 1 minute. Remove from the heat.", "Pack jam into hot, sterilized jars, filling to within ¼ inch of the top. Run a clean knife or thin spatula around insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart. Rest until cool, about 12 hours. Press center of each lid with a finger to ensure lid does not move up or down. Remove rings for storage and store in a cool, dark area."]', 4.30, 3, 'AllRecipes', 'https://www.allrecipes.com/recipe/254183/aunt-patsis-easy-peach-jam/', 'https://www.allrecipes.com/thmb/IeRJxw6q4aw_twDVnxo53k0KeX8=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/3916248-021936112a194ef4875808c00b7df0a6.jpg', '120 servings', '', '', '', '2025-09-27 08:38:28.204262', '2025-09-27 08:38:28.204262');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (860, 'Kiki''s Spiced Habanero Peach Jam', '[{"item": "3 ½ pounds fresh peaches - peeled, pitted, and chopped", "name": "fresh peaches - peeled, pitted, and chopped", "unit": "pounds", "quantity": "3 ½"}, {"item": "6 tablespoons lemon juice", "name": "lemon juice", "unit": "tablespoons", "quantity": "6"}, {"item": "1  vanilla bean, halved lengthwise and seeds scraped out", "name": "vanilla bean, halved lengthwise and seeds scraped out", "unit": "", "quantity": "1"}, {"item": "1 teaspoon ground cinnamon", "name": "ground cinnamon", "unit": "teaspoon", "quantity": "1"}, {"item": "1 teaspoon ground allspice", "name": "ground allspice", "unit": "teaspoon", "quantity": "1"}, {"item": "½ teaspoon ground nutmeg", "name": "ground nutmeg", "unit": "teaspoon", "quantity": "½"}, {"item": "½ teaspoon ground cardamom", "name": "ground cardamom", "unit": "teaspoon", "quantity": "½"}, {"item": "2  habanero peppers, stemmed and seeded", "name": "habanero peppers, stemmed and seeded", "unit": "", "quantity": "2"}, {"item": "2 (3 ounce) pouches liquid pectin", "name": "liquid pectin", "unit": "(3 ounce) pouches", "quantity": "2"}, {"item": "5 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "5"}, {"item": "2 cups packed brown sugar", "name": "packed brown sugar", "unit": "cups", "quantity": "2"}]', '["Put peaches in a Dutch oven or soup pot; stir in lemon juice, vanilla bean, cinnamon, allspice, nutmeg, and cardamom.", "Place habanero peppers in a blender; top peppers with peach mixture. Blend until mostly smooth; transfer mixture back to the Dutch oven. Stir pectin into peach-habanero pepper mixture; bring to a full rolling boil. Quickly stir white sugar and brown sugar into mixture; return to a boil, stirring constantly, until sugar is dissolved, about 2 minutes.", "Sterilize the jars and lids in boiling water for at least 5 minutes. Pack jam into the hot, sterilized jars, filling the jars to within 1/4 inch of the top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars into the boiling water using a holder. Leave a 2-inch space between the jars. Pour in more boiling water if necessary to bring the water level to at least 1 inch above the tops of the jars. Bring the water to a rolling boil, cover the pot, and process for 15 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool, dark area, and wait 1 to 2 weeks before opening for best results."]', 4.00, 15, 'AllRecipes', 'https://www.allrecipes.com/recipe/233317/kikis-spiced-habanero-peach-jam/', 'https://www.allrecipes.com/thmb/kexdS6XtCtkJby_SoZA62ilFqOc=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1343708-ab38a19ffe4a4b0db0e0bca4941f4c12.jpg', '80 servings', '', '', '', '2025-09-27 08:38:28.205107', '2025-09-27 08:38:28.205107');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (861, 'Raspberry Peach Jam', '[{"item": "7 pounds fresh peaches", "name": "fresh peaches", "unit": "pounds", "quantity": "7"}, {"item": "10 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "10"}, {"item": "5 cups fresh raspberries", "name": "fresh raspberries", "unit": "cups", "quantity": "5"}, {"item": "⅓ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "⅓"}, {"item": "1 (6 fluid ounce) container liquid pectin (such as CERTO®)", "name": "liquid pectin (such as CERTO®)", "unit": "(6 fluid ounce) container", "quantity": "1"}, {"item": "3 tablespoons powdered pectin", "name": "powdered pectin", "unit": "tablespoons", "quantity": "3"}]', '["Immerse ten 12-ounce jars in simmering water until jam is ready. Wash lids and rings in warm, soapy water.", "Bring a large pot of water to a boil. Add peaches, 3 or 4 at a time; cook for 30 seconds. Immerse in ice water for several minutes to stop the cooking process. Drain. Peel off skins, core, and dice peaches into small pieces.", "Combine diced peaches, sugar, raspberries, and lemon juice in a large pot. Cook over medium heat, stirring occasionally, until sugar is dissolved, about 5 minutes. Increase heat to medium-high; cook at a rapid boil for 10 minutes. Stir in liquid and powdered pectin; bring back to a boil. Remove from heat and skim foam off the jam with a slotted spoon.", "Pack jam into hot jars, filling to within 1/4 inch of the top. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water if necessary to bring the water level to at least 1 inch above the tops of the jars. Bring the water to a rolling boil, cover the pot, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool, 12 to 24 hours. Press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all)."]', 4.00, 5, 'AllRecipes', 'https://www.allrecipes.com/recipe/254669/raspberry-peach-jam/', 'https://www.allrecipes.com/thmb/7PMZloayvb-YdK_PAOH8H9MvUKI=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5435891-d143bf8c647646abb786e07f179d433c.jpg', '120 servings', '', '', '', '2025-09-27 08:38:28.206805', '2025-09-27 08:38:28.206805');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (756, 'Sweet chilli jam', '[{"name": "8red peppersdeseeded and roughly chopped", "unit": "", "amount": ""}, {"name": "10red chilliesroughly chopped", "unit": "", "amount": ""}, {"name": "finger-sized piecefresh root gingerpeeled and roughly chopped", "unit": "", "amount": ""}, {"name": "8garlic clovespeeled", "unit": "", "amount": ""}, {"name": "400gcan cherry tomatoes", "unit": "", "amount": ""}, {"name": "750ggolden caster sugar", "unit": "", "amount": ""}, {"name": "250mlred wine vinegar", "unit": "", "amount": ""}]', '["Tip the peppers, chillies (with seeds), ginger and garlic into a food processor, then whizz until very finely chopped. Scrape into a heavy-bottomed pan with the tomatoes, sugar and vinegar, then bring everything to the boil. Skim off any scum that comes to the surface, then turn the heat down to a simmer and cook for about 50 mins, stirring occasionally.", "Once the jam is becoming sticky, continue cooking for 10-15 mins more, stirring frequently so that it doesn’t catch and burn. It should now look like thick, bubbling lava. Cool slightly, transfer to sterilised jars, then leave to cool completely. Keeps for 3 months in a cool, dark cupboard – refrigerate once opened."]', 5.00, 312, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/sweet-chilli-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2012/09/Sweet-chilli-jam-ddfa167.jpg?resize=700,636', '4', 'PT20M', 'PT1H', 'PT1H20M', '2025-09-27 08:34:20.216675', '2025-09-27 08:34:20.216675');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (757, 'Strawberry jam', '[{"name": "1kghulled strawberry", "unit": "", "amount": ""}, {"name": "750gjam sugar", "unit": "", "amount": ""}, {"name": "juice 1  lemon", "unit": "", "amount": ""}, {"name": "small knob of butter(optional)", "unit": "", "amount": ""}]', '["Prepare the strawberries by wiping them with a piece of damp kitchen paper. (Wiping the strawberries rather than washing them ensures the fruit doesn’t absorb lots of water – too much water and the jam won’t set easily.) To hull the fruit, use a knife to cut a cone shape into the strawberry and remove the stem. Cut any large berries in half.", "Put the strawberries in a bowl and gently toss through the sugar. Leave uncovered at room temperature for 12 hrs or overnight. This process helps the sugar to dissolve, ensures the fruit doesn’t disintegrate too much and helps to keep its vibrant colour.", "Before starting the jam, put 2 saucers in the freezer. Tip the strawberry mixture into a preserving pan with the lemon juice. Set over a low heat and cook very gently. If any sugar remains on the sides of the pan, dip a pastry brush in hot water and brush the sugar away.", "When you can no longer feel any grains of sugar remaining, turn up the heat to start bubbling the jam and bringing it to the boil. (The sugar must be completely dissolved before increasing the heat, otherwise it will be difficult for the jam to set, and it may contain crystallised lumps of sugar.)", "Boil hard for 5-10 mins until the jam has reached 105C on a preserving or digital thermometer, then turn off the heat. If you don’t have a thermometer, spoon a little jam onto one of the cold saucers. Leave for 30 secs, then push with your finger; if the jam wrinkles and doesn’t flood to fill the gap, it is ready. If not, turn the heat back on and boil for 2 mins more, then turn off the heat and do the wrinkle test again. Repeat until ready.", "Use a spoon to skim any scum that has risen to the surface and discard this. Do this only once at the end, rather than constantly during the boiling stage, to reduce wastage.", "Add a knob of butter, if you like, to the finished jam, and stir in to melt. This will help to dissolve any remaining scum that you haven’t managed to spoon off the top. Leave the jam to settle for 15 mins – this will ensure that the fruit stays suspended in the mixture and doesn’t all float to the top of the jam jar. Meanwhile, sterilise your jars.", "Ladle into warm jars, filling to just below the rim. Place a wax disc on top of the jam (this prevents mildew forming), then cover with a lid or a cellophane circle and elastic band. Pop on a label (include the date), plus a pretty fabric top, if you like. The jam can be stored for up to 1 year in a cool, dry place. Refrigerate after opening."]', 5.00, 60, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/strawberry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1309564_2-ca9f1b4.jpg?resize=440,400', '3', 'PT20M', 'PT40M', 'PT1H', '2025-09-27 08:34:20.218769', '2025-09-27 08:34:20.218769');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (758, 'Blackcurrant jam', '[{"name": "600gblackcurrantsstripped off the stalks", "unit": "", "amount": ""}, {"name": "about 400gwhite caster sugaror granulated sugar", "unit": "", "amount": ""}, {"name": "juice of ½ alemon", "unit": "", "amount": ""}]', '["If you don’t have a cooking thermometer, put a saucer in the freezer. Sterilise the jars you want to use. Tip the blackcurrants into a heavy-based saucepan with about 100ml of water. Bring to the boil and simmer for 5 mins until the fruit has broken down to a chunky pulp. Leave to cool slightly.", "You now have two options. For a smooth jelly-style jam, squash the fruit through a sieve into a bowl. If you prefer your jam chunky and seeded, leave the pulp as it is. Whether it''s strained or unstrained, weigh the fruit pulp and then add 400g of sugar to every 500g of pulp, then tip back in the saucepan.", "Pour in the lemon juice then heat gently, stirring, to dissolve the sugar. Turn up the heat, then boil hard for about 10 mins or until it reaches 105C (setting point) on a cooking thermometer. If you don’t own a thermometer, test for setting point by spooning a little jam onto the cold saucer. After a couple of minutes gently push your finger through the jam – if the surface wrinkles, it''s ready. If not, return to the boil for 2 mins, then re-test.", "Take off the heat and skim off any froth with a slotted spoon. Cool for 10-15 minutes. Stir gently to distribute the fruit, then ladle into sterilised jars. Keeps for 6 months in a cool dry cupboard."]', 5.00, 36, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/blackcurrant-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blackcurrant-jam-1-2e0800c.jpg?resize=440,400', '3', 'PT30M', 'PT30M', 'PT1H', '2025-09-27 08:34:20.219843', '2025-09-27 08:34:20.219843');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (759, 'Apple & blackberry jam', '[{"name": "500gBramley applespeeled, cored, quartered and cut into 2cm chunks", "unit": "", "amount": ""}, {"name": "1lemonjuiced", "unit": "", "amount": ""}, {"name": "500gblackberriesfresh or frozen", "unit": "", "amount": ""}, {"name": "1kggranulated sugar", "unit": "", "amount": ""}]', '["Put two small plates in the freezer ready to test the set of the jam. Put the apples in a heavy pan or preserving pan with the lemon juice and 250ml water. Bring up to the boil, then simmer for 5-8 mins until soft.", "Add the blackberries and sugar and stir to combine. Stir over a low heat until the sugar his dissolved, then turn the heat up to medium and simmer for 30-35 mins until the jam is at setting stage.", "To test the jam, take one of the plates out of the freezer and place a little bit of jam on it, if it sets and when you run your finger over it and it wrinkles, it means it’s ready.", "Using a funnel, transfer the jam to a sterilised jar. Make sure you seal the lids once it’s cold. Will keep in the fridge for six months in a sealed, sterilised jar."]', 5.00, 35, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/apple-blackberry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2021/05/Apple-and-Blackberry-Jam-0547950.jpg?resize=768,713', '1', 'PT5M', 'PT45M', 'PT50M', '2025-09-27 08:34:20.22066', '2025-09-27 08:34:20.22066');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (760, 'Crab apple jelly', '[{"name": "1½ kgcrab apples", "unit": "", "amount": ""}, {"name": "1lemonzest pared and half juiced", "unit": "", "amount": ""}, {"name": "around 450g white caster orgranulated sugar", "unit": "", "amount": ""}, {"name": "Jelly bag", "unit": "", "amount": ""}]', '["Wash and drain the fruit, then cut the apples in half and add to a large, heavy-based saucepan with an equal quantity of water and the pared lemon zest. Bring to the boil, then reduce the heat and simmer for 20-25 mins until the apples are pulpy. Stir occasionally using the back of a wooden spoon to help break down the fruit.", "Remove from the heat and allow to cool a little. Place the fruit pulp in a jelly bag and allow the juice to drip through into a large bowl (this will take several hours, so doing this overnight is ideal). If you force the fruit through, this will create a cloudy jelly.", "Put two small saucers in the freezer – you will use these to test the setting point of the jam.", "Weigh the fruity liquid and return to a large saucepan. Weigh three quarters of sugar to your total liquid amount (for example, if you have 400g of liquid, add 300g of sugar).", "Add the sugar to the juice with the lemon juice, and stir over a medium heat until completely dissolved. Turn up the heat and bring the fruit to the boil. Stir occasionally and allow to boil hard for about 8 mins, removing any scum with a spoon. If using a thermometer, you’re looking to reach 105c. If not, check the setting point of the liquid after 8 mins by removing a saucer from the freezer and adding a teaspoon of the liquid to the plate. Allow to sit for a minute, then push your finger through the liquid. If it starts to wrinkle, the jelly is ready. If not, return to the boil and try again after another minute.", "Pour into sterilised jars and allow to cool completely. You can sterilise the jars by washing them in hot soapy liquid, rinsing and placing in an oven at 180C/160C fan/gas 4 for 5 mins. Remove carefully and allow to cool. Store in a cool, dark place."]', 5.00, 19, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/crab-apple-jelly', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/09/Crab-apple-jelly-da99f85.jpg?resize=768,713', '2', 'PT20M', 'PT35M', 'PT55M', '2025-09-27 08:34:20.221453', '2025-09-27 08:34:20.221453');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (761, 'Apple jam', '[{"name": "1kgBramley applespeeled, cored and chopped", "unit": "", "amount": ""}, {"name": "250gcaster sugar", "unit": "", "amount": ""}, {"name": "½lemonjuiced", "unit": "", "amount": ""}, {"name": "1cinnamon stick", "unit": "", "amount": ""}]', '["Put a small plate in the freezer. Tip the chopped apple and caster sugar into a large saucepan along with 350ml of water and bring to a simmer, stirring to dissolve the sugar.", "Cook gently, stirring regularly, for 30 mins until the apples have collapsed.", "Stir in the lemon juice, add the cinnamon stick and simmer gently for another 2-3 mins until the mixture is thick and glossy.", "Remove the small plate from the freezer and drop a little of the mix onto it. Leave for a few seconds – it should be fairly firm. If it’s still a little runny, put the pan back of the heat and simmer for another 10-15 mins. Put the plate back in the freezer and test again, it should now be ready.", "Divide between clean, sterilised jars and seal. Will keep for three months in a cool, dry place. Once opened, chill and use within one month."]', 5.00, 16, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/apple-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2021/07/Apple-Jam-01e8e4b.jpg?resize=768,713', '2', 'PT15M', 'PT40M', 'PT55M', '2025-09-27 08:34:20.222288', '2025-09-27 08:34:20.222288');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (762, 'Christmas jam', '[{"name": "500gfresh cranberries", "unit": "", "amount": ""}, {"name": "2orangeszested and juiced", "unit": "", "amount": ""}, {"name": "1star anise", "unit": "", "amount": ""}, {"name": "1cinnamon stick", "unit": "", "amount": ""}, {"name": "2cloves", "unit": "", "amount": ""}, {"name": "500gcaster sugar", "unit": "", "amount": ""}, {"name": "2 tspedible glitter(optional)", "unit": "", "amount": ""}]', '["Put a small plate in the fridge to chill. Tip the cranberries, orange zest and juice, the star anise, cinnamon stick, cloves and sugar into a heavy-based saucepan. Bring to the boil. Reduce the heat and simmer for 30-35 mins, stirring often until the cranberries have burst and the liquid has reduced – it should be thick and jammy. Put a teaspoon of the jam on the chilled plate and leave to cool. Once cool, run your finger through the jam – if it wrinkles up, it’s ready. If not, cook for 5 mins more and test again.", "Leave to cool for 10 mins, then remove the whole spices and stir in the glitter, if using. Decant into sterilised jam jars and seal. Will keep in a cool, dark place for three months, or chilled for one month once opened."]', 5.00, 8, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/christmas-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2022/11/Christmas-jam-bfa3acf.jpg?resize=768,713', '650', 'PT5M', 'PT40M', 'PT45M', '2025-09-27 08:34:20.223088', '2025-09-27 08:34:20.223088');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (763, 'Gooseberry & apple jam', '[{"name": "1kggooseberrytopped and tailed", "unit": "", "amount": ""}, {"name": "400mlunsweetened clear apple juice(or use water)", "unit": "", "amount": ""}, {"name": "1kggranulated sugar", "unit": "", "amount": ""}, {"name": "knob of  butter", "unit": "", "amount": ""}, {"name": "1vanilla podseeds scraped out", "unit": "", "amount": ""}]', '["Sterilise the jars and any other equipment before you start (see tip, below). Put a couple of saucers in the freezer, as you’ll need these for testing if the jam is ready later (or use a sugar thermometer). Put the berries and juice (or water) in a preserving or very large deep pan, bring to a simmer, then cook for about 15 mins until the fruit is very tender.", "Add the sugar to the pan and heat gently until all the sugar has dissolved – do not boil at this point. Turn up the heat and cook the jam at a rolling boil, skimming away any scum that comes to the top as it cooks. After about 10 mins, spoon a little jam onto a cold saucer. Leave for a few secs, then push the jam with your fingertip. If it wrinkles, the jam is ready. If not, cook for a few mins more and test again on another cold saucer. (If you have a sugar thermometer, it will read 105 degrees when ready.)", "Give the jam a final skim, stir in the butter, then the vanilla seeds and the pod too. Let the jam cool for a few mins – this will thicken it and prevent any lumps from sinking to the bottom of the jars – then ladle into hot jars, seal and leave to cool."]', 5.00, 6, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/gooseberry-vanilla-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/jam_0-f0a8a6a.jpg?resize=440,400', '2', 'PT15M', 'PT40M', 'PT55M', '2025-09-27 08:34:20.223873', '2025-09-27 08:34:20.223873');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (764, 'Apple jelly', '[{"name": "1.5kgappleswashed well and roughly chopped (skin and core kept on)", "unit": "", "amount": ""}, {"name": "1lemonpared and juiced", "unit": "", "amount": ""}, {"name": "around 750gjam sugar", "unit": "", "amount": ""}]', '["Tip the apples into a preserving pan or large saucepan with 1.5 litres of water and the lemon peel. Bring to the boil over a medium heat, then reduce to a gentle simmer and cook for 30-40 mins, breaking the apples up with a spoon once softened a little, until the apples are pulpy. Spoon the apples and liquid into the bag of a jam strainer set over a large bowl and allow the juice to drop through (don’t be tempted to squeeze it through or it’ll go cloudy). Leave overnight if you can, or for at least a few hours.", "Once the liquid has dripped through, weigh it to work out the amount of sugar needed. Once you have your weight of liquid, divide it by 4 then multiply that figure by 3 to work out three-quarters of the liquid’s weight. This is how much sugar you need (e.g. if you have 1 litre of liquid, you will need 750g sugar). Put the liquid, sugar and lemon juice into a large saucepan or preserving pan set over a medium heat and cook until the sugar has completely dissolved, without boiling. This should take around 5 mins. Bring to the boil and cook for around 10 mins, removing any scum that rises to the surface, until the mixture reaches 105C on a digital thermometer. You can also check it by putting a spoonful on an ice-cold plate – it should wrinkle when prodded. If it’s not set, keep cooking and checking every couple of minutes.", "Pour into sterilised jars and seal before leaving to cool. Will keep, unopened, in a cool, dark place for three months."]', 5.00, 3, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/apple-jelly', 'https://images.immediate.co.uk/production/volatile/sites/30/2024/09/Apple-jelly-078ddc2.jpg?resize=768,713', '3', 'PT50M', 'PT1H', 'PT1H50M', '2025-09-27 08:34:20.225297', '2025-09-27 08:34:20.225297');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (765, 'Bright and Fruity Strawberry Jam, Your Way', '[{"item": "4cups(22ounces;625g) roughly chopped hulledstrawberries(see notes)", "name": "4cups(22ounces;625g) roughly chopped hulledstrawberries(see notes)", "unit": "", "quantity": ""}, {"item": "2tablespoons(30ml)fresh juicefrom1wholelemon, plus more if needed", "name": "2tablespoons(30ml)fresh juicefrom1wholelemon, plus more if needed", "unit": "", "quantity": ""}, {"item": "2cupsgranulated sugar(14ounces;400g)", "name": "2cupsgranulated sugar(14ounces;400g)", "unit": "", "quantity": ""}, {"item": "1/3cuppowdered low-sugar pectin(1 1/2ounces;45g), such as Ball or Sure-Jell, plus more if needed (see notes)", "name": "1/3cuppowdered low-sugar pectin(1 1/2ounces;45g), such as Ball or Sure-Jell, plus more if needed (see notes)", "unit": "", "quantity": ""}]', '["Combine strawberries and lemon juice in a large nonreactive saucepan and set over medium heat until simmering. Stir in sugar and bring mixture to a full boil over medium-high heat, then reduce to a simmer. Stir in pectin until dissolved, return heat to medium-high and bring to a full rolling boil. As soon as mixture reaches a full boil, cook for 1 minute, then remove from heat. Let stand 5 minutes.Serious Eats / Vicky Wasik", "To Check the Jam Set:Spoon 1 tablespoon (15ml) jam onto a small dish and allow to cool completely, to room temperature. Run a finger through the jam. If the jam remains separated, it is properly set; if it runs back together, it is not. If not set, add an extra 1 tablespoon lemon juice and 1 tablespoon low-sugar pectin, then return to a boil and boil for 1 minute longer. Repeat jam set test until jam is properly set.", "Carefully scrape any foam or scum from surface of jam. Transfer jam to clean jars, leaving 1/4 inch space at the top; seal jars. Jam can be refrigerated up to 3 weeks or frozen up to 3 months. If heat-processing jam, process for 8 minutes."]', 5.00, 2, 'Serious Eats', 'https://www.seriouseats.com/customizable-strawberry-jam-recipe', 'https://www.seriouseats.com/thmb/13LCNkWCsZF1myjfGQ4xbsg5EVQ=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__recipes__images__2017__07__20170717-preserving-strawberry-jam-vicky-wasik-4-f67927f78a0341c690b182b91576d4c1.jpg', '64 servings', 'Prep20 mins', '', 'Total35 mins', '2025-09-27 08:34:20.22632', '2025-09-27 08:34:20.22632');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (766, 'Cherry Jam', '[{"item": "3 cups pitted, chopped fresh cherries", "name": "pitted, chopped fresh cherries", "unit": "cups", "quantity": "3"}, {"item": "½ cup unsweetened apple juice", "name": "unsweetened apple juice", "unit": "cup", "quantity": "½"}, {"item": "2 teaspoons lemon juice", "name": "lemon juice", "unit": "teaspoons", "quantity": "2"}, {"item": "2 (1.75 ounce) packages powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) packages", "quantity": "2"}, {"item": "3 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "3"}, {"item": "4  half pint canning jars with lids and rings", "name": "half pint canning jars with lids and rings", "unit": "", "quantity": "4"}]', '["Place cherries, apple juice, lemon juice, and pectin into a large saucepan over medium heat; bring to a boil and mix in sugar. Cook jam at a rolling boil for 2 minutes, stirring constantly. Remove from heat and skim off any foam.", "Sterilize the jars and lids in boiling water for at least 5 minutes. Pack cherry jam into the hot, sterilized jars, filling the jars to within 1/4 inch of the top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil over high heat, then carefully lower the jars into the pot using a holder. Leave a 2 inch space between the jars. Pour in more boiling water if necessary until the water level is at least 1 inch above the tops of the jars. Bring the water to a full boil, cover the pot, and process for 15 minutes, or the time as recommended by your county Extension agent.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool, dark area."]', 4.80, 48, 'AllRecipes', 'https://www.allrecipes.com/recipe/218434/bc-cherry-jam/', 'https://www.allrecipes.com/thmb/t2wNTwMw6iTz7tQRBtSpFW2HH3o=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/218434-cherry-jam-amdolcevita-4x3.jpg-894f2e70c8dc464381239cffb831aab2.jpg', '40 servings', '', '', '', '2025-09-27 08:34:20.227144', '2025-09-27 08:34:20.227144');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (767, 'Fresh Strawberry Jam', '[{"item": "3  apples, stemmed, coarsely chopped (including core and seeds)", "name": "apples, stemmed, coarsely chopped (including core and seeds)", "unit": "", "quantity": "3"}, {"item": "1 cup water", "name": "water", "unit": "cup", "quantity": "1"}, {"item": "1  lemon, chopped (including rind and seeds)", "name": "lemon, chopped (including rind and seeds)", "unit": "", "quantity": "1"}, {"item": "3 pounds fresh strawberries, hulled", "name": "fresh strawberries, hulled", "unit": "pounds", "quantity": "3"}, {"item": "2 ¼ cups white sugar, divided", "name": "white sugar, divided", "unit": "cups", "quantity": "2 ¼"}, {"item": "1 teaspoon fresh lemon juice", "name": "fresh lemon juice", "unit": "teaspoon", "quantity": "1"}]', '["Place chopped apples, water, and chopped lemon in a saucepan. Bring to a boil over high heat; reduce heat to medium. Cover and cook, stirring occasionally, until fruit is very soft and collapsed, about 30 minutes.", "Strain apple mixture through a fine-mesh-sieve into a bowl to separate liquid from pulp and seeds. Press with the back of a wooden spoon or spatula to extract as much liquid as possible. Discard pulp and seeds. You should have about 1 ½ cups pectin purée. A bit more or less will work fine.", "Place hulled strawberries in the bowl of a food processor; pulse until puréed, about 1 minute. Transfer to a saucepan; attach a candy thermometer to the saucepan. Add pectin purée; stir in 1 cup sugar. Bring to a simmer over medium-high heat; reduce heat to medium and simmer for about 15 minutes. Skim off foam. Adjust heat, if necessary, to ensure mixture is always simmering without boiling over. Stir in remaining 1 ¼ cups sugar.", "Cook, stirring often, until mixture reaches 220 degrees F (105 degrees C), 50 to 60 minutes. Off heat, whisk in lemon juice.", "Meanwhile, inspect three 8-ounce jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Pack into hot, sterilized jars while jam is still hot, filling to within ¼ inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Cool to room temperature uncovered. Tops with lids and screw rings on tightly. Refrigerate before serving to allow sugar and pectin to thicken jam, 1 to 2 days.", "Delicious served onChef John''s Scones, biscuits, or anything jam-worthy.", "Here''s Chef John''s recipe forclotted cream."]', 4.80, 26, 'AllRecipes', 'https://www.allrecipes.com/recipe/257919/fresh-strawberry-jam/', 'https://www.allrecipes.com/thmb/e32Xg-rWIb5HLmSHK6KEpP1l4l4=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/4507924-4ae29271ad6d492fb16968a5719e1402.jpg', '24 servings', '', '', '', '2025-09-27 08:34:20.227978', '2025-09-27 08:34:20.227978');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (768, 'Tomato and Bacon Jam', '[{"item": "½ pound smoked bacon", "name": "smoked bacon", "unit": "pound", "quantity": "½"}, {"item": "2 pounds ripe tomatoes, chopped", "name": "ripe tomatoes, chopped", "unit": "pounds", "quantity": "2"}, {"item": "1  yellow sweet onion, finely diced", "name": "yellow sweet onion, finely diced", "unit": "", "quantity": "1"}, {"item": "1 cup white sugar", "name": "white sugar", "unit": "cup", "quantity": "1"}, {"item": "2 ½ tablespoons apple cider vinegar", "name": "apple cider vinegar", "unit": "tablespoons", "quantity": "2 ½"}, {"item": "1 ½ teaspoons salt", "name": "salt", "unit": "teaspoons", "quantity": "1 ½"}, {"item": "¼ teaspoon black pepper", "name": "black pepper", "unit": "teaspoon", "quantity": "¼"}, {"item": "1 dash hot pepper sauce (such as Tabasco) (Optional)", "name": "hot pepper sauce (such as Tabasco) (Optional)", "unit": "dash", "quantity": "1"}]', '["Cook bacon in a large skillet over medium-high heat, turning occasionally, until evenly browned, about 10 minutes. Drain bacon slices on paper towels. Crumble bacon when cool.", "Stir tomatoes, sweet onion, sugar, apple cider vinegar, salt, black pepper, and hot sauce together in a saucepan and bring to a boil, stirring constantly to dissolve sugar. Stir in crumbled bacon and cook over medium heat, stirring often, until the spread is very thick, about 1 hour. Adjust salt and black pepper.", "Cool and spoon into a clean 1-pint glass jar with a lid; store in the refrigerator for 1 week or frozen for 2 months. If freezing, put spread into small jars and let jars thaw in refrigerator overnight before using."]', 4.70, 94, 'AllRecipes', 'https://www.allrecipes.com/recipe/228140/tomato-and-bacon-jam/', 'https://www.allrecipes.com/thmb/D1d9R7Gnc8HrPKdnB12A7on-Lqc=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/9370895-61024e5f45b94a1f90f622d44c1de6e6.jpg', '10 servings', '', '', '', '2025-09-27 08:34:20.229359', '2025-09-27 08:34:20.229359');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (769, 'Rachel''s Sugar Plum Spice Jam', '[{"item": "10  red plums, pitted and chopped", "name": "red plums, pitted and chopped", "unit": "", "quantity": "10"}, {"item": "2 cups chopped fresh pineapple", "name": "chopped fresh pineapple", "unit": "cups", "quantity": "2"}, {"item": "½ (12 fluid ounce) can frozen orange juice concentrate", "name": "frozen orange juice concentrate", "unit": "(12 fluid ounce) can", "quantity": "½"}, {"item": "½ cup apple juice", "name": "apple juice", "unit": "cup", "quantity": "½"}, {"item": "1  lemon, juiced", "name": "lemon, juiced", "unit": "", "quantity": "1"}, {"item": "1 (1.75 ounce) package powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "1 tablespoon butter", "name": "butter", "unit": "tablespoon", "quantity": "1"}, {"item": "1 ½ teaspoons ground cloves", "name": "ground cloves", "unit": "teaspoons", "quantity": "1 ½"}, {"item": "1 teaspoon ground allspice", "name": "ground allspice", "unit": "teaspoon", "quantity": "1"}, {"item": "8 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "8"}, {"item": "4 drops red food coloring", "name": "red food coloring", "unit": "drops", "quantity": "4"}]', '["Heat plums, pineapple, orange juice concentrate, apple juice, and lemon juice in a large pot over medium-low heat, covered; cook until plums are soft, 15 to 20 minutes. Mash plums and pineapple with a potato masher until thoroughly broken up, leaving small chunks in mixture.", "Bring mixture to a slow simmer; stir in pectin, butter (to reduce foaming), cloves, and allspice. Increase heat to high; bring to a full, rolling boil for 1 minute, stirring constantly. Stir in sugar, stirring constantly; cook at a full, rolling boil, 1 minute more. Off heat, stir in red food coloring until evenly colored; skim off any foam.", "Inspect 10 (12-ounce) jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water. and lids in boiling water for at least 5 minutes.", "Pack jam into hot, sterilized jars, filling to within ¼ inch of the top. Run a clean knife or a thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil over high heat, then carefully lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover the pot, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the center of each lid with a finger to ensure the lid does not move up or down. Store in a cool, dark area."]', 4.70, 45, 'AllRecipes', 'https://www.allrecipes.com/recipe/215135/rachels-sugar-plum-spice-jam/', 'https://www.allrecipes.com/thmb/mska6CgzuGn26irN4dSRvLgXRzo=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/2422500-fc5abeb02d6a4977975c6fb82cf7751d.jpg', '120 servings', '', '', '', '2025-09-27 08:34:20.230271', '2025-09-27 08:34:20.230271');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (770, 'Caramel Apple Jam', '[{"item": "8 cups Granny Smith apples - peeled, cored, and chopped", "name": "Granny Smith apples - peeled, cored, and chopped", "unit": "cups", "quantity": "8"}, {"item": "⅓ cup water", "name": "water", "unit": "cup", "quantity": "⅓"}, {"item": "1 tablespoon butter", "name": "butter", "unit": "tablespoon", "quantity": "1"}, {"item": "3 cups brown sugar", "name": "brown sugar", "unit": "cups", "quantity": "3"}, {"item": "2 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "2"}, {"item": "1 ½ (1.75 ounce) packages powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) packages", "quantity": "1 ½"}, {"item": "1 tablespoon clear butter flavoring (such as Wilton®)", "name": "clear butter flavoring (such as Wilton®)", "unit": "tablespoon", "quantity": "1"}]', '["Sterilize jars and lids in boiling water for at least 5 minutes.", "Bring apples, water, and butter to a boil in a large pot. Reduce heat to low, cover, and simmer until apples are softened, about 8 minutes. Mix in brown sugar, white sugar, pectin powder, and butter flavoring. Bring to a full rolling boil, stirring often. Cook at a boil for 2 minutes without stirring.", "Pack apple jam into the hot, sterilized jars, filling them to within 1/4 inch of the top. Run a knife or a thin spatula around the insides of the jars to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with the lids and screw on the rings tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower the jars into the boiling water using a holder. Leave a 2-inch space between the jars. Pour in more boiling water if necessary to bring water level to at least 1 inch above the tops of the jars. Bring water to a rolling boil, cover the pot, and process for 15 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (the lid does not move up or down at all). Let stand for 8 hours to overnight before use. Store in a cool, dark area; refrigerate opened jars."]', 4.70, 31, 'AllRecipes', 'https://www.allrecipes.com/recipe/228921/buttery-caramel-apple-jam/', 'https://www.allrecipes.com/thmb/0_lZtDysiVxa1ip3PHO82tJbulA=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/2826115-5932236d478e4aa69abfda6714dc6a74.jpg', '48 servings', '', '', '', '2025-09-27 08:34:20.231344', '2025-09-27 08:34:20.231344');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (771, 'Fresh Tomato and Caramelized Onion Jam', '[{"item": "3/4poundsyellow onions(about 2 medium), finely sliced", "name": "3/4poundsyellow onions(about 2 medium), finely sliced", "unit": "", "quantity": ""}, {"item": "1tablespoonbutter", "name": "1tablespoonbutter", "unit": "", "quantity": ""}, {"item": "1 1/2poundsRoma tomatoes(about 6 medium),peeled, cored, seeded, and finely chopped", "name": "1 1/2poundsRoma tomatoes(about 6 medium),peeled, cored, seeded, and finely chopped", "unit": "", "quantity": ""}, {"item": "1/2cupwhite sugar", "name": "1/2cupwhite sugar", "unit": "", "quantity": ""}, {"item": "1/4cuplight brown sugar", "name": "1/4cuplight brown sugar", "unit": "", "quantity": ""}, {"item": "2tablespoonsjuice from 1 lemon", "name": "2tablespoonsjuice from 1 lemon", "unit": "", "quantity": ""}, {"item": "2tablespoonsapple cider vinegar", "name": "2tablespoonsapple cider vinegar", "unit": "", "quantity": ""}, {"item": "1teaspoonkosher salt", "name": "1teaspoonkosher salt", "unit": "", "quantity": ""}, {"item": "1/2teaspoonred pepper flakes", "name": "1/2teaspoonred pepper flakes", "unit": "", "quantity": ""}]', '["Melt butter in a 3-quart stainless-steel saucepan or saucier or a 4- or 5-quart enameled Dutch oven over high heat. Add onions and cook, stirring frequently, until bottom of saucepan is glazed in a pale brown fond, about 5 minutes. Add 2 tablespoons water and scrape up fond with wooden spoon. Continue to cook, stirring frequently, until fond has built up again, about 2 minutes longer. Add 2 more tablespoons water and scrape up browned bits. Repeat cooking, adding water, and scraping until onions are completely softened and a deep, dark brown, about 15 minutes total.Serious Eats / Mariel De La Cruz", "Add tomatoes, white sugar, brown sugar, lemon juice, vinegar, salt, and red pepper flakes in with the onions; stir to combine. Bring mixture to a boil, then reduce heat and simmer, stirring occasionally, until tomatoes have broken down and jam has thickened and developed a jammy consistency, 1 to 1 1/2 hours. Remove from heat. Transfer jam to an airtight container and store in refrigerator for up to 2 weeks.Serious Eats / Mariel De La Cruz"]', 4.50, 2, 'Serious Eats', 'https://www.seriouseats.com/fresh-tomato-caramelized-onion-jam-recipe', 'https://www.seriouseats.com/thmb/i0Mbs075P1bOG9DSaOYCrDFvliI=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/fresh-tomato-caramelized-onion-jam-recipe-hero-social-01FINAL-b52e94f4ad234347a1c36e8f244238ec.JPG', '16 servings', 'Prep15 mins', '', 'Total2 hrs', '2025-09-27 08:34:20.233216', '2025-09-27 08:34:20.233216');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (772, 'Apple Jam', '[{"item": "6  Granny Smith apples - peeled, cored, and finely chopped", "name": "Granny Smith apples - peeled, cored, and finely chopped", "unit": "", "quantity": "6"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}, {"item": "½ cup light brown sugar", "name": "light brown sugar", "unit": "cup", "quantity": "½"}, {"item": "1 large lemon, zested and juiced", "name": "lemon, zested and juiced", "unit": "large", "quantity": "1"}, {"item": "1 teaspoon ground cinnamon", "name": "ground cinnamon", "unit": "teaspoon", "quantity": "1"}, {"item": "½ teaspoon ground nutmeg", "name": "ground nutmeg", "unit": "teaspoon", "quantity": "½"}, {"item": "¼ teaspoon ground cloves", "name": "ground cloves", "unit": "teaspoon", "quantity": "¼"}, {"item": "⅛ teaspoon allspice", "name": "allspice", "unit": "teaspoon", "quantity": "⅛"}, {"item": "5  half-pint canning jars with lids and rings", "name": "half-pint canning jars with lids and rings", "unit": "", "quantity": "5"}]', '["Combine apples, white sugar, light brown sugar, lemon juice, lemon zest, cinnamon, nutmeg, cloves, and allspice in a pot; bring to a rolling boil. Cook at a boil, stirring occasionally, until fruit is soft and jam gelling point (220 degrees F (104 degrees C) is reached, at least 45 minutes. Put a small amount of jam on a cold plate; freeze for several minutes. If the mixture is gelled, it is ready to process in a canner.", "Sterilize the jars and lids in boiling water for at least 5 minutes. Pack apple jam into hot, sterilized jars, filling to within 1/4 inch of the top. Run a knife or thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars into the boiling water using a holder. Leave a 2-inch space between the jars. Pour in more boiling water if necessary to bring the water level to at least 1 inch above the tops of the jars. Bring the water to a rolling boil, cover the pot, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool, dark area."]', 4.40, 11, 'AllRecipes', 'https://www.allrecipes.com/recipe/239918/apple-jam-apple-pie-in-a-jar/', 'https://www.allrecipes.com/thmb/9qEJxLNpkQpyz_tU4siT8-T72ug=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/4588806-8b4762671f9942bb9751d64b0d40aa1a.jpg', '80 servings', '', '', '', '2025-09-27 08:34:20.234593', '2025-09-27 08:34:20.234593');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (773, 'Spicy Tomato Jam', '[{"item": "3 pounds tomatoes", "name": "tomatoes", "unit": "pounds", "quantity": "3"}, {"item": "1 gallon boiling water", "name": "boiling water", "unit": "gallon", "quantity": "1"}, {"item": "1 ½ cups brown sugar", "name": "brown sugar", "unit": "cups", "quantity": "1 ½"}, {"item": "1 cup cider vinegar", "name": "cider vinegar", "unit": "cup", "quantity": "1"}, {"item": "½ cup apple juice", "name": "apple juice", "unit": "cup", "quantity": "½"}, {"item": "1 ½ teaspoons salt", "name": "salt", "unit": "teaspoons", "quantity": "1 ½"}, {"item": "½ teaspoon ground black pepper", "name": "ground black pepper", "unit": "teaspoon", "quantity": "½"}, {"item": "½ teaspoon ground mustard", "name": "ground mustard", "unit": "teaspoon", "quantity": "½"}, {"item": "½ teaspoon ground allspice", "name": "ground allspice", "unit": "teaspoon", "quantity": "½"}, {"item": "½ teaspoon ground cumin", "name": "ground cumin", "unit": "teaspoon", "quantity": "½"}, {"item": "¼ teaspoon cayenne pepper", "name": "cayenne pepper", "unit": "teaspoon", "quantity": "¼"}, {"item": "1  lemon, quartered and sliced thin", "name": "lemon, quartered and sliced thin", "unit": "", "quantity": "1"}]', '["Place tomatoes in a large pot and pour boiling water over tomatoes; allow to sit for 5 minutes. Remove tomatoes from boiling water and place in ice-cold water until cool enough to handle. Slip off skins and stems.", "Coarsely chop tomatoes in a food processor; reserve any juices and add to chopped tomatoes.", "Combine brown sugar, vinegar, apple juice, salt, black pepper, mustard, allspice, cumin, and cayenne pepper in a large non-reactive pan over medium heat; cook and stir until sugar dissolves completely. Stir in chopped tomatoes. Bring to a boil; reduce heat and simmer, stirring frequently, until liquid has reduced by half, 30 to 45 minutes. Add lemon slices and cook for 15 more minutes.", "Sterilize the jars and lids in boiling water for at least 5 minutes. Pack jam into the hot, sterilized jars, filling the jars to within 1/4 inch of the top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with the lids and screw on the rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil over high heat, then carefully lower the jars into the pot using a holder. Leave a 2 inch space between the jars. Pour in more boiling water if necessary until water level is at least 1 inch above the tops of the jars. Bring water to a full boil, cover the pot, and process for 30 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool, dark area."]', 4.10, 32, 'AllRecipes', 'https://www.allrecipes.com/recipe/174275/spicy-tomato-jam/', 'https://www.allrecipes.com/thmb/TBfUPaAugjTew1AjoLi-hPmCmV0=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5853985-c98add9fd0304d5ea1f0ae8b0b211304.jpg', '32 servings', '', '', '', '2025-09-27 08:34:20.235671', '2025-09-27 08:34:20.235671');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (774, 'Easy plum jam', '[{"name": "2kgplumsstoned and roughly chopped", "unit": "", "amount": ""}, {"name": "2kgwhite granulated sugar", "unit": "", "amount": ""}, {"name": "2 tspground cinnamon", "unit": "", "amount": ""}, {"name": "1 tbsplemonjuice", "unit": "", "amount": ""}, {"name": "3cinnamon sticks(optional)", "unit": "", "amount": ""}, {"name": "knob ofbutter", "unit": "", "amount": ""}]', '["Sterilise the jars and any other equipment before you start (see tip). Put a couple of saucers in the freezer, as you’ll need these for testing whether the jam is ready later (or use a sugar thermometer). Put the plums in a preserving pan and add 200ml water. Bring to a simmer, and cook for about 10 mins until the plums are tender but not falling apart. Add the sugar, ground cinnamon and lemon juice, then let the sugar dissolve slowly, without boiling. This will take about 10 mins.", "Increase the heat and bring the jam to a full rolling boil. After about 5 mins, spoon a little jam onto a cold saucer. Wait a few seconds, then push the jam with your fingertip. If it wrinkles, the jam is ready. If not, cook for a few mins more and test again, with another cold saucer. If you have a sugar thermometer, it will read 105C when ready.", "Take the jam off the heat and add the cinnamon sticks (if using) and the knob of butter. The cinnamon will look pretty in the jars and the butter will disperse any scum. Let the jam cool for 15 mins, which will prevent the lumps of fruit sinking to the bottom of the jars. Ladle into hot jars, seal and leave to cool. Will keep for 1 year in a cool, dark place. Chill once opened."]', 4.00, 85, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/cinnamon-scented-plum-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cinnamon-scented-plum-jam-ea5e628.jpg?resize=440,400', '3', 'PT30M', 'PT30M', 'PT1H', '2025-09-27 08:34:20.236685', '2025-09-27 08:34:20.236685');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (775, 'Pineapple-Apple Marmalade Jam', '[{"item": "1  pineapple, cored and finely chopped", "name": "pineapple, cored and finely chopped", "unit": "", "quantity": "1"}, {"item": "1  apple - peeled, cored, and chopped", "name": "apple - peeled, cored, and chopped", "unit": "", "quantity": "1"}, {"item": "1 ¾ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "1 ¾"}, {"item": "½ cup orange juice", "name": "orange juice", "unit": "cup", "quantity": "½"}, {"item": "1 teaspoon lemon zest", "name": "lemon zest", "unit": "teaspoon", "quantity": "1"}]', '["Combine pineapple, apple, sugar, orange juice, and lemon zest in a pot. Bring to a boil. Reduce heat to medium and cook, stirring frequently, until pineapple turns translucent, 1 to 1 ½ hours.", "Place a few small plates in the freezer.", "Inspect 2 or 3 small Mason jars for cracks, discarding any defective ones. Sterilize jars in a pot of boiling water for 10 minutes and leave in hot water until ready for use. Wash new, unused lids and rings in warm soapy water.", "Add a tablespoon of marmalade to a frozen plate. Freeze for 1 minute, remove, and nudge marmalade with your finger. If the mixture stays nudged, it is ready to be canned. If it slides back, continue cooking and check for readiness every minute.", "Pack marmalade into hot, sterilized jars, filling to within 1/2 to 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and let rest, several inches apart, for 12 to 24 hours. Press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 4.00, 3, 'AllRecipes', 'https://www.allrecipes.com/recipe/270460/pineapple-apple-marmalade-jam/', 'https://www.allrecipes.com/thmb/opKqe7p5N5XgcKB8Vz9V_MIYEEQ=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/270460-pineapple-apple-marmalade-jam-tcasa-4x3-48208ac15bea4f96a1f43c63b514ca76.jpg', '16 servings', '', '', '', '2025-09-27 08:34:20.237542', '2025-09-27 08:34:20.237542');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (862, 'Peach Melba jam', '[{"name": "500graspberries", "unit": "", "amount": ""}, {"name": "500gpeachhalved and stoned, then diced into similar sizes to the raspberries", "unit": "", "amount": ""}, {"name": "800gjam sugar", "unit": "", "amount": ""}, {"name": "juice 1lemon", "unit": "", "amount": ""}, {"name": "fingertip-sized knob ofbutter(optional)", "unit": "", "amount": ""}]', '["The night before you make your jam, layer the berries, peaches and sugar in a very large bowl. Cover and set aside at room temperature.", "The next morning, give everything a good stir and set aside again until you’re ready to start cooking. Put a small saucer in the freezer.", "Tip the raspberries and peaches, scraping out all the juices and any undissolved sugar, into a preserving pan (or use a large, wide-based pan – the wider and more open the pan, the faster the jam will be ready without the fruit cooking to mush). Stir in the lemon juice and cook over a very low heat until any remaining sugar is dissolved.", "Bring the mixture to the boil, then simmer for 5 mins. Turn off the heat and spoon a little of the hot jam onto the chilled saucer. Once cooled, push the jam with your finger – if it wrinkles a little, it has reached setting point. If it’s still too runny to wrinkle, return the pan to the heat and boil in 2-min stages, removing the pan from the heat each time you repeat the saucer test, until ready.", "Skim off any scum, then stir in the butter, if using, to dissolve any remaining scum. Leave for 15 mins, then ladle into sterilised jars (see below). Will keep for up to 6 months in a cool, dark place."]', 4.00, 4, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/peach-melba-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/melba-a988f00.jpg?resize=440,400', '3', 'PT30M', 'PT20M', 'PT50M', '2025-09-27 08:38:28.207692', '2025-09-27 08:38:28.207692');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (777, 'Apricot jam', '[{"name": "1 ½kgapricot", "unit": "", "amount": ""}, {"name": "200mlapple juice", "unit": "", "amount": ""}, {"name": "1kgpreserving sugar", "unit": "", "amount": ""}, {"name": "juice 1 large lemon", "unit": "", "amount": ""}, {"name": "knob of  butter", "unit": "", "amount": ""}]', '["Put a couple of small plates in the freezer. Halve and stone the apricots, chop the flesh, then put in a large pan with the apple juice. Crack some of the kernels to extract the nut inside (this is easy to do if you crack with the flat side of a heavy saucepan), then add them to the pan – this is optional, but gives extra flavour. Bring to the boil, reduce the heat and simmer for 10 mins, until the apricots are softened.", "Stir in the sugar and lemon juice, then stir well over a moderate heat to dissolve the sugar. Increase the heat and boil for about 20 mins until jam has set. Test by spooning a little jam onto one of your cold plates. After a moment push the jam with your finger; if the jam wrinkles, it is ready. If not, return to the boil for a further 5 mins, then test again.", "Remove from the heat, skim off any scum, then stir in the butter to dissolve any remaining scum. Cool for 10 mins, stir again, then ladle into warm sterilised jars. Seal, label, then store in the fridge for 4-6 weeks."]', 5.00, 10, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/apricot-conserve', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-561099_11-32caa30.jpg?resize=440,400', '3', 'PT20M', 'PT35M', 'PT55M', '2025-09-27 08:34:35.337517', '2025-09-27 08:34:35.337517');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (778, 'Homemade Apricot Jam', '[{"item": "5 ¾ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "5 ¾"}, {"item": "2 ½ pounds fresh apricots, pitted and finely chopped", "name": "fresh apricots, pitted and finely chopped", "unit": "pounds", "quantity": "2 ½"}, {"item": "⅓ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "⅓"}, {"item": "½ teaspoon butter", "name": "butter", "unit": "teaspoon", "quantity": "½"}, {"item": "1 (1.75 ounce) package powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) package", "quantity": "1"}]', '["Combine sugar, apricots, and lemon juice in a large pot over medium heat; add butter to reduce foaming. Bring to a rolling boil, stirring constantly. Quickly stir in pectin; return to a full boil until pectin is dissolved, 1 minute. Remove from heat; skim off any foam with a metal spoon.", "Inspect jars for cracks and rings for rust, discarding any defective ones. Sterilize jars and lids in boiling water for at least 5 minutes.", "Pack apricot jam into hot, sterilized jars, filling to within ¼ inch of the top. Run a clean knife or a thin spatula around the insides of the jars to remove any air bubbles. Wipe the rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 5 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, and let rest for at least 24 hours. Press the center of each lid with a finger to ensure that the seal is tight (and the lid does not move up or down). It may take up to 2 weeks for jam to set. Store in a cool, dark area."]', 5.00, 2, 'AllRecipes', 'https://www.allrecipes.com/recipe/223063/homemade-apricot-jam/', 'https://www.allrecipes.com/thmb/dDGLeRtrIG6cUN8RV1GkZSSRjpI=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1215260-f892c32097944ba799a15b91620827a3.jpg', '56 servings', '', '', '', '2025-09-27 08:34:35.340173', '2025-09-27 08:34:35.340173');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (779, 'Dried apricot jam', '[{"name": "500gwhole dried apricotchopped", "unit": "", "amount": ""}, {"name": "juice 3 large lemons", "unit": "", "amount": ""}, {"name": "1 ½kgjam sugar", "unit": "", "amount": ""}]', '["Put the apricots in a large pan, add 1.5 litres water, cover and leave overnight to soak.", "Next day, place a small plate in the fridge to chill. Put the apricots and water on the hob, then add the lemon juice and bring to the boil. Reduce the heat, then simmer for 30 mins or until the apricots are tender and starting to break up.", "Remove from the heat and add the sugar, stirring until it dissolves. Return to the heat and boil rapidly for 20 mins or until setting point is reached. To check this, remove the plate from the fridge, put a spoonful of jam onto the plate and pop it back in the fridge for a few mins – the jam should wrinkle when you push it with your finger. A sugar thermometer will also give you the setting point of jam, which is 105C.", "Carefully pour the jam into hot sterilised jars, top with a disc of wax paper or baking parchment, seal with a lid, then leave to cool and set. The jam will last for 6 months unopened in a cool, dark cupboard."]', 5.00, 2, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/dried-apricot-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1067522_11-af44be8.jpg?resize=440,400', '4', 'PT5M', 'PT1H', 'PT1H5M', '2025-09-27 08:34:35.341198', '2025-09-27 08:34:35.341198');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (780, 'Boozy Apricot Jam', '[{"item": "3 ½ pounds firm-ripe apricots, halved and pitted", "name": "firm-ripe apricots, halved and pitted", "unit": "pounds", "quantity": "3 ½"}, {"item": "1 (1.75 ounce) package fruit pectin (such as Sure-Jell Premium Fruit Pectin)", "name": "fruit pectin (such as Sure-Jell Premium Fruit Pectin)", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "7 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "7"}, {"item": "⅓ cup apricot brandy", "name": "apricot brandy", "unit": "cup", "quantity": "⅓"}]', '["Inspect 9 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Finely chop apricots; place 5 cups in a large saucepan over medium heat. Stir in pectin; slowly bring to a full rolling boil. Add sugar; stir to dissolve. Return to a full rolling boil over high heat; boil for exactly 1 minute, stirring constantly. Skim off any foam with a ladle or a large spoon.", "Off heat, stir in apricot brandy until incorporated.", "Pack apricot jam into hot, sterilized jars, filling to within ¼ inch of tops. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and tightly screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into boiling water using a holder. Pour in more boiling water to cover the jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart. Rest for 24 hours without moving the jars. Gently press center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 5.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/264865/boozy-apricot-jam/', 'https://www.allrecipes.com/thmb/W4R-FlL7ze11Wg0h5Fsxf2Qvt6w=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5403766-cea33cc7c12f48558dc0c428501955b0.jpg', '144 servings', '', '', '', '2025-09-27 08:34:35.341967', '2025-09-27 08:34:35.341967');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (781, 'Apricot Lavender Jam', '[{"item": "4 ½ cups white sugar, divided", "name": "white sugar, divided", "unit": "cups", "quantity": "4 ½"}, {"item": "1 tablespoon dried lavender blossoms", "name": "dried lavender blossoms", "unit": "tablespoon", "quantity": "1"}, {"item": "3 ½ pounds fresh apricots, pitted and sliced", "name": "fresh apricots, pitted and sliced", "unit": "pounds", "quantity": "3 ½"}, {"item": "2 tablespoons fresh lemon juice", "name": "fresh lemon juice", "unit": "tablespoons", "quantity": "2"}, {"item": "1 (1.75 ounce) package fruit pectin (such as Sure-Jell Pectin Light for less or no sugar)", "name": "fruit pectin (such as Sure-Jell Pectin Light for less or no sugar)", "unit": "(1.75 ounce) package", "quantity": "1"}]', '["Combine ½ cup sugar and lavender blossoms in the bowl of a food processor; pulse until lavender blossoms are finely chopped and sugar is fragrant.", "Measure 5 cups sliced apricots; finely chop and place into a plastic or glass bowl with a tight-fitting lid. Stir in lavender sugar and lemon juice; mix well. Cover the bowl; refrigerate for 8 hours to overnight.", "Inspect 8 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Stir ¼ cup sugar and pectin together in a bowl; stir into apricots. Transfer apricot mixture to a large saucepan; mix well. Slowly bring to a full rolling boil that does not stop bubbling when stirred. Stir in remaining 3 ¾ cups sugar, scraping the bottom of the pan, to fully dissolve sugar. Cook for 1 minute, stirring constantly.", "Pack hot jam into the prepared jars, filling to within ¼ inch of tops. Run a clean knife or thin spatula around insides of jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and tightly screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water; bring to a boil. Lower jars 2 inches apart into boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove jars from the stockpot and place on a cloth-covered or wood surface, several inches apart. Rest for 24 hours without moving jars. Gently press center of each lid with a finger to ensure the lid does not move up or down. Remove rings for storage. Store in a cool, dark area."]', 5.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/264867/apricot-lavender-jam/', 'https://www.allrecipes.com/thmb/0saP4On05SHkYdX4PnZplbdic-0=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/8305677-cb6b1d6e8fe0492fa2ea2c6e100e386f.jpg', '128 servings', '', '', '', '2025-09-27 08:34:35.342733', '2025-09-27 08:34:35.342733');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (782, 'Apricot Jam', '[{"item": "8 cups fresh apricots - peeled, pitted, and crushed", "name": "fresh apricots - peeled, pitted, and crushed", "unit": "cups", "quantity": "8"}, {"item": "¼ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "¼"}, {"item": "6 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "6"}]', '["Gather the ingredients.", "Mix apricots and lemon juice in a large pot over medium heat; add sugar and slowly bring to a boil, stirring until sugar dissolves.", "Continue to cook and stir until apricot mixture thickens, about 25 minutes. Remove from heat and skim foam off the top if necessary.", "Meanwhile, prepare five 1-pint jars with lids and rings by cleaning and sterilizing them in the dishwasher or a boiling water bath. Leave lids in simmering water until ready to seal jars.", "Ladle hot jam into hot sterilized jars, leaving about 1/4 inch of space on top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles.", "Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil, then carefully lower the jars into the pot using a holder, leaving a 2-inch space between the jars. Pour in more boiling water if necessary until the water level is at least 1 inch above the tops of the jars. Bring the water to a full boil, cover the pot, and process for 15 minutes.", "Remove the jars from the stockpot and place them onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight and the lid does not move up or down at all. Store in a cool, dark area."]', 4.70, 77, 'AllRecipes', 'https://www.allrecipes.com/recipe/222949/apricot-jam/', 'https://www.allrecipes.com/thmb/V3M4UDwX-Zud6k-X-TO6rTOPCGM=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/222949-apricot-jam-VAT-Beauty-2x1-178e497a782d47af8822d5a829731169.jpg', '50 servings', '', '', '', '2025-09-27 08:34:35.344137', '2025-09-27 08:34:35.344137');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (783, 'Dried Apricot Jam', '[{"item": "4 ½ cups dried apricots", "name": "dried apricots", "unit": "cups", "quantity": "4 ½"}, {"item": "4 ½ cups boiling water", "name": "boiling water", "unit": "cups", "quantity": "4 ½"}, {"item": "1 teaspoon vanilla extract", "name": "vanilla extract", "unit": "teaspoon", "quantity": "1"}, {"item": "1 (1.75 ounce) package powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "7 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "7"}, {"item": "¼ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "¼"}]', '["Soak apricots in boiling water in a bowl until hydrated, about 30 minutes. Blend apricots, remaining water, and vanilla extract in a food processor, working in batches, until blended but still slightly chunky.", "Combine apricot mixture with pectin in a large pot over medium heat; cook until just boiling. Add sugar and lemon juice; boil until sugar has dissolved, 1 to 2 minutes.", "Sterilize jars and lids in boiling water for at least 5 minutes. Pack the apricot jam into the hot, sterilized jars, filling the jars to within 1/4-inch of the top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil, then carefully lower the jars into the pot using a holder. Leave a 2-inch space between the jars. Pour in more boiling water if necessary until the water level is at least 1 inch above the tops of the jars. Bring the water to a full boil, cover the pot, and process for 10 minutes.", "Remove jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool, about 1 hour. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool, dark area."]', 4.60, 11, 'AllRecipes', 'https://www.allrecipes.com/recipe/223062/dried-apricot-jam/', 'https://www.allrecipes.com/thmb/LZgikrqsyNJhwOGXM0x_Iwfn2fM=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1044664-7eeb6523d6d84799a977424734f52b73.jpg', '144 servings', '', '', '', '2025-09-27 08:34:35.345628', '2025-09-27 08:34:35.345628');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (784, 'Bourbon Peach Jam', '[{"item": "3 1/2poundsripepeaches", "name": "3 1/2poundsripepeaches", "unit": "", "quantity": ""}, {"item": "7 1/2cupsgranulated sugar", "name": "7 1/2cupsgranulated sugar", "unit": "", "quantity": ""}, {"item": "1/4cupfreshly squeezed lemon juice", "name": "1/4cupfreshly squeezed lemon juice", "unit": "", "quantity": ""}, {"item": "1/2cupbourbon", "name": "1/2cupbourbon", "unit": "", "quantity": ""}, {"item": "1cinnamonstick", "name": "1cinnamonstick", "unit": "", "quantity": ""}, {"item": "1vanilla bean, split lengthwise", "name": "1vanilla bean, split lengthwise", "unit": "", "quantity": ""}, {"item": "1/2teaspoonunsalted butter", "name": "1/2teaspoonunsalted butter", "unit": "", "quantity": ""}, {"item": "One(6-ounce) packageliquid pectin(2 foil packets)", "name": "One(6-ounce) packageliquid pectin(2 foil packets)", "unit": "", "quantity": ""}]', '["If you are going to preserve the jam, prepare the jars and lids: place 6 half-pint jars on a rack in a large pot. Add enough water to cover jars, and bring to boil over high heat. Boil for 10 minutes, then turn off heat and allow jars to rest in hot water. Meanwhile, put bands and lids in a small saucepan and cover with water. Heat over medium heat until water is simmering, then remove pan from heat and allow bands and lids to rest in hot water until ready to use.", "Bring a large pot of water to a boil. Fill a large bowl with ice water. Cut a shallow X into the bottom of each peach and drop them into the boiling water. Blanch for 20 to 30 seconds, then immediately plunge peaches into ice water. Peel, pit, and chop the peaches. Transfer them to a blender and pulse just until they are coarsely puréed. You should have about 4 cups of purée.", "Transfer peaches to a large, heavy-bottomed pot. Add sugar, lemon juice, bourbon, cinnamon stick, vanilla bean, and butter. Bring to a full rolling boil over medium-high heat, stirring constantly. Add pectin and return mixture to a full rolling boil. Boil hard for 1 minute. Remove pot from heat and skim any foam from the surface with a metal spoon. Discard cinnamon stick and vanilla bean.", "Ladle hot jam into hot sterilized jars, leaving 1/4-inch headspace. Wipe rims of the jars, cover with lids, and screw bands on until just barely tight. Place jars on rack in pot and cover completely with water. Cover pot and bring to a boil over high heat. Boil for 10 minutes. Turn off heat, uncover pot, and allow jars to rest in water for 5 minutes. Remove jars from pot and allow them to rest undisturbed on countertop for 6 hours or overnight. Preserved jam will keep for up to 1 year in a cool, dark place. Unpreserved jam will keep in the refrigerator for about 6 months."]', 4.30, 13, 'Serious Eats', 'https://www.seriouseats.com/bourbon-peach-jam-recipe', 'https://www.seriouseats.com/thmb/DjsyEfEqNkhhPFrWfFsW-SeeJkY=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__recipes__images__2011__07__20110724bourbonpeachjam-17c8e3b2a1434c2cbb13b9b91b48ebf0.jpg', '48 servings', 'Prep10 mins', '', 'Total90 mins', '2025-09-27 08:34:35.346912', '2025-09-27 08:34:35.346912');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (863, 'Auntie Doris''s Peach Jam', '[{"item": "12  fresh peaches - peeled, pitted and chopped", "name": "fresh peaches - peeled, pitted and chopped", "unit": "", "quantity": "12"}, {"item": "4  oranges", "name": "oranges", "unit": "", "quantity": "4"}, {"item": "6 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "6"}]', '["Place the peaches into a large bowl. Scrub the oranges then chop into pieces - including the peel. This is easiest done in a food processor so you don''t lose as much juice. Transfer to the bowl with the peaches. Stir in sugar, cover, and refrigerate overnight.", "The next day, pour the entire contents of the bowl into a large pot. Bring to a simmer over low heat and cook for 2 to 3 hours, stirring occasionally. Make sure the heat is low enough, or the jam will burn and stick to the bottom of the pot. This does not need to come to a rolling boil.", "While the jam simmers, sterilize your jars in boiling water for at least 5 minutes, and keep hot. Prepare new lids according to the manufacturer''s instructions. Ladle the jam into the jars, leaving 1/4 inch of headspace. Wipe the rims with a clean damp cloth or paper towel, and seal with lids and rings. Process according to guidelines suggested by your local extension."]', 3.80, 18, 'AllRecipes', 'https://www.allrecipes.com/recipe/90198/auntie-doriss-peach-jam/', 'https://www.allrecipes.com/thmb/U41ax4v1cQXygJ51vmdQa70-8So=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/683842-ff2dfb32421b43959a26559eada70379.jpg', '48 servings', '', '', '', '2025-09-27 08:38:28.208473', '2025-09-27 08:38:28.208473');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (785, 'Apricot Jam Pots', '[{"item": "3 ½ cups all-purpose flour", "name": "all-purpose flour", "unit": "cups", "quantity": "3 ½"}, {"item": "2 cups butter, softened", "name": "butter, softened", "unit": "cups", "quantity": "2"}, {"item": "1 cup confectioners'' sugar", "name": "confectioners'' sugar", "unit": "cup", "quantity": "1"}, {"item": "2 tablespoons cornstarch", "name": "cornstarch", "unit": "tablespoons", "quantity": "2"}, {"item": "24 ounces apricot preserves", "name": "apricot preserves", "unit": "ounces", "quantity": "24"}, {"item": "3 cups sweetened flaked coconut", "name": "sweetened flaked coconut", "unit": "cups", "quantity": "3"}, {"item": "1 ½ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "1 ½"}, {"item": "3  eggs", "name": "eggs", "unit": "", "quantity": "3"}]', '["Mix flour, butter, confectioners'' sugar, and cornstarch together in a bowl until dough is just mixed; refrigerate until chilled, at least 30 minutes.", "Preheat oven to 350 degrees F (175 degrees C). Line 6 dozen miniature muffin cups with paper liners.", "Roll dough into small balls and press each ball into the bottom and up the sides of each muffin cup. Fill each cup 3/4-full with apricot preserves.", "Mix coconut, white sugar, and eggs together in a bowl; spoon over apricot filling, making sure to cover completely.", "Bake in the preheated oven until topping is lightly browned, 25 to 30 minutes.", "Make sure topping is on top of cookie and not over sides as this might cause difficulty in removing cookies from pan."]', 4.30, 3, 'AllRecipes', 'https://www.allrecipes.com/recipe/240882/apricot-jam-pots/', 'https://www.allrecipes.com/thmb/3hfKsqfjwHrg1TVulkvSm_CIP4U=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/2002130-f433359986144f2b91483a3bc35bd17c.jpg', '72 servings', '', '', '', '2025-09-27 08:34:35.348331', '2025-09-27 08:34:35.348331');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (786, 'Damson jam', '[{"name": "900gdamsonsstalks removed", "unit": "", "amount": ""}, {"name": "900ggolden granulated sugar", "unit": "", "amount": ""}, {"name": "knob ofbutter", "unit": "", "amount": ""}]', '["Wash your jars and lids in hot, soapy water, rinse, then place on a baking tray and put in a low oven for 10 mins or until completely dry. If you want to use rubber seals, remove the seals and cover in just-boiled water. Make sure you sterilise any funnels, ladles or spoons you’re going to be using too.", "Put the fruit into a jam pan or a large, wide, heavy-based saucepan. Leave the stones in (see tip below). Add 150ml water and bring to the boil. Put a couple of saucers in the freezer. Lower the heat and simmer for 15 mins or until the fruit is soft.", "Tip in the sugar and stir over a very low heat until the sugar has completely dissolved, up to 10 mins. This step is vital – if you don’t dissolve the sugar, the bottom of the pan may catch and burn. Raise the heat, bring to a full rolling boil, then rapidly boil for 10 minutes. Don’t stir until the setting point of 105C is reached. If you don’t have a thermometer, test the jam by spooning a little onto a cold saucer. Wait a few seconds, then push the jam with your fingertip. If it wrinkles, the jam is ready. If not, cook for a few mins more and test again, with another cold saucer. Once you have reached 105C or setting point, stir the jam thoroughly.", "Remove from the heat, skim off any excess scum, then stir a knob of butter into the surface (this helps to dissolve any remaining scum). Leave for about 15 mins so the fruit can settle – if you decant the jam too soon, all the fruit will sink to the bottom. Pour into sterilised jars, label and seal."]', 4.00, 44, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/damson-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/damson_jam-b339d55.jpg?resize=440,400', '4', 'PT10M', 'PT50M', 'PT1H', '2025-09-27 08:34:35.34954', '2025-09-27 08:34:35.34954');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (787, 'Rustic Apricot Jam', '[{"item": "6poundsfirm-but-ripe freestoneapricots, such as Blenheims, halved and pitted", "name": "6poundsfirm-but-ripe freestoneapricots, such as Blenheims, halved and pitted", "unit": "", "quantity": ""}, {"item": "2pounds10ouncesgranulated sugar", "name": "2pounds10ouncesgranulated sugar", "unit": "", "quantity": ""}, {"item": "3ounces(6tablespoons)fresh lemon juicefrom3lemons", "name": "3ounces(6tablespoons)fresh lemon juicefrom3lemons", "unit": "", "quantity": ""}]', '["In a large stainless steel or glass mixing bowl, combine apricots, sugar, and 1 1/2 ounces (3 tablespoons) lemon juice and mix until all the sugar is moistened; if some sugar remains dry, allow to macerate until fruit has released enough juices to moisten sugar, 5 to 15 minutes.", "Thoroughly wash 12 half-pint canning jars and their lids. If you plan to process the jam for shelf-stable storage, prepare a water bath and sterilize the jars as described in ourcanning guide. Set several metal spoons on a plate in the freezer. Scrape apricot mixture into a wide shallow pot and heat over medium-high heat, stirring regularly, until sugar is melted and mixture starts to bubble, 10 to 15 minutes.", "Cook apricot mixture over medium-high heat, stirring as needed to prevent scorching, until jam starts to foam, about 15 minutes. Continue to cook, stirring minimally and lowering the heat if necessary to prevent scorching, until foaming has subsided, about 15 minutes longer; scrape any foam off jam surface with a stainless steel spoon as needed.", "Taste jam for tartness: If the jam tastes too sweet without a good balance of tartness, add 1/2 ounce (1 tablespoon) of the remaining lemon juice at a time, stirring well and tasting between additions, until the lemony flavor is just barely perceptible and the balance of sweet and tart has been reached (like good lemonade).", "Continue to cook until bubbling has slowed and jam looks glossy and appears thickened around the edge, 5 to 15 minutes; lower heat as necessary to prevent scorching. Turn off heat and set a dollop of jam on one of the freezing-cold spoons, then return to freezer for 5 minutes. Jam is ready once it holds together and doesn''t run off the spoon when tilted. If jam is too runny, return to heat and cook, stirring frequently and repeating the spoon test every 5 minutes, until jam passes test.", "Transfer jam into prepared canning jars and wipe any jam from the rims. To store in refrigerator, simply place lids on jars, screw on rings, and let cool completely at room temperature before refrigerating. To process jam for shelf-stable storage, bring water bath to a rolling boil. Place lids on jars and screw on rings until they are snug but not overly tight. Carefully lower jars into boiling water bath and process for 10 minutes. Remove jars and let cool completely at room temperature. Unprocessed jam can be refrigerated for a couple of months; Processed jam can be stored in a cool, dark place for up to 1 year."]', 4.00, 2, 'Serious Eats', 'https://www.seriouseats.com/rustic-apricot-jam-recipe', 'https://www.seriouseats.com/thmb/zonP5rsHvLORD8oeA3ME6WPaQoA=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__recipes__images__2014__08__08112014-apricot-jam-toast-jennifer-latham-59121b73603a4556b9bf38bc797d4916.jpg', '192 servings', 'Prep10 mins', '', 'Total110 mins', '2025-09-27 08:34:35.351764', '2025-09-27 08:34:35.351764');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (790, 'Blackberry jam', '[{"name": "1.8kgblackberries", "unit": "", "amount": ""}, {"name": "1 ½kgjam sugar(the one with added pectin)", "unit": "", "amount": ""}, {"name": "juice and pips of 1 lemon", "unit": "", "amount": ""}, {"name": "finger-tip size knob of butter(optional)", "unit": "", "amount": ""}]', '["The night before you make your jam, layer the blackberries and sugar together in a very large bowl, then cover and set aside at room temperature. This helps the sugar to start dissolving so you don’t run the risk of over-cooking the fruit when you actually begin to make the jam. The next morning, give everything a quick stir, then set aside again until you are ready to start cooking.", "Before you start, put a small saucer in the freezer. Take a preserving pan or a large, wide-based pan (the wider and more open the pan, the faster the jam will be ready, so a preserving pan is really ideal) and tip the berries in, scraping out all the juices and any undissolved sugar. Stir in the lemon juice, then collect all the pips and secure them inside a tea-leaf strainer or piece of muslin before adding them to the pan (cooking the pips along with the fruit extracts the pectin they contain, helping your jam to set).", "Start the blackberries over a low heat until all the sugar is completely dissolved, then bring to the boil and simmer for 5 mins. Turn off the heat and spoon a little hot syrupy jam onto the chilled saucer. Once it''s cool, push it with your finger. If it wrinkles a little, it’s ready and has reached setting point. If it''s too runny to wrinkle, return the pan to the heat and boil in 2 or 3-minute stages, removing the pan from the heat each time you do the saucer check, until the jam wrinkles.", "Skim off any excess scum, then stir in the knob of butter, if you want – this will help to dissolve any remaining scum. Leave the jam for 15 mins before ladling into sterilised jars – this allows the fruit to settle so it doesn’t sink to the bottom. The jam will keep in a cool, dark place for at least 6 months. Refrigerate jars once opened."]', 5.00, 38, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/blackberry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1067485_11-5fa48a1.jpg?resize=440,400', '2', 'PT20M', 'PT40M', 'PT1H', '2025-09-27 08:34:46.455166', '2025-09-27 08:34:46.455166');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (791, 'Blueberry-Blackberry Jam', '[{"item": "2 tablespoons slivered almonds", "name": "slivered almonds", "unit": "tablespoons", "quantity": "2"}, {"item": "2 ½ cups crushed ripe blueberries", "name": "crushed ripe blueberries", "unit": "cups", "quantity": "2 ½"}, {"item": "2 ½ cups crushed ripe blackberries", "name": "crushed ripe blackberries", "unit": "cups", "quantity": "2 ½"}, {"item": "1 (1.75 ounce) package fruit pectin (such as Sure-Jell Premium Fruit Pectin®)", "name": "fruit pectin (such as Sure-Jell Premium Fruit Pectin®)", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "7 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "7"}, {"item": "  lemon, juiced", "name": "lemon, juiced", "unit": "", "quantity": ""}, {"item": "2 tablespoons gin", "name": "gin", "unit": "tablespoons", "quantity": "2"}]', '["Inspect 8 half-pint jars for cracks and rings for rust; discard any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Toast the almonds in an ungreased skillet until lightly colored, 3 to 5 minutes. Set aside.", "Place crushed blueberries and blackberries in a large pot and add pectin. Slowly bring to a full rolling boil. Add sugar and lemon juice and stir to dissolve. Return to a full rolling boil for exactly 1 minute, stirring constantly. Skim off any foam with a ladle or a large spoon.", "Remove the pot from the heat. Stir in gin and toasted almonds.", "Ladle jam immediately into the prepared jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any spills. Top with lids and tightly screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart. Let rest for 24 hours without moving the jars. Gently press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 5.00, 3, 'AllRecipes', 'https://www.allrecipes.com/recipe/265992/blueberry-blackberry-jam/', 'https://www.allrecipes.com/thmb/iKamLm9pJnXGzbpPg2pfhMh8cjI=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5508980-5127e7e1f9284e40a57e60b20897ea24.jpg', '128 servings', '', '', '', '2025-09-27 08:34:46.457599', '2025-09-27 08:34:46.457599');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (792, 'Blackberry Jam', '[{"item": "4 cups blackberries", "name": "blackberries", "unit": "cups", "quantity": "4"}, {"item": "1 cup white sugar", "name": "white sugar", "unit": "cup", "quantity": "1"}, {"item": "2 tablespoons cornstarch", "name": "cornstarch", "unit": "tablespoons", "quantity": "2"}, {"item": "¼ teaspoon ground cinnamon", "name": "ground cinnamon", "unit": "teaspoon", "quantity": "¼"}, {"item": "¼ teaspoon ground allspice", "name": "ground allspice", "unit": "teaspoon", "quantity": "¼"}, {"item": "½ teaspoon lemon juice", "name": "lemon juice", "unit": "teaspoon", "quantity": "½"}]', '["Gather all ingredients.", "Mash blackberries in a saucepan with a potato masher.", "Stir in sugar until juices are released. Remove 1 tablespoon blackberry juice from the saucepan; mix with cornstarch in a small bowl. Stir cornstarch mixture back into the saucepan.", "Bring blackberries to a boil; cook, stirring often, until jam has thickened, about 15 minutes.", "Stir in cinnamon and allspice. Remove saucepan from the heat and allow jam to cool, 30 to 45 minutes.", "Transfer jam to a bowl, cover, and refrigerate until chilled. Stir in lemon juice before serving."]', 4.80, 135, 'AllRecipes', 'https://www.allrecipes.com/recipe/223332/blackberry-jam/', 'https://www.allrecipes.com/thmb/L1yGW6nZNIS4TjAWrz-a-RUY-_c=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/AR-223332-blackberry-jam-ddmfs-2x1-09fc6975925248fd91352a95f0903370.jpg', '32 servings', '', '', '', '2025-09-27 08:34:46.459025', '2025-09-27 08:34:46.459025');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (793, 'Angela''s ''flexible'' jam recipe', '[{"name": "900g fruit(blackberries, plums, raspberries or strawberries), prepared weight", "unit": "", "amount": ""}, {"name": "900g golden granulated sugar", "unit": "", "amount": ""}, {"name": "knob of  butter", "unit": "", "amount": ""}]', '["Put the fruit into a preserving pan or large heavy-based saucepan. For blackberries, add 50ml of water and 1½ tbsp of lemon juice; for plums (halved and stoned), use 150ml of water; for strawberries, add 3 tbsp of lemon juice (no water); and for raspberries, add nothing. Bring to the boil.", "Lower the heat. For blackberries, simmer for 15 minutes; for plums, simmer for 30-40 minutes; for raspberries, simmer for 2 minutes; for strawberries, simmer for 5 minutes. The fruit should be soft.", "Tip in the sugar, stir over a very low heat until the sugar has completely dissolved. Raise the heat, bring to a full rolling boil, then rapidly boil blackberries for 10-12 minutes, plums for 10 minutes, raspberries for 5 minutes or strawberries for 20-25 minutes - don’t stir though – until the setting point of 105C is reached.", "Remove from the heat, skim off any excess scum, then stir a knob of butter across the surface (this helps to dissolve any remaining scum). Leave for about 15 mins so the fruit can settle. Pour into sterilised jars, label and seal."]', 4.00, 80, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/angelas-flexible-jam-recipe', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1082452_11-ac2ef93.jpg?resize=440,400', '1', 'PT10M', 'PT50M', 'PT1H', '2025-09-27 08:34:46.459771', '2025-09-27 08:34:46.459771');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (794, 'Blackberry jelly (bramble jelly)', '[{"name": "800gblackberries", "unit": "", "amount": ""}, {"name": "200gcooking apple(1 medium sized cooking apple)", "unit": "", "amount": ""}, {"name": "1lemonjuiced", "unit": "", "amount": ""}, {"name": "Around 400gpreserving sugar", "unit": "", "amount": ""}, {"name": "Muslin cloth", "unit": "", "amount": ""}]', '["Put two small saucers in the freezer – you will use these to test the setting point of the jam.", "Wash and drain the fruit, cut the apple into 2-inch pieces and tip into a large, heavy-based saucepan with 200ml water and the lemon juice. Bring to the boil, then reduce the heat and simmer with a lid on for 10-15 mins until the fruit is soft. Stir occasionally using the back of a wooden spoon to help break down the fruit.", "Remove from the heat and allow to cool a little. Place the fruit in a muslin lined sieve over a large mixing bowl and allow the juice to drip through. Discard the contents of your sieve, retaining your muslin to use another time.", "Weigh the remaining liquid and return to the saucepan. Weigh three quarters of sugar to your total liquid amount (for example, if you have 400g of liquid, add 300g of sugar). Add the sugar to the berry liquid and stir over a low medium heat until completely dissolved.", "Turn up the heat and bring the fruit to the boil, stirring occasionally, and allow to boil hard for about 8 mins. If using a thermometer, you’re looking to reach 105C. If not, check the setting point of the liquid after 8 mins by removing a saucer from the freezer and adding a teaspoon of the berry liquid to the plate. Allow to sit for a minute. Push your finger through the liquid – if it starts to wrinkle the jelly is ready. If not, return to the boil and try again after another minute.", "Pour into sterilised jars and allow to cool completely. You can sterilise the saucers by washing them in hot soapy liquid, rinsing and placing in an oven at 180C/160C fan/gas 4 for 5 mins. Remove carefully and allow to cool. Store in a cool dark place."]', 4.00, 10, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/blackberry-jelly', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/09/Blackberry-jelly-18a4920.jpg?resize=768,713', '2', 'PT10M', 'PT20M', 'PT30M', '2025-09-27 08:34:46.460621', '2025-09-27 08:34:46.460621');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (864, 'Pear Jam', '[{"item": "4 ½ cups mashed ripe pears", "name": "mashed ripe pears", "unit": "cups", "quantity": "4 ½"}, {"item": "¼ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "¼"}, {"item": "3 tablespoons powdered fruit pectin", "name": "powdered fruit pectin", "unit": "tablespoons", "quantity": "3"}, {"item": "1 teaspoon ground cinnamon", "name": "ground cinnamon", "unit": "teaspoon", "quantity": "1"}, {"item": "½ teaspoon ground cloves", "name": "ground cloves", "unit": "teaspoon", "quantity": "½"}, {"item": "½ teaspoon ground allspice", "name": "ground allspice", "unit": "teaspoon", "quantity": "½"}, {"item": "½ teaspoon ground nutmeg", "name": "ground nutmeg", "unit": "teaspoon", "quantity": "½"}, {"item": "7 ½ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "7 ½"}, {"item": "1 teaspoon butter", "name": "butter", "unit": "teaspoon", "quantity": "1"}, {"item": "8  half-pint canning jars with lids and rings, or as needed", "name": "half-pint canning jars with lids and rings, or as needed", "unit": "", "quantity": "8"}]', '["Gather all ingredients.", "Mix pears, lemon juice, fruit pectin, cinnamon, cloves, allspice, and nutmeg in a large heavy pot; bring to a boil, stirring constantly.", "Add sugar all at once, stirring, and bring back to a full rolling boil. Boil for 1 minute.", "Mix in butter to settle foam.", "Sterilize the jars and lids in boiling water for at least 5 minutes.", "Pack the pear jam into the hot, sterilized jars, filling the jars to within 1/4 inch of the top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw-on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars into the boiling water using a holder. Leave a 2-inch space between the jars. Pour in more boiling water if necessary to bring the water level to at least 1 inch above the tops of the jars. Bring the water to a rolling boil, cover the pot, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool, dark area for up to one year."]', 4.70, 47, 'AllRecipes', 'https://www.allrecipes.com/recipe/228625/pear-jam/', 'https://www.allrecipes.com/thmb/Q3c4yEb8nLG1BiIs80QNgWqbp9k=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/228625-pear-jam-DDMFS-001-2x1-38f6c98a57124d3ba5ddf10a58972266.jpg', '64 servings', '', '', '', '2025-09-27 08:38:38.935433', '2025-09-27 08:38:38.935433');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (865, 'Prickly Pear Jam', '[{"item": "8  prickly pears", "name": "prickly pears", "unit": "", "quantity": "8"}, {"item": "3 tablespoons freshly squeezed lemon juice", "name": "freshly squeezed lemon juice", "unit": "tablespoons", "quantity": "3"}, {"item": "2 ½ tablespoons powdered fruit pectin", "name": "powdered fruit pectin", "unit": "tablespoons", "quantity": "2 ½"}, {"item": "1 teaspoon powdered fruit pectin", "name": "powdered fruit pectin", "unit": "teaspoon", "quantity": "1"}, {"item": "2 ½ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "2 ½"}, {"item": "1 ½ teaspoons amaretto liqueur", "name": "amaretto liqueur", "unit": "teaspoons", "quantity": "1 ½"}]', '["Inspect a 1-quart jar and 1-pint jar for cracks and rings for rust, discarding any that are defective. Immerse jars in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Wear protective gloves to rub and rinse prickly pears under running water to make sure all spines are removed. Cut each pear in half; scoop pulp and seeds and place in the bowl of a food processor. Pulse for 30 to 40 seconds. Transfer mixture to a strainer lined with cheesecloth set over a bowl. Gather cheesecloth into a pouch and squeeze out juice. Remove any remaining pulp from seeds and add to the bowl with juice.", "Combine prickly pear juice, pectin, and lemon juice in a saucepan over medium heat. Bring to a rolling boil that can''t be stirred down. Add sugar and amaretto; stir until completely dissolved. Return mixture to a rolling boil, constantly stirring. Cook until jam is reduced by half, 5 to 7 minutes.", "Remove jam from heat. Skim off and discard any froth that may have formed. Dip a metal spoon into jam to test for desired thickness. Add another 1/2 teaspoon of pectin and boil for 1 minute more if you desire a thicker jam.", "Pack jam into hot, sterilized jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any spills. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill it halfway with water. Bring to a boil and lower jars 2 inches apart into boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove jars from the stockpot and let rest, several inches apart, for 12 to 24 hours. Press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store jam in a cool, dark area."]', 3.80, 6, 'AllRecipes', 'https://www.allrecipes.com/recipe/266437/prickly-pear-jam/', 'https://www.allrecipes.com/thmb/7CNvhe-Rwnt_WGCzZCjw46p8Tfs=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5539237-prickly-pear-jam-Sandra-Garth-4x3-1-74c6965e9b1f44428c00e34b34f7c618.jpg', '80 servings', '', '', '', '2025-09-27 08:38:38.938743', '2025-09-27 08:38:38.938743');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (795, 'Bumbleberry Jam', '[{"item": "6 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "6"}, {"item": "1 cup chopped fresh rhubarb", "name": "chopped fresh rhubarb", "unit": "cup", "quantity": "1"}, {"item": "1 cup mashed fresh strawberries", "name": "mashed fresh strawberries", "unit": "cup", "quantity": "1"}, {"item": "1 cup mashed fresh peaches", "name": "mashed fresh peaches", "unit": "cup", "quantity": "1"}, {"item": "1 cup mashed fresh blackberries", "name": "mashed fresh blackberries", "unit": "cup", "quantity": "1"}, {"item": "1 cup mashed fresh blueberries", "name": "mashed fresh blueberries", "unit": "cup", "quantity": "1"}, {"item": "1 cup chopped fresh apples", "name": "chopped fresh apples", "unit": "cup", "quantity": "1"}, {"item": "1 (1.75 ounce) package fruit pectin (such as Sure-Jell)", "name": "fruit pectin (such as Sure-Jell)", "unit": "(1.75 ounce) package", "quantity": "1"}]', '["Combine sugar, rhubarb, strawberries, peaches, blackberries, blueberries, and apples in a pot over medium heat; bring to a boil. Cook at a boil for 10 minutes, stirring constantly. Stir in pectin; continue boiling until jam coats the back of a metal spoon, about 10 minutes more.", "Pack hot jam into hot, sterilized jars, filling to within ¼ inch of tops. Run a clean knife or thin spatula around insides of jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water; bring to a boil and lower the jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove jars from the stockpot; rest, several inches apart, for 12 to 24 hours. Press center of each lid with a finger to ensure the lid does not move up or down. Remove rings for storage and store in a cool, dark area."]', 5.00, 2, 'AllRecipes', 'https://www.allrecipes.com/recipe/270449/bumbleberry-jam/', 'https://www.allrecipes.com/thmb/HtKq-wtMIcQi3Ymm1l-By62OQ_Q=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/6919856-0cd757897f0042baaa753a10126cf1f1.jpg', '80 servings', '', '', '', '2025-09-27 08:35:01.716248', '2025-09-27 08:35:01.716248');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (796, 'Blueberry and Raisin Jam', '[{"item": "12 ounces raisins", "name": "raisins", "unit": "ounces", "quantity": "12"}, {"item": "½ cup orange juice", "name": "orange juice", "unit": "cup", "quantity": "½"}, {"item": "4 pints fresh blueberries", "name": "fresh blueberries", "unit": "pints", "quantity": "4"}, {"item": "1 (1.75 ounce) package powdered low-sugar pectin", "name": "powdered low-sugar pectin", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}]', '["Blend raisins and orange juice in a blender or food processor until smooth; transfer mixture to a pot. Blend blueberries in blender or food processor until pureed; add to raisin mixture. Stir pectin into fruit mixture and bring to a boil. Mix sugar into fruit mixture; boil, stirring often, until sugar is dissolved, about 5 more minutes.", "Sterilize the jars and lids in boiling water for 10 minutes. Pack the jam into the hot, sterilized jars, filling the jars to within 1/4 inch of the top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars into the boiling water using a holder. Leave a 2-inch space between the jars. Pour in more boiling water if necessary to bring the water level to at least 1 inch above the tops of the jars. Bring the water to a rolling boil, cover the pot, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool dark area."]', 5.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/233375/blueberry-and-raisin-jam/', 'https://www.allrecipes.com/thmb/yO09E3CjaNcWDm7gS5ep9hPppkc=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1110086-c646b8be48c74eaaae792f3573700b7d.jpg', '10 servings', '', '', '', '2025-09-27 08:35:01.719793', '2025-09-27 08:35:01.719793');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (797, 'Easy Small-Batch Blueberry Jam', '[{"item": "4 cups fresh blueberries", "name": "fresh blueberries", "unit": "cups", "quantity": "4"}, {"item": "1 cup white sugar", "name": "white sugar", "unit": "cup", "quantity": "1"}, {"item": "1 tablespoon fresh lemon juice", "name": "fresh lemon juice", "unit": "tablespoon", "quantity": "1"}, {"item": "1 pinch ground cinnamon (Optional)", "name": "ground cinnamon (Optional)", "unit": "pinch", "quantity": "1"}]', '["Gather all ingredients.", "Mix blueberries, sugar, lemon juice, and cinnamon in a saucepan.", "Cook, stirring constantly, over medium heat until thickened, about 30 minutes.", "Serve and enjoy!"]', 4.80, 275, 'AllRecipes', 'https://www.allrecipes.com/recipe/239514/easy-small-batch-blueberry-jam/', 'https://www.allrecipes.com/thmb/m4BFSnHyxaGCInvq-bpNcRz1ZGM=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/239514-Easy-Small-Batch-Blueberry-Jam-DDMFS-2x1-0521-a1330af473c64397b5a5b962fed86ed9.jpg', '16 servings', '', '', '', '2025-09-27 08:35:01.720799', '2025-09-27 08:35:01.720799');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (798, 'Blueberry Fig Jam', '[{"item": "¼ cup blueberries", "name": "blueberries", "unit": "cup", "quantity": "¼"}, {"item": "2 cups quartered fresh figs", "name": "quartered fresh figs", "unit": "cups", "quantity": "2"}, {"item": "¾ cup white sugar", "name": "white sugar", "unit": "cup", "quantity": "¾"}, {"item": "½ cup water", "name": "water", "unit": "cup", "quantity": "½"}, {"item": "1 tablespoon lime juice", "name": "lime juice", "unit": "tablespoon", "quantity": "1"}, {"item": "½ teaspoon balsamic vinegar", "name": "balsamic vinegar", "unit": "teaspoon", "quantity": "½"}]', '["Mash blueberries in a small bowl with a fork; transfer to a 3 ½-quart pot. Add figs, sugar, water, lime juice, and balsamic vinegar; lightly pulse with an immersion blender until mostly smooth and some chunks remain for texture.", "Place the pot over medium-high heat; stir continuously until comes to a boil. Reduce heat to low; cook, stirring periodically, until thickened, 20 to 25 minutes. Check doneness by dropping a small spoonful of jam onto a frozen plate. Let sit for 1 to 2 minutes; if jam appears to gel, it is ready. Continue cooking if jam appears thin and runny. Remove jam from the heat when jam has thickened; cool slightly, about 10 minutes.", "Pour jam into a sterilized jar with a hermetic seal. Cool completely before refrigerating."]', 4.80, 4, 'AllRecipes', 'https://www.allrecipes.com/recipe/275586/blueberry-fig-jam/', 'https://www.allrecipes.com/thmb/fWwNtjeVC-K-7gep0b-7QX4EhgA=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/6957902-5191866f26ba4f52873f20878defc75f.jpg', '12 servings', '', '', '', '2025-09-27 08:35:01.721605', '2025-09-27 08:35:01.721605');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (866, 'Pineapple and Coconut Jam', '[{"item": "1 (20 ounce) can pineapple chunks, drained, liquid reserved", "name": "pineapple chunks, drained, liquid reserved", "unit": "(20 ounce) can", "quantity": "1"}, {"item": "½ cup brown sugar", "name": "brown sugar", "unit": "cup", "quantity": "½"}, {"item": "½ cup finely grated coconut", "name": "finely grated coconut", "unit": "cup", "quantity": "½"}]', '["Grind pineapple chunks in a food processor. Stir pineapple, reserved liquid, and brown sugar together in a saucepan; bring to a boil, reduce heat to low, and simmer until sugar dissolves and mixture thickens, about 20 minutes.", "Stir coconut into pineapple mixture, return to a simmer, and cook until flavors blend, about 5 minutes more. Store in a sealable jar or container."]', 4.50, 2, 'AllRecipes', 'https://www.allrecipes.com/recipe/240439/pineapple-and-coconut-jam/', 'https://www.allrecipes.com/thmb/Py-P4AOF3c9TxbFsrimUNHJ2M-M=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/9205409-d06be3985c5146e898af6bd40c312767.jpg', '24 servings', '', '', '', '2025-09-27 08:38:51.191682', '2025-09-27 08:38:51.191682');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (799, 'Blueberry Lavender Jam', '[{"item": "2tablespoonsdried lavender buds", "name": "2tablespoonsdried lavender buds", "unit": "", "quantity": ""}, {"item": "8cupsfresh blueberries", "name": "8cupsfresh blueberries", "unit": "", "quantity": ""}, {"item": "4 1/2cupsgranulated sugar", "name": "4 1/2cupsgranulated sugar", "unit": "", "quantity": ""}, {"item": "Zest and juiceof1lemon", "name": "Zest and juiceof1lemon", "unit": "", "quantity": ""}, {"item": "One(1.75-ounce) package low sugar powdered pectin, such asSure Jell", "name": "One(1.75-ounce) package low sugar powdered pectin, such asSure Jell", "unit": "", "quantity": ""}, {"item": "1/2teaspoonunsalted butter", "name": "1/2teaspoonunsalted butter", "unit": "", "quantity": ""}]', '["If you are going to preserve jam, prepare jars and lids: place 8 half-pint jars on rack in large pot. Add enough water to cover jars, and bring to boil over high heat. Boil for 10 minutes, then turn off heat and allow jars to rest in the hot water. Meanwhile, put bands and lids in small saucepan and cover with water. Heat over medium heat until the water is simmering, then remove pan from heat and allow bands and lids to rest in hot water until ready to use.", "Put the lavender buds in a small bowl and pour 1/2 cup of boiling water over them. Let them steep for 10 minutes, then strain the \"tea\" into a bowl and discard the buds.", "Working in batches if necessary, pulse blueberries in blender until coarsely crushed. You should have about 6 cups.", "Measure 4 1/4 cups of sugar in one bowl. In another bowl, combine remaining 1/4 cup sugar and pectin.", "Combine blueberries, lemon zest and juice, and lavender tea in large, heavy saucepan or stockpot. Stir in sugar-pectin mixture and the butter. Bring to a boil over high heat, stirring constantly.", "Add remaining sugar all at once. Return to a full rolling boil. Boil for 1 minute. Remove jam from heat and skim off any foam from surface.", "Ladle hot jam into hot sterilized jars, leaving 1/4-inch headspace. Wipe rims of the jars, cover with lids, and screw bands on until just barely tight. Place jars on rack in pot and cover completely with water. Cover pot and bring to a boil over high heat. Boil for 10 minutes. Turn off heat, uncover pot, and allow jars to rest in water for 5 minutes. Remove jars from pot and allow them to rest undisturbed on countertop for six hours or overnight."]', 4.50, 2, 'Serious Eats', 'https://www.seriouseats.com/blueberry-lavender-jam-recipe', 'https://www.seriouseats.com/thmb/jQdCAdr_8-iKyRkmdaY-NFbilsg=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__recipes__images__2011__08__20110819preservedblueberrylavenderp-4ebce55320674f4cac99ee92e1c8eb2b.jpg', '64 servings', 'Prep5 mins', '', 'Total60 mins', '2025-09-27 08:35:01.722368', '2025-09-27 08:35:01.722368');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (801, 'No-Cook, Sugar-Free Blueberry and Chia Seed Jam', '["375g blueberries", "4 tsp stevia (a natural sweetener available in the sugar aisle of big supermarkets)", "Juice and zest of 1 lemon", "30g chia seeds (available in health food shops)"]', '["Blend the blueberries to a smooth purée and add the lemon juice and zest.", "Place the chia seeds in a large bowl and mix in the stevia and blueberry purée", "Cover and leave in the fridge for 2-3 hours. This will reconstitute the chia seeds so they''re nice and plump.", "Place the chia seed mixture in a blender and pulse until slightly smooth. It''s okay if it''s not a totally smooth paste.", "Sterilise a 0.5L clip top Kilner jar using these handy instructions for sterilising jars properly. This will make your jam last longer.", "Store the jam in the fridge and use within 4 days.", "Recipe courtesy of Sanjana Modha on behalf of Kilner."]', 4.30, 21, 'Food Network', 'https://foodnetwork.co.uk/recipes/no-cook-sugar-free-blueberry-chia-seed-jam', 'https://d2vsf1hynzxim7.cloudfront.net/production/media/17588/conversions/foodnetwork-image-5604ff09-10ba-4ad1-b570-ab363743d7af-default.webp', '0', '', '', '', '2025-09-27 08:35:01.723959', '2025-09-27 08:35:01.723959');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (802, 'Blueberry jam', '[{"name": "800gblueberrieswashed", "unit": "", "amount": ""}, {"name": "500ggranulated sugar", "unit": "", "amount": ""}, {"name": "1vanilla podsplit lengthways", "unit": "", "amount": ""}, {"name": "1large lemonjuiced", "unit": "", "amount": ""}]', '["Put a plate in the freezer for you to test your jam is set later. Sterilise two 500ml jam jars so they are ready to use.", "Put the blueberries, sugar, vanilla and lemon in a large heavy-based saucepan over a medium heat. Crush the blueberries a little with a potato masher. Bring to the boil, then reduce to a simmer and cook for 30 mins. Skim off any scum that has formed on the top with a spoon and discard.", "Test the jam is set by spooning a mound onto your cooled plate. Put into the fridge for 3 mins. If the jam forms a skin and ripples when you push it with your finger, it''s ready. If not, return to the heat for 5 more mins then repeat the test. Do this until you reach setting point.", "Leave the jam to sit for 10 mins off the heat. Ladle into your warm, sterilised jars and seal the lids on top. Leave to cool completely. Will keep for six months unopened in a cool, dry place, or two weeks once opened in the fridge."]', 4.00, 10, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/blueberry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/blueberry_jam-72d1b29.jpg?resize=440,400', '2', 'PT15M', 'PT30M', 'PT45M', '2025-09-27 08:35:01.724703', '2025-09-27 08:35:01.724703');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (890, 'Strawberry Jam with JELL-O', '[{"item": "2 cups strawberries", "name": "strawberries", "unit": "cups", "quantity": "2"}, {"item": "1 cup cold water", "name": "cold water", "unit": "cup", "quantity": "1"}, {"item": "1 (3 ounce) package strawberry flavored Jell-O®, sugar free", "name": "strawberry flavored Jell-O®, sugar free", "unit": "(3 ounce) package", "quantity": "1"}]', '["Crush strawberries in a large saucepan. Add water and gelatin, mixing well.", "Over medium heat bring mixture to boiling, stirring constantly. Reduce heat to low and simmer for 2 minutes.", "Pour into jars, allow to cool, and then cover. Can be stored in the refrigerator for 1 week, or frozen for several weeks."]', 4.10, 43, 'AllRecipes', 'https://www.allrecipes.com/recipe/20888/strawberry-jam-with-jell-o/', 'https://www.allrecipes.com/thmb/-mioAngM7X11OpagvSFiA2Gt2XU=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5185624-13957cc773b345c6bb4eec3169d91993.jpg', '24 servings', '', '', '', '2025-09-27 08:54:17.20929', '2025-09-27 08:54:17.20929');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (803, 'Summer Blueberry Jam', '[{"item": "3pounds,8ouncesblueberries, preferably small, mostly just-ripe berries with some under-ripe berries as well", "name": "3pounds,8ouncesblueberries, preferably small, mostly just-ripe berries with some under-ripe berries as well", "unit": "", "quantity": ""}, {"item": "1pound,12ouncessugar", "name": "1pound,12ouncessugar", "unit": "", "quantity": ""}, {"item": "1/4cupfresh juicefrom2wholelemons", "name": "1/4cupfresh juicefrom2wholelemons", "unit": "", "quantity": ""}]', '["Mix everything together in a large mixing bowl and scrape into a large pot or jam-making vessel.", "Cook berries on medium-high heat, stirring very frequently, until blueberries have released enough liquid to dissolve the sugar, about 8 minutes. Increase heat to high and cook, stirring just enough to prevent scorching on bottom of pot, until berries are foamy, about 10 minutes. Continue to cook, stirring more frequently and lowering temperature as foam begins to subside, 10 to 20 minutes longer. Cook until most of the fruit is disintegrated and bubbles have slowed down and become glossy, about 10 more minutes (times can vary depending on fruit).", "Test for doneness: Turn off heat and set a small spoonful of jam in the freezer for 5 minutes. Jam is done once it holds together and doesn''t run off the spoon when tilted; if jam is too runny, return to low heat and continue to cook, stirring frequently and repeating doneness test every 5 minutes, until jam passes test. Eat right away, or can following your canning equipment instructions (read more on canninghere). Canned jams can be stored in a cool, dark place for up to 1 year."]', 4.00, 2, 'Serious Eats', 'https://www.seriouseats.com/summer-blueberry-jam-recipe', 'https://www.seriouseats.com/thmb/XX2mhgvatjD28i1vwQ4LX6kedKc=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__recipes__images__2014__07__20140608-blueberry-jam-finished-jennifer-latham-094b0f6c864b41689d61dc3ea8d52992.jpg', '96 servings', 'Prep5 mins', '', 'Total60 mins', '2025-09-27 08:35:01.72569', '2025-09-27 08:35:01.72569');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (867, 'Plum & raspberry jam', '[{"name": "1kgjam sugar", "unit": "", "amount": ""}, {"name": "500gplumstoned and roughly chopped", "unit": "", "amount": ""}, {"name": "juice 1  lemon", "unit": "", "amount": ""}, {"name": "500graspberry", "unit": "", "amount": ""}]', '["Tip the sugar into a heavy preserving pan or flameproof dish with the plums, lemon juice and half the raspberries. Heat until the sugar has dissolved, then bring to the boil and simmer for a few mins until the plums are tender. Add the rest of the raspberries to the pan.", "Place a jam thermometer in the pan, bring to the boil, then cook over a high heat until the temperature reaches 104C. Alternatively, put a small plate in the fridge or freezer until really cold. Once the jam has been boiling for 10 mins and looks thick and syrupy, turn off the heat and pour a spoonful onto the plate. The jam is ready when it wrinkles as you push it with your finger; be careful not to burn yourself. If the jam is not ready, boil for another 3 mins, then repeat test as above, until it is ready.", "Allow to cool for about 5 mins. Skim the surface of any scum and pack into warm sterilised jars (see tip below). Cover with wax discs and lids. Will keep in the fridge or a cold larder for up to 6 months."]', 5.00, 8, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/plum-raspberry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1066516_11-2d8d721.jpg?resize=440,400', '1', 'PT10M', 'PT35M', 'PT45M', '2025-09-27 08:39:02.692586', '2025-09-27 08:39:02.692586');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (868, 'Clove-Spiced Plum Jam', '[{"item": "5 whole cloves", "name": "cloves", "unit": "whole", "quantity": "5"}, {"item": "8 cups pitted and quartered plums", "name": "pitted and quartered plums", "unit": "cups", "quantity": "8"}, {"item": "8 tablespoons lemon juice", "name": "lemon juice", "unit": "tablespoons", "quantity": "8"}, {"item": "8 tablespoons water", "name": "water", "unit": "tablespoons", "quantity": "8"}, {"item": "1 (1.75 ounce) package powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "8 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "8"}]', '["Wrap cloves in cheesecloth or a spice sack; set aside.", "Inspect 9 glass jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Combine plums, lemon juice, water, and pectin in a large, deep saucepan over low heat. Bring to a boil.", "Add sugar and cloves. Cook, stirring continuously, until sugar dissolves and mixture reaches 220 degrees F (104 degrees C), 3 to 5 minutes. Remove and discard cloves.", "Pour jam into sterilized jars. Wipe any drips off lip of jars, and add lids. Place on a damp towel to cool and self-seal; you will hear a popping sound as jam cools, about 2 hours."]', 5.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/283446/clove-spiced-plum-jam/', 'https://www.allrecipes.com/thmb/XLjNYALb_CTD5uzUZg_M2n4qlHE=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/8474763-da6989adba71472f917e9cb92bebd2cf.jpg', '72 servings', '', '', '', '2025-09-27 08:39:02.695693', '2025-09-27 08:39:02.695693');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (806, 'Gooseberry jam', '[{"name": "1kggooseberriestopped and tailed", "unit": "", "amount": ""}, {"name": "juice of ½lemon", "unit": "", "amount": ""}, {"name": "1kggranulated sugar", "unit": "", "amount": ""}]', '["First sterilise your jars by washing thoroughly in very hot soapy water. Rinse in very hot water then put on a baking sheet in a 140C/fan 120C/gas 1 oven until completely dry.", "Put the gooseberries, lemon juice and 400ml water in a large wide pan (use a preserving pan if you have one). Bring to the boil then simmer for 15 minutes until the fruit is very soft and pulpy. Put 2 or 3 small saucers in the freezer (these will be used to test the setting later on).", "Add the sugar and stir over a gentle heat for another 10 minutes until the sugar is completely dissolved. You don’t want it to boil at this point as the sugar could crystallize. Once you can’t feel or see any grains of sugar bring to the boil and boil hard for 10 minutes, skimming the surface as you go and stirring now and again to stop it catching. The jam will start to turn a pinky red hue as it cooks.", "Spoon a little jam onto a chilled saucer, leave to cool then run your finger through it. If it’s ready it will wrinkle up. If this doesn’t happen boil for another 5 minutes then keep testing and boiling until it does.", "Do a final skim on the finished jam then pour into the sterilised jars and seal. Store in a cool dark place – the jam will be good for up to 6 months. Keep in the fridge once opened."]', 5.00, 54, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/gooseberry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1067494_11-a2824d2.jpg?resize=440,400', '3', 'PT10M', 'PT40M', 'PT50M', '2025-09-27 08:35:15.724785', '2025-09-27 08:35:15.724785');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (807, 'Easy cherry jam', '[{"name": "2kgcherriespitted, ½ roughly chopped, ½ halved", "unit": "", "amount": ""}, {"name": "1.2kgjamor preserving sugar (with added pectin)", "unit": "", "amount": ""}, {"name": "2lemonsjuiced", "unit": "", "amount": ""}]', '["Put two saucers in the freezer for testing the jam later on. Tip the cherries, sugar and lemon juice into a large, heavy-based saucepan and simmer uncovered over a medium-high heat for 35-50 mins, stirring frequently until thick and glossy.", "After 35 mins, spoon a little of the jam onto one of the chilled saucers. Leave for 1 min, press a fingertip into the jam. If it starts the wrinkle, it’s ready. If it slides away, continue to boil the jam for 10 mins, then test again on the other saucer.", "Spoon the jam into sterilised jars and seal. Will keep for six months unopened (when stored in a dark, cool place), or opened in the fridge for three months."]', 5.00, 8, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/easy-cherry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/easy-cherry-jam-9b01602.jpg?resize=440,400', '4', 'PT25M', 'PT50M', 'PT1H15M', '2025-09-27 08:35:15.727265', '2025-09-27 08:35:15.727265');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (808, 'Chilli & tomato jam', '[{"name": "2 tbspolive oil", "unit": "", "amount": ""}, {"name": "2banana shallotspeeled and finely chopped", "unit": "", "amount": ""}, {"name": "2garlic clovesfinely chopped", "unit": "", "amount": ""}, {"name": "1 tspfennel seeds", "unit": "", "amount": ""}, {"name": "2-3 chillies of your choicefinely chopped (deseeded, if you prefer less heat)", "unit": "", "amount": ""}, {"name": "800gcherry tomatoeshalved", "unit": "", "amount": ""}, {"name": "275glight brown soft sugar", "unit": "", "amount": ""}, {"name": "250mlwhite wine vinegar", "unit": "", "amount": ""}]', '["Heat the oil in a large saucepan over a medium heat and cook the shallots for 5-6 mins until softened and lightly golden. Add the garlic, fennel seeds and chillies. Cook for 2-3 mins until fragrant.", "Add the tomatoes, sugar and vinegar, stir well, then bring to the boil. Reduce the heat to a simmer and cook for 45 mins-1 hr, or until the mixture has thickened and become sticky, and a spatula leaves a clear path when drawn through it. Transfer to a sterilised 500ml jar (find out how to sterilise jars) and leave to cool completely. Will keep unopened for up to a year in a cool, dry place."]', 5.00, 6, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/chilli-tomato-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2023/05/Chilli-and-tomato-jam-026febc.jpg?resize=768,713', '1', 'PT15M', 'PT1H15M', 'PT1H30M', '2025-09-27 08:35:15.728741', '2025-09-27 08:35:15.728741');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (809, 'Melted Camembert with Cherry Jam', '["1 whole camembert in wooden container", "1 punnet of cherries", "3 tbsp brown sugar", "4 tbsp red wine vinegar", "4 tbsp red wine", "20g butter"]', '["Wash and de-stone the cherries. Place in a frying pan over a medium heat with the butter until softened, approximately three minutes. Pour over the red wine vinegar, red wine and sugar. Stir well, turn down the heat and leave to simmer for 10 minutes.", "Remove the cheese from the plastic wrapping and place in half the wooden container it came in on the baking sheet.  Bake at 200°C for 10 minutes until almost oozing. When the cheese is ready, remove from the oven, cut a cross into the centre of the cheese and pull back the rind.  Dollop a large spoonful of the cherry jam in the centre of the cheese and serve with warm crusty bread.", "Alternatives: Try using blueberries when cherries are not in season."]', 4.80, 21, 'Food Network', 'https://foodnetwork.co.uk/recipes/melted-camembert-with-cherry-jam', 'https://d2vsf1hynzxim7.cloudfront.net/production/media/17145/conversions/foodnetwork-image-e33f6916-f91b-4eee-8532-3653130467f9-default.webp', '2', 'PT10M', 'PT20M', '', '2025-09-27 08:35:15.730236', '2025-09-27 08:35:15.730236');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (810, 'Mandarin Blue Cherry Jam', '[{"item": "3 pounds fresh blueberries, crushed", "name": "fresh blueberries, crushed", "unit": "pounds", "quantity": "3"}, {"item": "1 ½ pounds pitted fresh sweet cherries, crushed", "name": "pitted fresh sweet cherries, crushed", "unit": "pounds", "quantity": "1 ½"}, {"item": "1 (11 ounce) can Mandarin orange segments, drained and crushed", "name": "Mandarin orange segments, drained and crushed", "unit": "(11 ounce) can", "quantity": "1"}, {"item": "¼ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "¼"}, {"item": "1 (1.75 ounce) package powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "5 ½ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "5 ½"}]', '["Combine the blueberries, cherries, oranges, lemon juice, and pectin in a large pot; bring to a rolling boil. Stir in the sugar until it dissolves completely and the mixture returns to a rolling boil; boil 1 minute and remove from heat. Pour into hot, sterilized jars and top with sterilized lids. Secure lids with bands, process for 5 minutes in a boiling water bath. Allow jars to cool slowly, creating a vacuum seal. Allow to sit at room temperature 1 to 2 days to allow jam to set. Refrigerate after opening."]', 4.00, 2, 'AllRecipes', 'https://www.allrecipes.com/recipe/110907/mandarin-blue-cherry-jam/', 'https://www.allrecipes.com/thmb/kCGLCvavRYEjnXSdKZEf9x0L7pw=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/3452940-eb157851be7c4393b6764bac319ae716.jpg', '56 servings', '', '', '', '2025-09-27 08:35:15.731617', '2025-09-27 08:35:15.731617');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (811, 'Sour Cherry Amaretto Jam', '[{"item": "3 pounds fresh sour cherries", "name": "fresh sour cherries", "unit": "pounds", "quantity": "3"}, {"item": "1 (1.75 ounce) package fruit pectin (such as Sure-Jell® Pectin Light for less or no sugar)", "name": "fruit pectin (such as Sure-Jell® Pectin Light for less or no sugar)", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "3 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "3"}, {"item": "⅓ cup amaretto liqueur", "name": "amaretto liqueur", "unit": "cup", "quantity": "⅓"}]', '["Inspect 5 cup-sized jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Wash and pit the cherries. Chop them finely. You should have about 5 cups. Place cherries in a large saucepan and slowly bring to a boil over low-medium heat. Stir in pectin and slowly bring mixture to a full rolling boil. Add sugar and stir to dissolve. Return to a full rolling boil on high heat and boil for exactly 1 minute, stirring constantly. Skim off any foam with a ladle or a large spoon.", "Remove from heat and stir in amaretto liqueur.", "Ladle sour cherry jam into the prepared jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any spills. Top with lids and tightly screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart. Let rest for 24 hours without moving the jars. Gently press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 4.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/265005/sour-cherry-amaretto-jam/', 'https://www.allrecipes.com/thmb/JgGs5AMufKGTKWlIpbbdGF80GGQ=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5416275-801dd3b58e0c44e092613f28e863930c.jpg', '40 servings', '', '', '', '2025-09-27 08:35:15.732948', '2025-09-27 08:35:15.732948');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (812, 'Sweet Cherry Jam with Spices and Red Wine', '[{"item": "2 ¾ pounds sweet cherries", "name": "sweet cherries", "unit": "pounds", "quantity": "2 ¾"}, {"item": "5 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "5"}, {"item": "¼ cup fresh lemon juice", "name": "fresh lemon juice", "unit": "cup", "quantity": "¼"}, {"item": "1  cinnamon stick, broken in half", "name": "cinnamon stick, broken in half", "unit": "", "quantity": "1"}, {"item": "4  whole cloves", "name": "whole cloves", "unit": "", "quantity": "4"}, {"item": "1 (1.75 ounce) package fruit pectin (such as Sure-Jell Premium Fruit Pectin)", "name": "fruit pectin (such as Sure-Jell Premium Fruit Pectin)", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "½ cup dry red wine", "name": "dry red wine", "unit": "cup", "quantity": "½"}]', '["Inspect 6 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Pit and finely chop cherries; measure out 3 1/2 cups. Combine cherries, sugar, and lemon juice in a bowl. Place cinnamon and cloves in a small piece of cheesecloth and tie together to make a spice bag. Add to the pot with the cherries, and marinate in the refrigerator for 8 hours.", "Add the cherries with all the liquid, spice bag, and the pectin in a large heavy saucepan. Slowly bring it to a boil. Stir in red wine. Slowly return mixture to a full rolling boil over high heat. Boil for exactly 1 minute, stirring constantly. Skim off any foam with a ladle or a large spoon.", "Remove pot from the heat. Remove and discard the spice bag. Ladle cherry jam immediately into prepared jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any spills. Top with lids and tightly screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart. Let rest for 24 hours without moving the jars. Gently press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area.", "You can swap the cinnamon and cloves for other spices; for example, 1 star anise and 6 crushed cardamom pods."]', 3.50, 2, 'AllRecipes', 'https://www.allrecipes.com/recipe/265993/sweet-cherry-jam-with-spices-and-red-wine/', 'https://www.allrecipes.com/thmb/itfj1SY9DE5OQsCFw-RjqOrcT9Y=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5506059-9df18a65d6424cfc84285b64b0cadb6a.jpg', '48 servings', '', '', '', '2025-09-27 08:35:15.73382', '2025-09-27 08:35:15.73382');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (813, 'Cherry Bacon Jam with Thyme and Cloves', '[{"item": "1 pound Smithfield® Thick Cut Bacon", "name": "Smithfield® Thick Cut Bacon", "unit": "pound", "quantity": "1"}, {"item": "1 large onion, finely diced", "name": "onion, finely diced", "unit": "large", "quantity": "1"}, {"item": "1 (10 ounce) jar cherry preserves", "name": "cherry preserves", "unit": "(10 ounce) jar", "quantity": "1"}, {"item": "¼ cup light brown sugar", "name": "light brown sugar", "unit": "cup", "quantity": "¼"}, {"item": "½ cup red wine vinegar", "name": "red wine vinegar", "unit": "cup", "quantity": "½"}, {"item": "1 pinch ground cloves", "name": "ground cloves", "unit": "pinch", "quantity": "1"}, {"item": "¼ teaspoon salt", "name": "salt", "unit": "teaspoon", "quantity": "¼"}, {"item": "½ teaspoon freshly ground black pepper", "name": "freshly ground black pepper", "unit": "teaspoon", "quantity": "½"}, {"item": "1 ½ teaspoons fresh thyme leaves", "name": "fresh thyme leaves", "unit": "teaspoons", "quantity": "1 ½"}]', '["In large heavy-bottomed skillet over medium-low heat, cook bacon until just crisp, turning as needed. Remove bacon from pan and drain on paper towels until cooled. Drain all but 2 tablespoons of bacon fat from pan. Increase heat to medium and cook onions for 8-10 minutes or until soft and translucent.", "Add preserves and sugar to pan and cook over medium heat until hot and sugar has dissolved, stirring occasionally. Add in vinegar, cloves, salt and pepper and cook for 8-10 minutes or until thickened and syrupy, stirring occasionally.", "Meanwhile finely chop bacon while jam is reducing. Add bacon and thyme to pan and stir to combine, cooking until desired consistency. Let jam cool and store in airtight jars for up to two weeks.", "For a smoother, less chunky jam, pulse cooled bacon jam in a food processor until desired smoothness."]', 3.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/256188/cherry-bacon-jam-with-thyme-and-cloves/', 'https://www.allrecipes.com/thmb/P3HjcUMyFZEO-pbMWqHjrmvC880=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/4188526-443ef90047f948a0815b050d21e1abb9.jpg', '20 servings', '', '', '', '2025-09-27 08:35:15.734851', '2025-09-27 08:35:15.734851');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (869, 'Plum Jam', '[{"item": "4 ½ cups pitted, chopped plums", "name": "pitted, chopped plums", "unit": "cups", "quantity": "4 ½"}, {"item": "½ cup water", "name": "water", "unit": "cup", "quantity": "½"}, {"item": "7 ½ cups white sugar, or to taste", "name": "white sugar, or to taste", "unit": "cups", "quantity": "7 ½"}, {"item": "½ teaspoon butter (Optional)", "name": "butter (Optional)", "unit": "teaspoon", "quantity": "½"}, {"item": "1 (1.75 ounce) package powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) package", "quantity": "1"}]', '["Gather the ingredients.", "Inspect 6 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Combine plums and water in a large pot over medium-high heat; bring to a boil. Reduce the heat to medium-low, cover, and simmer for 5 minutes. Stir in sugar, then add butter to reduce foaming if needed.", "Increase the heat to high and stir constantly until mixture comes to a full, rolling boil. Quickly stir in pectin and return to a full boil. Boil, stirring constantly, for 1 minute. Remove from the heat; skim off and discard any foam.", "Pack jam into hot, sterilized jars, filling to within 1/8 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and let rest on a cloth-covered or wood surface, several inches apart, for 12 to 24 hours. Press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 4.70, 102, 'AllRecipes', 'https://www.allrecipes.com/recipe/215131/plum-jam/', 'https://www.allrecipes.com/thmb/cWt5sk9Twhgz_BPjPdXkCLK9Gt8=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/215131-Plum-Jam-ddmds-2x1-5eda61f74820488d80c4b848f8078d82.jpg', '96 servings', '', '', '', '2025-09-27 08:39:02.696782', '2025-09-27 08:39:02.696782');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (870, 'Damson Plum Cardamom Jam', '[{"item": "5 pounds fresh Damson or Damask plums", "name": "fresh Damson or Damask plums", "unit": "pounds", "quantity": "5"}, {"item": "1 cup water", "name": "water", "unit": "cup", "quantity": "1"}, {"item": "12  whole cardamom pods", "name": "whole cardamom pods", "unit": "", "quantity": "12"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}, {"item": "¼ teaspoon butter", "name": "butter", "unit": "teaspoon", "quantity": "¼"}]', '["Rinse and de-stem plums in a sink full of cool water; place them into a thick-bottomed pot suitable for slow cooking and deep enough to allow frothing when plums begin to boil. Add water and cardamom pods; bring to a low boil over medium heat. Turn heat to low for a slow simmer and allow plums to cook down uncovered for 1 hour and 30 minutes. Remove from heat and allow to cool.", "Strain cooled plums in a colander set over a large bowl. Press juice out with your hands, collecting it in the bowl. Pick up small handfuls of the pit-and-fruit slurry in the colander and squeeze pulp and skins gently into the bowl with the syrup, retaining the pits in the palm of your hand and then discarding them.", "Place plums back into the pot. Add sugar and butter; mix well. Cook at a very low simmer until jam thickens, about 4 hours. Test for adequate development of pectin by dropping a spoonful of jam onto a plate and putting it in the refrigerator for a few minutes: jam should be soft-set and no longer syrupy.", "Ladle jam into hot, sterilized jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with the lids and screw the rings on tightly. Allow the jars to cool to room temperature and check to be sure that each jar has sealed."]', 4.70, 26, 'AllRecipes', 'https://www.allrecipes.com/recipe/210194/damson-plum-cardamom-jam/', 'https://www.allrecipes.com/thmb/JajasIE7uEKa744L9N_o5OhpsVY=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/482400-4ee316877dd843b29b1fe3255f533573.jpg', '100 servings', '', '', '', '2025-09-27 08:39:02.698163', '2025-09-27 08:39:02.698163');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (814, 'Cranberry & sweet chilli jam', '[{"name": "500gfresh or frozen cranberries", "unit": "", "amount": ""}, {"name": "finger-sized piecegingerpeeled and roughly chopped", "unit": "", "amount": ""}, {"name": "3-4red chilliesroughly chopped (deseeded if you don’t like it too hot)", "unit": "", "amount": ""}, {"name": "200ggolden caster sugar", "unit": "", "amount": ""}, {"name": "100mlred wine vinegar", "unit": "", "amount": ""}]', '["Tip half the cranberries, the ginger and chillies into a food processor and whizz until very finely chopped. Scrape into a heavy-bottomed pan and add the remaining cranberries along with the sugar and vinegar, then bring everything to the boil, skimming off any scum that comes to the surface. Turn the heat down to a gentle simmer and keep cooking for about 20-30 mins, stirring occasionally.", "Once the jam is starting to become sticky, continue cooking for a further 5 mins, stirring frequently so that the mixture doesn’t burn on the bottom of the pan. It should look like a thick, bubbling lava. Cool slightly, then transfer to sterilised jars and leave to cool completely. Will keep for three months in a cool, dark place; keep in the fridge once opened."]', 5.00, 6, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/cranberry-sweet-chilli-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/cranberry-sweet-chilli-jam-47dae5f.jpg?resize=440,400', '2', 'PT20M', 'PT1H', 'PT1H20M', '2025-09-27 08:35:25.836205', '2025-09-27 08:35:25.836205');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (815, 'Sparkling Holiday Jam', '[{"item": "2 ½ quarts strawberries, coarsely chopped", "name": "strawberries, coarsely chopped", "unit": "quarts", "quantity": "2 ½"}, {"item": "1 (12 ounce) package fresh or frozen cranberries, coarsely chopped", "name": "fresh or frozen cranberries, coarsely chopped", "unit": "(12 ounce) package", "quantity": "1"}, {"item": "2 (2 ounce) packages powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(2 ounce) packages", "quantity": "2"}, {"item": "1 teaspoon margarine", "name": "margarine", "unit": "teaspoon", "quantity": "1"}, {"item": "5 pounds white sugar", "name": "white sugar", "unit": "pounds", "quantity": "5"}]', '["Sterilize jars and lids in boiling water for at least 10 minutes. Let simmer while making jam.", "Combine strawberries, cranberries, pectin, and margarine in a large saucepan. Bring to a boil. Stir in sugar and return to a boil. Cook for 1 minute; remove from heat.", "Quickly fill jars to within 1/2 inch from the top. Wipe rims clean and put on lids. Process in a boiling water bath for 5 minutes."]', 4.80, 107, 'AllRecipes', 'https://www.allrecipes.com/recipe/50823/sparkling-holiday-jam/', 'https://www.allrecipes.com/thmb/GyojJrVBMS3_lXQ-yI3mJMLKSnU=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/7463758-51253b1c026b4a65b470bc56b72272a8.jpg', '96 servings', '', '', '', '2025-09-27 08:35:25.838996', '2025-09-27 08:35:25.838996');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (816, 'Spiced Cranberry Jam', '[{"item": "1 (12 ounce) bag fresh cranberries", "name": "fresh cranberries", "unit": "(12 ounce) bag", "quantity": "1"}, {"item": "¾ cup white sugar", "name": "white sugar", "unit": "cup", "quantity": "¾"}, {"item": "½ cup turbinado sugar", "name": "turbinado sugar", "unit": "cup", "quantity": "½"}, {"item": "¼ cup water", "name": "water", "unit": "cup", "quantity": "¼"}, {"item": "1  orange, zested", "name": "orange, zested", "unit": "", "quantity": "1"}, {"item": "1 teaspoon ground cinnamon", "name": "ground cinnamon", "unit": "teaspoon", "quantity": "1"}, {"item": "1 teaspoon ground ginger", "name": "ground ginger", "unit": "teaspoon", "quantity": "1"}, {"item": "¼ teaspoon ground cloves", "name": "ground cloves", "unit": "teaspoon", "quantity": "¼"}, {"item": "¼ teaspoon ground allspice", "name": "ground allspice", "unit": "teaspoon", "quantity": "¼"}, {"item": "1 dash ground black pepper", "name": "ground black pepper", "unit": "dash", "quantity": "1"}]', '["Combine cranberries, white sugar, turbinado sugar, water, orange zest, cinnamon, ginger, cloves, allspice, and pepper in a large pot over medium heat. Simmer, stirring frequently, until sugar dissolves and cranberries pop, 10 to 12 minutes.", "Blend cranberry jam into desired consistency using an immersion blender.", "Pour jam into a glass pint jar; cool until thickened and set, about 1 hour. Seal jar and refrigerate."]', 4.80, 11, 'AllRecipes', 'https://www.allrecipes.com/recipe/256448/spiced-cranberry-jam/', 'https://www.allrecipes.com/thmb/gbahfNjjt9iKit_wyMGirdfVwOU=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/4298135-95a534b9f17548f4a38a47a423a03802.jpg', '16 servings', '', '', '', '2025-09-27 08:35:25.839755', '2025-09-27 08:35:25.839755');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (818, 'Chorizo jam', '[{"name": "500gcooking chorizodiced", "unit": "", "amount": ""}, {"name": "1large white onionfinely sliced", "unit": "", "amount": ""}, {"name": "2garlic clovescrushed", "unit": "", "amount": ""}, {"name": "85glight muscovado sugar", "unit": "", "amount": ""}, {"name": "3 tbspSherry or red wine vinegar", "unit": "", "amount": ""}, {"name": "2 tbspmaple syrup", "unit": "", "amount": ""}, {"name": "1shot of espressocoffee or 100ml/4fl oz strong black coffee", "unit": "", "amount": ""}]', '["Fry the chorizo in a large saucepan for about 5 mins until golden and crispy at the edges. Remove the chorizo with a slotted spoon and transfer to a plate, leaving the oil that has been released in the pan.", "Turn the heat down low and gently cook the onion in the chorizo oil for 15-20 mins or until very soft and starting to brown. Stir in the garlic, muscovado sugar, Sherry, maple syrup, espresso and chorizo.", "Cook slowly for 35-40 mins, stirring occasionally, until thick and syrupy. Turn off the heat, leave to cool a little, then tip everything into a food processor. Pulse briefly to chop everything up into really small pieces, then leave the mixture to cool before packing it into 2 sterilised jam jars. Will keep for up to 1 month in the fridge."]', 5.00, 16, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/chorizo-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chorizo-jam-932385a.jpg?resize=575,522', '2', 'PT30M', 'PT1H', 'PT1H30M', '2025-09-27 08:35:34.754529', '2025-09-27 08:35:34.754529');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (819, 'Bacon jam', '[{"name": "500gsmoked baconlardons", "unit": "", "amount": ""}, {"name": "1large white onionfinely sliced", "unit": "", "amount": ""}, {"name": "2garlic clovescrushed", "unit": "", "amount": ""}, {"name": "85glight brown sugar", "unit": "", "amount": ""}, {"name": "2 tbspcider vinegar", "unit": "", "amount": ""}, {"name": "1 tbspbourbon whiskey", "unit": "", "amount": ""}, {"name": "2 tbspmaple syrup", "unit": "", "amount": ""}, {"name": "100mlfreshly brewed strong black coffee", "unit": "", "amount": ""}]', '["Fry the bacon in a large non-stick pan for 5-10 mins mins until golden and crisp. Remove with a slotted spoon and transfer to a plate, leaving the fat in the pan.", "Turn the heat to low and cook the onion in the bacon fat for 15-20 mins until soft and starting to brown. Stir in the garlic, sugar, cider vinegar, bourbon, maple syrup, coffee and the bacon.", "Cook slowly for 25-30 mins, stirring occasionally until thick and syrupy. Turn off the heat, leave to cool for a few mins, then carefully tip everything into a food processor. Pulse briefly to chop into small pieces, then leave the mixture to cool before packing it into two sterilised jars. Will keep for up to one month in the fridge."]', 5.00, 13, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/bacon-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/bacon-jam-81730c1.jpg?resize=440,400', '2', 'PT5M', 'PT1H', 'PT1H5M', '2025-09-27 08:35:34.757586', '2025-09-27 08:35:34.757586');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (820, 'Redcurrant jam', '[{"name": "800gredcurrantsfresh or frozen (defrosted if frozen)", "unit": "", "amount": ""}, {"name": "Around 400g granulated orwhite caster sugar", "unit": "", "amount": ""}]', '["Put two small saucers in the freezer – you will use these to test the setting point of the jam.", "Remove the stalks from the fruit, and place the fruit in a large saucepan. Pour in 150ml water, bring to a simmer and cook gently for about 10 mins until the fruit has softened and is starting to break down. Place a sieve over a mixing bowl and push as much of the fruit and pulp through the sieve with the back or a spoon as possible, discarding the seeds and any stalks. You don''t need to sieve the fruit if you prefer a seeded jam.", "Weigh the liquid and add three quarters of the sugar to the total liquid (for example, if your liquid is 400g, add 300g of sugar). Place back in the pan over a low medium heat and stir to dissolve the sugar.", "Once the sugar has fully dissolved, turn the heat up and bring to the boil. Stir occasionally. If using a jam or digital thermometer, wait until the liquid reaches 105C. If not, after about 8 mins, remove a saucer from the freezer and spread a teaspoon of jam over it. Let it sit for 1 min before pushing your finger through the liquid. If it starts to wrinkle, the jam is ready. If not, return to the boil and check again after another minute. Continue to do this until the jam reaches setting point.", "Allow to cool slightly, then pour into sterilised jars and allow to cool completely. You can sterilise the jars by washing them in hot soapy liquid, rinsing and placing in an oven at 180C/160C fan/gas 4 for 5 mins. Remove carefully and allow to cool. Store in a cool dark place."]', 5.00, 7, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/redcurrant-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/09/Redcurrant-jam-1f616e9.jpg?resize=768,713', '2', 'PT10M', 'PT30M', 'PT40M', '2025-09-27 08:35:34.758431', '2025-09-27 08:35:34.758431');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (821, 'Fig jam', '[{"name": "1kgripe figsquartered", "unit": "", "amount": ""}, {"name": "1large orangezested", "unit": "", "amount": ""}, {"name": "400ggranulated sugar", "unit": "", "amount": ""}, {"name": "1lemonjuiced, plus strips of zest, to serve (optional)", "unit": "", "amount": ""}]', '["Put the figs and 150ml water in a large heavy based saucepan. Bring to a simmer and gently bubble for 5 mins or until the figs have softened and released their juices. Add the orange zest, sugar and lemon juice. Bring to a boil and cook, stirring regularly for around 5-7 mins or until thick, skimming off any skum that rises to the surface. Remove from the heat and leave to sit for 10 mins.", "Ladle the mixture into sterilised jars. Seal with the lids and leave to cool completely. Will keep for six months unopened and 2 weeks in the fridge once opened."]', 4.00, 23, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/fig-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/fig_jam-7a327cf.jpg?resize=440,400', '2', 'PT20M', 'PT20M', 'PT40M', '2025-09-27 08:35:34.759648', '2025-09-27 08:35:34.759648');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (872, 'Quince Jam', '[{"item": "2 ½ pounds ripe quinces", "name": "ripe quinces", "unit": "pounds", "quantity": "2 ½"}, {"item": "1 medium lemon", "name": "lemon", "unit": "medium", "quantity": "1"}, {"item": "1 cup water", "name": "water", "unit": "cup", "quantity": "1"}, {"item": "2 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "2"}, {"item": "1 (1 inch) piece cinnamon stick", "name": "cinnamon stick", "unit": "(1 inch) piece", "quantity": "1"}]', '["Gather all ingredients.", "Wash quinces under running water with a soft sponge or fruit brush to remove the fuzzy outer layer.", "Zest lemon; set zest aside. Squeeze lemon juice into a saucepan.", "Add water to lemon juice in the saucepan and bring to a simmer. Add quinces and bring to a boil. Stir in sugar and cinnamon stick. Reduce the heat and simmer until quinces are soft, about 45 minutes.", "Remove from the heat. Discard cinnamon stick and stir in lemon zest.", "Pass hot mixture through a sieve into a bowl to create a semi-smooth consistency.", "Allow to cool and thicken for 30 to 45 minutes, then pour into sealable jars."]', 5.00, 2, 'AllRecipes', 'https://www.allrecipes.com/recipe/270105/quince-jam/', 'https://www.allrecipes.com/thmb/m2jZIewzcbtzziQvl2NXEE9_aQI=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/ALR-270105-quince-jam-VAT-2x1-f417cd175607444fb584950cc41fe936.jpg', '12 servings', '', '', '', '2025-09-27 08:39:13.715723', '2025-09-27 08:39:13.715723');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (822, 'Courgette jam', '[{"name": "1kgcourgettesgrated", "unit": "", "amount": ""}, {"name": "750gjam sugar", "unit": "", "amount": ""}, {"name": "1lemonjuiced", "unit": "", "amount": ""}, {"name": "thumb-sized piece of gingerpeeled and grated", "unit": "", "amount": ""}]', '["If you don’t have a jam thermometer, put a couple of saucers in the freezer to chill. Put all the ingredients in a large saucepan or jam pan, and cook over a medium heat until the courgettes have released their liquid and the sugar has dissolved, about 10 mins. Cook for another 10 mins, stirring often until the liquid has reduced slightly, then cook for 10-15 mins more until the mixture reaches 105C on a jam thermometer. Or, spoon a little of the jam onto one of the cold saucers. After a couple of minutes, gently push your finger through the jam – if the surface wrinkles, it’s ready. If not, return to the boil for 2 mins, then re-test using the second saucer.", "Remove from the heat, skim off any froth, then leave to cool for 10 mins. Spoon into sterilised jars (see our guide). Will keep chilled for up to three months. Serve on toast as you would a marmalade."]', 5.00, 2, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/courgette-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2023/06/Courgette-jam-36f84ee.jpg?resize=768,713', '1', 'PT15M', 'PT30M', 'PT45M', '2025-09-27 08:35:44.183098', '2025-09-27 08:35:44.183098');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (873, 'Quince jelly', '[{"name": "1.5kg ripequincechopped", "unit": "", "amount": ""}, {"name": "1lemonzest peeled into strips, juiced", "unit": "", "amount": ""}, {"name": "900gpreserving sugar(you may need less)", "unit": "", "amount": ""}, {"name": "1 tbsporange blossom water(optional)", "unit": "", "amount": ""}]', '["Put the quince and lemon peel and juice in a large, deep saucepan. Cover with water (you''ll need about 3.2 litres) and bring to the boil. Reduce to a simmer and cook for 1 hr 30 mins until the quince is very soft.", "Line a colander with muslin cloth, and put over a very large saucepan or heatproof bowl. Tip the quince into the lined colander, cover with a clean tea towel and leave to strain for 4-8 hrs until all the juice has dripped out. Discard the pulp, then measure out the strained juice and transfer to a large, deep saucepan (the jelly will bubble up high as it cooks). Add 500g sugar for every 600ml juice.", "Bring to the boil over a low heat, stirring to dissolve the sugar. Bring to a rapid bubble and cook until the temperature reaches 105C on a sugar thermometer. Remove from the heat and stir in the orange blossom water, if using. Ladle into hot sterilised jars and seal. Will keep in a cool, dark place for up to a year."]', 4.00, 10, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/quince-jelly', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/10/Quince-jelly-640f1d9.jpg?resize=768,713', '2', 'PT20M', 'PT2H', 'PT2H20M', '2025-09-27 08:39:13.71981', '2025-09-27 08:39:13.71981');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (823, 'Fig Jam', '[{"item": "1 ½ pounds ripe figs, chopped", "name": "ripe figs, chopped", "unit": "pounds", "quantity": "1 ½"}, {"item": "⅓ cup water", "name": "water", "unit": "cup", "quantity": "⅓"}, {"item": "¼ cup coconut sugar", "name": "coconut sugar", "unit": "cup", "quantity": "¼"}, {"item": "1  cinnamon stick", "name": "cinnamon stick", "unit": "", "quantity": "1"}, {"item": "⅛ teaspoon ground allspice", "name": "ground allspice", "unit": "teaspoon", "quantity": "⅛"}, {"item": "⅛ teaspoon freshly ground mixed peppercorns", "name": "freshly ground mixed peppercorns", "unit": "teaspoon", "quantity": "⅛"}, {"item": "½  lemon, juiced", "name": "lemon, juiced", "unit": "", "quantity": "½"}, {"item": "⅔ (.25 ounce) packet agar-agar powder", "name": "agar-agar powder", "unit": "(.25 ounce) packet", "quantity": "⅔"}]', '["Bring figs, water, sugar, cinnamon, allspice, and peppercorns to a boil in a saucepan over medium-high heat. Boil until figs begin to liquefy, about 10 minutes. Reduce heat and simmer, stirring frequently to break down the figs, until jam is slightly thickened, about 15 minutes more.", "While jam is simmering, whisk lemon juice and agar-agar together. Set aside to gel.", "Take jam off the heat and remove the cinnamon stick. Add the lemon mixture and mix well. Let cool slightly. Pour jam into a pint jar with a hermetic seal. Allow to cool completely before refrigerating."]', 5.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/267259/fig-jam/', 'https://www.allrecipes.com/thmb/e0YEkzyUAOU7vA6CLuiSUo_RowM=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5615040-4ce5358e412145aaa8fb1fb3bf431c27.jpg', '16 servings', '', '', '', '2025-09-27 08:36:05.02531', '2025-09-27 08:36:05.02531');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (824, 'Fig and Honey Jam with Walnuts', '[{"item": "4 cups Mission figs, stemmed", "name": "Mission figs, stemmed", "unit": "cups", "quantity": "4"}, {"item": "2 cups honey", "name": "honey", "unit": "cups", "quantity": "2"}, {"item": "1 cup water", "name": "water", "unit": "cup", "quantity": "1"}, {"item": "¼ cup butter", "name": "butter", "unit": "cup", "quantity": "¼"}, {"item": "3 tablespoons lemon juice", "name": "lemon juice", "unit": "tablespoons", "quantity": "3"}, {"item": "2 teaspoons ground cinnamon", "name": "ground cinnamon", "unit": "teaspoons", "quantity": "2"}, {"item": "1 ½ teaspoons vanilla extract", "name": "vanilla extract", "unit": "teaspoons", "quantity": "1 ½"}, {"item": "1 teaspoon ground cloves", "name": "ground cloves", "unit": "teaspoon", "quantity": "1"}, {"item": "1 teaspoon salt", "name": "salt", "unit": "teaspoon", "quantity": "1"}, {"item": "½ teaspoon grated fresh ginger", "name": "grated fresh ginger", "unit": "teaspoon", "quantity": "½"}, {"item": "1 cup chopped walnuts", "name": "chopped walnuts", "unit": "cup", "quantity": "1"}]', '["Combine figs, honey, water, butter, lemon juice, cinnamon, vanilla, cloves, salt, and ginger in a large pot; bring to a boil. Reduce heat and simmer until figs are soft, about 1 hour and 15 minutes.", "Blend fig mixture with an immersion blender until jam reaches desired consistency. Stir in walnuts. Continue cooking until flavors meld, about 15 minutes.", "Pour jam into a container. Store in the refrigerator for up to one month.", "This recipe will keep in the refrigerator for up to one month if you do not can it.", "If using dried figs, remove and discard stems, then place in a large pot. Add enough water to cover figs by 1 inch; bring to a boil. Reduce heat and simmer for 30 minutes. Remove from heat. Cover and let sit until softened, up to 4 hours. Use 1 cup of liquid from the pot instead of the water in the recipe for extra flavor."]', 4.70, 19, 'AllRecipes', 'https://www.allrecipes.com/recipe/256649/fig-and-honey-jam-with-walnuts/', 'https://www.allrecipes.com/thmb/QHoiL5rnBjmFcMyO03wcbelO_4k=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/4576930-ca5fa01fbea84730b28ad59fec93b20c.jpg', '48 servings', '', '', '', '2025-09-27 08:36:05.028734', '2025-09-27 08:36:05.028734');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (825, 'Fresh Fig Freezer Jam', '[{"item": "5 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "5"}, {"item": "3 cups stemmed and coarsely chopped figs", "name": "stemmed and coarsely chopped figs", "unit": "cups", "quantity": "3"}, {"item": "⅓ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "⅓"}, {"item": "2 (3 ounce) pouches liquid pectin", "name": "liquid pectin", "unit": "(3 ounce) pouches", "quantity": "2"}]', '["Mix sugar and figs together in a large glass or plastic bowl. Set aside, stirring occasionally, until sugar has dissolved slightly, about 15 minutes.", "Stir lemon juice and pectin together in another glass or plastic bowl; pour into fig mixture and stir until sugar is fully dissolved.", "Pour fig mixture into sterilized plastic canning containers, filling to within 1/4 inch of the top. Cover the containers and refrigerate or freeze."]', 4.50, 10, 'AllRecipes', 'https://www.allrecipes.com/recipe/234169/fresh-fig-freezer-jam/', 'https://www.allrecipes.com/thmb/bY_ovtUYyV9aBLeDqSkEJjdJo6w=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5696750-1bd656bfcac14316bcbf85dcd517ef98.jpg', '96 servings', '', '', '', '2025-09-27 08:36:05.030067', '2025-09-27 08:36:05.030067');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (874, 'Low-Sugar Raspberry Jam', '[{"item": "1 (6 ounce) container fresh raspberries", "name": "fresh raspberries", "unit": "(6 ounce) container", "quantity": "1"}, {"item": "2 tablespoons white sugar", "name": "white sugar", "unit": "tablespoons", "quantity": "2"}, {"item": "1 tablespoon sugar-free raspberry syrup (such as Torani)", "name": "sugar-free raspberry syrup (such as Torani)", "unit": "tablespoon", "quantity": "1"}, {"item": "6 packets stevia sweetener (such as Truvia)", "name": "stevia sweetener (such as Truvia)", "unit": "packets", "quantity": "6"}, {"item": "1 teaspoon fresh lemon juice", "name": "fresh lemon juice", "unit": "teaspoon", "quantity": "1"}]', '["Place raspberries in a small saucepan; mash with a potato masher. Stir in sugar, syrup, and stevia. Reduce heat to low and simmer until thick, stirring constantly, 10 to 12 minutes. Remove from heat and stir in lemon juice.", "Add an additional 2 to 3 packets of Truvia for a sweeter jam."]', 5.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/267559/low-sugar-raspberry-jam/', 'https://www.allrecipes.com/thmb/YCU_We5BA069ofHK8Q24KLeLgrs=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5638282-308449a067f54deb87e72ac348ed625d.jpg', '4 servings', '', '', '', '2025-09-27 08:39:27.010154', '2025-09-27 08:39:27.010154');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (875, 'Raspberry Habanero Jam', '[{"item": "1 ½ cups white vinegar", "name": "white vinegar", "unit": "cups", "quantity": "1 ½"}, {"item": "1  green bell pepper, halved and seeded", "name": "green bell pepper, halved and seeded", "unit": "", "quantity": "1"}, {"item": "5  habanero peppers, stemmed, or more to taste", "name": "habanero peppers, stemmed, or more to taste", "unit": "", "quantity": "5"}, {"item": "6 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "6"}, {"item": "2 (12 ounce) packages frozen raspberries", "name": "frozen raspberries", "unit": "(12 ounce) packages", "quantity": "2"}, {"item": "3 tablespoons powdered pectin", "name": "powdered pectin", "unit": "tablespoons", "quantity": "3"}, {"item": "5  1-pint canning jars with lids and rings", "name": "1-pint canning jars with lids and rings", "unit": "", "quantity": "5"}]', '["Process vinegar, green bell pepper, and habanero peppers together in a blender until smooth. Transfer pepper mixture to a large stockpot.", "Add sugar, raspberries, and pectin to the stockpot; bring to a boil and cook, stirring until smooth and sugar is dissolved, about 5 minutes.", "Sterilize jars and lids in boiling water for at least 5 minutes.", "Pack jam into hot, sterilized jars, filling to 1/4 inch of the top. Run a knife or thin spatula around the insides of jars to remove any air bubbles. Wipe rims of jars with a moist paper towel to remove any food residue. Top with lids and screw-on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil; add jars using a holder. Leave a 2-inch space between the jars. Pour in more boiling water if necessary to bring water level to at least 1 inch above the tops of the jars. Bring water to a rolling boil, cover, and process for 15 minutes.", "Remove jars from the stockpot and transfer to cloth-covered or wood surface, several inches apart. Cool.", "Press the top of each lid with a finger, ensuring that the seal is tight (the lid should not move up or down at all). Store in a cool, dark area."]', 4.70, 37, 'AllRecipes', 'https://www.allrecipes.com/recipe/238386/raspberry-habanero-jam/', 'https://www.allrecipes.com/thmb/qymSPrrOepd5jl34xb5HnpHBo_0=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/3999714-1cd468c0ea9341468dbb8f3f79d0098c.jpg', '80 servings', '', '', '', '2025-09-27 08:39:27.012997', '2025-09-27 08:39:27.012997');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (826, 'Gooseberry & elderflower jam', '[{"name": "1kggooseberries", "unit": "", "amount": ""}, {"name": "900ggranulated white sugar", "unit": "", "amount": ""}, {"name": "100mlelderflower cordial", "unit": "", "amount": ""}]', '["Tip the gooseberries into a large saucepan or preserving pan with 300ml water. Cook over a low heat for about 15 mins, stirring occasionally, until the fruit has broken down into a soupy purée.", "Add the sugar and cook over a low heat until the sugar has dissolved, then turn up the heat as high as it will go and boil everything for 10 mins, stirring the bottom to make sure the jam doesn’t burn and skimming any scum off when it appears. If you have a jam or digital thermometer, the optimum temperature for the jam to set is 105C, but anything around 104C will be fine.", "Do the wrinkle test (see tip) on a frozen saucer to make sure it’s set, then add the elderflower cordial and leave to cool before decanting your jam into sterilised jars."]', 5.00, 7, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/gooseberry-elderflower-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gooseberry-elderflower-jam-cbd0f91-scaled.jpg?resize=768,698', '3', 'PT5M', 'PT30M', 'PT35M', '2025-09-27 08:36:14.057751', '2025-09-27 08:36:14.057751');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (827, 'Gooseberry & camomile jam', '[{"name": "1 ½kggooseberrieswashed", "unit": "", "amount": ""}, {"name": "100mllemon juice(about 3 lemons)", "unit": "", "amount": ""}, {"name": "3 tbspdried camomile flowers(taken from 3 tea bags)", "unit": "", "amount": ""}, {"name": "1kgwhite granulated sugar", "unit": "", "amount": ""}]', '["Put a few small saucers in the freezer. Top and tail the gooseberries and tip into a preserving pan or large pan with the lemon juice.", "Measure 250ml boiling water and infuse the camomile flowers in it for 5 mins to make a tea. Strain, then mix in with the gooseberries and soften over a medium heat, stirring so it doesn’t stick, for about 10 mins.", "Sterilise the jars by washing in hot soapy water, rinsing thoroughly. Drip-dry upside-down, then put in a warm oven at 120C/100C fan/gas 1 /2 for at least 20 mins.", "Bring the gooseberry mixture to the boil, then add the sugar, stirring constantly until dissolved. Bring it back to a rapid boil, stirring intermittently so it doesn’t burn on the bottom of the pan, for 20-25 mins.", "Start testing whether your jam has set 15-20 mins into the rapid boil. Remove the pan from the heat. To test, take a saucer from the freezer, put 1 tsp of jam on it, then put it in the fridge for 1 min. Then push the jam with your finger – if the surface wrinkles, the jam is ready. If not, repeat the process until a desired surface has formed, trying not to overcook the jam.", "Once ready, remove from the heat, skim off any scum and ladle into the warm, dry sterilised jars. Seal immediately while the jam is hot. Will keep sealed for up to 1 year. Once opened, keep in the fridge and eat within 3 months."]', 5.00, 1, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/gooseberry-camomile-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/gooseberry-camomile-jam-540bdad.jpg?resize=440,400', '6', 'PT20M', 'PT35M', 'PT55M', '2025-09-27 08:36:14.061556', '2025-09-27 08:36:14.061556');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (828, 'Gooseberry Jam', '[{"item": "2 quarts fresh gooseberries", "name": "fresh gooseberries", "unit": "quarts", "quantity": "2"}, {"item": "6 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "6"}, {"item": "½ (6 fluid ounce) container liquid pectin", "name": "liquid pectin", "unit": "(6 fluid ounce) container", "quantity": "½"}]', '["Remove blossom and stem ends from gooseberries. Force berries through a food mill. Measure 4 cups of berry mash into a large pot. Stir in sugar. Bring to a full rolling boil over high heat, and boil hard for 1 minute, stirring constantly. Remove from heat, and stir in pectin. Skim off any foam with a large metal spoon.", "Sterilize jars and lids in boiling water for at least 10 minutes. Alternate skimming the foam and stirring the berry mixture while letting it cool slightly, about 5 minutes. Ladle into hot, sterile jars, leaving 1/4 inch headspace.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil over high heat, then carefully lower the jars into the pot using a holder. Leave a 2 inch space between the jars. Pour in more boiling water if necessary until the water level is at least 1 inch above the tops of the jars. Bring the water to a full boil, cover the pot, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool, dark area."]', 4.40, 8, 'AllRecipes', 'https://www.allrecipes.com/recipe/35123/gooseberry-jam/', 'https://www.allrecipes.com/thmb/AfpvuKEagwmoM88vmJA83iSfCvk=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/3177-9040c8f67c9b441c8580ce770febd0dd.jpg', '80 servings', '', '', '', '2025-09-27 08:36:14.064239', '2025-09-27 08:36:14.064239');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (876, 'No-Sugar Raspberry Jam', '[{"item": "2 tablespoons lemon juice", "name": "lemon juice", "unit": "tablespoons", "quantity": "2"}, {"item": "½ teaspoon agar-agar powder", "name": "agar-agar powder", "unit": "teaspoon", "quantity": "½"}, {"item": "4 cups fresh raspberries", "name": "fresh raspberries", "unit": "cups", "quantity": "4"}]', '["Combine lemon juice and agar-agar powder in a small bowl. Set aside to thicken for 5 minutes.", "Heat raspberries in a saucepan over medium heat until raspberries start to break up, 5 to 10 minutes. Use a masher to crush raspberries into a smooth consistency, but do not liquefy. Turn off the heat.", "Stir thickened lemon-agar-agar paste into the saucepan with the raspberries and mix well. Allow to cool to room temperature, about 30 minutes. Pour into an airtight container and refrigerate until set, about 2 hours."]', 4.70, 3, 'AllRecipes', 'https://www.allrecipes.com/recipe/267550/no-sugar-raspberry-jam/', 'https://www.allrecipes.com/thmb/heAiJKUmjbdeUrfjW-CnNemNuOI=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5647157-3a6e121e9b4f43dcb48dca5b49a71129.jpg', '10 servings', '', '', '', '2025-09-27 08:39:27.014114', '2025-09-27 08:39:27.014114');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (877, 'Green Tomato Raspberry Jam', '[{"item": "4 cups shredded green tomatoes", "name": "shredded green tomatoes", "unit": "cups", "quantity": "4"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}, {"item": "1 (6 ounce) package raspberry flavored Jell-O mix", "name": "raspberry flavored Jell-O mix", "unit": "(6 ounce) package", "quantity": "1"}]', '["Combine tomatoes and sugar in a large saucepan and bring to a boil over medium heat. Cook and stir for about 10 minutes.", "Add gelatin to tomato mixture; stir until well combined. Reduce heat to low and simmer for 20 minutes.", "Spoon jam into hot, sterilized jars and seal. Alternatively, pour into freezer containers and freeze."]', 4.20, 74, 'AllRecipes', 'https://www.allrecipes.com/recipe/20953/green-tomato-raspberry-jam/', 'https://www.allrecipes.com/thmb/jcu0NzAuD0ulER17YP27d8WlNlM=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/2499995-259fce6c668c4d95936f20b92305863d.jpg', '112 servings', '', '', '', '2025-09-27 08:39:27.014868', '2025-09-27 08:39:27.014868');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (829, 'Grape jelly', '[{"name": "1kgred grapespreferable with seeds (stripped from the stalks)", "unit": "", "amount": ""}, {"name": "450gjam sugar(with added pectin)", "unit": "", "amount": ""}, {"name": "juice 1lemon", "unit": "", "amount": ""}]', '["Tip the grapes into a large saucepan set over a low heat, then cover and leave to gently cook for 5 mins until the juices start to run. Take a potato masher or fork and mash up the grapes. Leave to cook for about 10 mins more, mashing every now and again until the grapes are falling apart. Place a clean tea towel or kitchen cloth in a sieve set over a bowl, then pour the grape mixture into this. Let the mixture drip through for at least 1 hr or preferably overnight.", "Measure out the juice (you should have about 600ml) and pour it into a pan along with the sugar and lemon juice. Set the pan over a high heat and bring to the boil. Skimming any scum as it boils, let the mixture bubble until the temperature reaches 105C on a sugar thermometer. If you don’t have one, put a small plate in the freezer for 5 mins, then pour a little of the juice onto the cold saucer. After 1 min, run your finger through; if the jam wrinkles slightly, it’s ready. Pour the hot jam into a sterilised jar. Will keep unopened for up to 3 months."]', 5.00, 27, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/grape-jelly', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-12219_12-43589ec.jpg?resize=440,400', '600', 'PT10M', 'PT20M', 'PT30M', '2025-09-27 08:36:26.079256', '2025-09-27 08:36:26.079256');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (830, 'Concord Grape Onion Jam', '[{"item": "1 quart stemmed Concord grapes", "name": "stemmed Concord grapes", "unit": "quart", "quantity": "1"}, {"item": "1 large red onion, quartered and thinly sliced", "name": "red onion, quartered and thinly sliced", "unit": "large", "quantity": "1"}, {"item": "½ cup light brown sugar", "name": "light brown sugar", "unit": "cup", "quantity": "½"}, {"item": "2 tablespoons balsamic vinegar", "name": "balsamic vinegar", "unit": "tablespoons", "quantity": "2"}]', '["Immerse a jar with a tight fitting lid in simmering water until grape jam is ready. Wash lid in warm, soapy water.", "Place grapes in a saucepan and crush with a potato masher until they begin to release some juice. Cover and cook over low heat until grapes are very soft and can be easily crushed, about 10 minutes. Remove from heat and set aside to cool.", "Push grapes and all of the accumulated juice through a food mill or a fine mesh strainer. Extract as much pulp and juice as you can and return back to the saucepan. This should yield a generous 2 cups pulp. Discard leftover seeds.", "Add onion, brown sugar, and balsamic vinegar to grape pulp and mix until sugar has dissolved. Bring to a boil over medium-high heat, then reduce temperature to low and simmer, uncovered, stirring occasionally at the beginning and then more regularly to prevent scorching, until jam is thick, about 30 minutes. (Do not overcook--jam will thicken more as it cools.)", "Pour hot jam into the sterilized jar, seal tightly with the clean lid, and allow to cool. Store in the refrigerator and use within 2 weeks.", "Note that this is a refrigerator jam, not a jam for canning."]', 5.00, 2, 'AllRecipes', 'https://www.allrecipes.com/recipe/277232/concord-grape-onion-jam/', 'https://www.allrecipes.com/thmb/fNLK8Egu5dVkNJWGpU2mgDvs5LA=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/7304934-9e30d237744e4c53ab658998bc344bad.jpg', '20 servings', '', '', '', '2025-09-27 08:36:26.082444', '2025-09-27 08:36:26.082444');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (831, 'Muscadine Jam', '[{"item": "2 quarts muscadine grapes", "name": "muscadine grapes", "unit": "quarts", "quantity": "2"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}]', '["Inspect four 8-ounce jars (or two 16-ounce jars) for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water. Place a couple of small plates in the freezer.", "Wash grapes and cut in half. Combine grapes and sugar in a large pot over medium heat and bring to a boil. Maintain a medium to low boil, and cook for 15 to 20 minutes, stirring often to prevent sticking; make sure it does not get too hot.", "Check doneness by dropping a small spoonful of jam onto a frozen plate. Let sit for 1 to 2 minutes; if jam appears to gel, it is ready. Continue cooking if jam appears thin and runny.", "Transfer a portion of the jam mixture to a blender; puree until all hulls and seeds are ground. Set pureed jam in a pot to keep warm, and continue with remaining mixture.", "Pack pureed jam into hot, sterilized jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 5 to 10 minutes.", "Remove from water and set aside until the entire canning jar cools and you hear the \"pop\" of the can sealing, at least 1 hour. Store in a dark, cool area."]', 4.80, 5, 'AllRecipes', 'https://www.allrecipes.com/recipe/281386/muscadine-jam/', 'https://www.allrecipes.com/thmb/JpkXOV4WzT9VRtfv2yBc8NtPo-o=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/293959-aa6826f0aa704f09b0c1c5e469c91947.jpg', '160 servings', '', '', '', '2025-09-27 08:36:26.084054', '2025-09-27 08:36:26.084054');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (832, 'Concord Grape Jam', '[{"item": "8cupsconcord grapes", "name": "8cupsconcord grapes", "unit": "", "quantity": ""}, {"item": "6cupsgranulated sugar", "name": "6cupsgranulated sugar", "unit": "", "quantity": ""}, {"item": "1/2teaspoonunsalted butter", "name": "1/2teaspoonunsalted butter", "unit": "", "quantity": ""}, {"item": "Juice of1/2alemon", "name": "Juice of1/2alemon", "unit": "", "quantity": ""}]', '["If you are going to preserve the jam, prepare the jars and lids: place six half-pint jars on a rack in a large pot. Add enough water to cover the jars, and bring to a boil over high heat. Boil for 10 minutes, then turn off heat and allow jars to rest in the hot water. Meanwhile, put bands and lids in a small saucepan and cover with water. Heat over medium heat until the water is simmering, then remove pan from heat and allow the bands and lids to rest in hot water until ready to use.", "Separate the grape skins from the pulp by squeezing the grapes between your fingers. Put the skins in the work bowl of a food processor and pulse until they are coarsely chopped. Transfer them to a large heavy-bottomed pot and add 1/4 cup of water. Bring to a simmer and cook until the skins have softened a bit, about 10 minutes.", "Meanwhile, place the grape pulp in a medium saucepan and bring to a boil. Reduce the heat and simmer until the grapes lose their shape, about 10 minutes. Pour the grape pulp through a fine-mesh sieve or food mill into a large bowl. Force out as much pulp as you can and discard the seeds.", "Add grape pulp to the pot with grape skins and stir to combine. Add sugar, butter, and lemon juice and bring the mixture to a boil, stirring constantly. Reduce heat and simmer, stirring frequently, until jam has reduced and thickened and reached the gel stage, about 45 minutes. To test the jam, put a small plate in the freezer. When it is chilled, spoon a bit of jam onto the plate and return it to the freezer for one minute. Drag your fingertip through the jam and tilt the plate from side to side. If the jam stays put and doesn''t run, it''s set. If not, simmer the jam for a few minutes more.", "Ladle hot jam into hot sterilized jars, leaving 1/4-inch headspace. Wipe rims of jars, cover with lids, and screw bands on until just barely tight. Place jars on rack in pot and cover completely with water. Cover pot and bring to a boil over high heat. Boil for 10 minutes. Turn off heat, uncover pot, and allow jars to rest in water for 5 minutes. Remove jars from pot and allow them to rest undisturbed on countertop for 6 hours or overnight."]', 4.30, 7, 'Serious Eats', 'https://www.seriouseats.com/concord-grape-jam-recipe-grape-jelly', 'https://www.seriouseats.com/thmb/BjVtaTl3T0kW0Y3u4Vq4jMXvYaY=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__recipes__images__2011__09__20110918preservedjamresaved-937f276352cc4708a8b7f14f89bff3bb.jpg', '48 servings', 'Prep10 mins', '', 'Total7 hrs 40 mins', '2025-09-27 08:36:26.08497', '2025-09-27 08:36:26.08497');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (834, 'Kiwi Jam', '[{"item": "24  kiwis, peeled and mashed", "name": "kiwis, peeled and mashed", "unit": "", "quantity": "24"}, {"item": "¾ cup pineapple juice", "name": "pineapple juice", "unit": "cup", "quantity": "¾"}, {"item": "¼ cup fresh lemon juice", "name": "fresh lemon juice", "unit": "cup", "quantity": "¼"}, {"item": "3  apples, unpeeled and halved", "name": "apples, unpeeled and halved", "unit": "", "quantity": "3"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}]', '["In a large saucepan, combine 3 cups mashed kiwi, pineapple juice, lemon juice and apples. Bring to a boil and then add the sugar; stir to dissolve, reduce heat and simmer for 30 minutes.", "Sterilize the jars and lids in boiling water for at least 5 minutes. Pack the jam into the hot, sterilized jars, filling the jars to within 1/4 inch of the top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil over high heat, then carefully lower the jars into the pot using a holder. Leave a 2 inch space between the jars. Pour in more boiling water if necessary until the water level is at least 1 inch above the tops of the jars. Bring the water to a full boil, cover the pot, and process for 10 minutes."]', 4.50, 27, 'AllRecipes', 'https://www.allrecipes.com/recipe/25904/kiwi-jam/', 'https://www.allrecipes.com/thmb/wZkswMEvXpHVgApgcA5bJh6eV1I=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/7240854-9aeae1f7fa554d2888e6bab514d00978.jpg', '40 servings', '', '', '', '2025-09-27 08:36:45.468131', '2025-09-27 08:36:45.468131');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (878, 'Homemade raspberry jam', '[{"name": "1kgraspberry", "unit": "", "amount": ""}, {"name": "juice of 1lemon", "unit": "", "amount": ""}, {"name": "1kgbag jamsugar (the one with pectin added)", "unit": "", "amount": ""}]', '["Before you start, sterilise your jars (see tip below) and put a plate in the freezer to chill. Tip half the raspberries into a preserving pan and add the lemon juice. Mash the berries to a pulp over the heat with a potato masher, then leave to cook for 5 mins. Tip the cooked berries into a sieve over a bowl, then once all of the juice has drained off, firmly work the pulp through the sieve with a wooden spoon until you are left with just the seeds.", "Tip the juice and pulp back into the preserving pan and stir in the sugar. Heat gently, then add the remaining whole raspberries. Bring to the boil, then boil rapidly for 5 mins. Remove from the heat and drop a little jam onto the chilled plate. Now push your finger through it – it should wrinkle and look like jam. If it doesn’t, boil for 2 mins, then test again.", "The top of the jam may look like it has sediment on it, but I find that if you stir it well as it cools, a little of this disappears. Pour into the jars and seal. It will keep unopened for a year, although the lovely bright colour will darken a little. Once open, keep in the fridge."]', 4.00, 41, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/homemade-raspberry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1067474_11-48f0a50.jpg?resize=440,400', '1', 'PT15M', 'PT12M', 'PT28M', '2025-09-27 08:39:27.015577', '2025-09-27 08:39:27.015577');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (879, 'Easy, Homemade Raspberry Jam', '["500g raspberries", "400g jam sugar", "Finely grated zest and juice 1 lemon"]', '["1. Put the raspberries in a large saucepan and heat gently until they soften and the juices start to run. Press the raspberries with the edge of a wooden spoon to break them up, then stir in the sugar and lemon zest and juice. Heat gently until the sugar dissolves, stirring frequently.", "2 Bring the mixture to the boil and boil for about 5-6 minutes until setting point is reached. To test, place a teaspoon of the jam on a small plate and chill for 2 minutes. The surface should wrinkle when pushed with a finger. If necessary, boil the jam a little longer. If the surface of the jam becomes foamy, skim it off with a slotted spoon.", "3. Ladle the jam into steralised jars, cover with waxed discs and lids. Label and store for up to 12 months."]', 4.00, 7, 'Food Network', 'https://foodnetwork.co.uk/recipes/easy-homemade-raspberry-jam', 'https://d2vsf1hynzxim7.cloudfront.net/production/media/14506/conversions/foodnetwork-image-1de9748b-8938-4b06-b561-a9e71c5e1b0c-default.webp', '0', 'PT15M', 'PT15M', '', '2025-09-27 08:39:27.016627', '2025-09-27 08:39:27.016627');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (835, 'Rich Red Plum Jam', '[{"item": "5poundslarge red-fleshed plums, such as Elephant Hearts, halved and pitted", "name": "5poundslarge red-fleshed plums, such as Elephant Hearts, halved and pitted", "unit": "", "quantity": ""}, {"item": "2poundswhitecane sugar, divided", "name": "2poundswhitecane sugar, divided", "unit": "", "quantity": ""}, {"item": "2ounces(4tablespoons)fresh juicefrom2wholelemons", "name": "2ounces(4tablespoons)fresh juicefrom2wholelemons", "unit": "", "quantity": ""}]', '["Divide plums halves into two equal portions. In a non-reactive mixing bowl, mix first group of plum halves with 1 pound 4 ounces of sugar. Cut the remaining plum halves in half to make quarters. In a separate bowl, mix plum quarters with remaining 12 ounces sugar. Cover both bowls tightly and let stand at room temperature overnight.", "In a large pot, scrape plum halves along with any sugar and syrup and cook over medium heat, stirring only enough to prevent scorching, until softened, about 15 minutes. Pass plum halves through a food mill fitted with the smallest-hole plate; scrape any plum left on the mill into the plum puree. Mix plum puree into quartered plums and stir in lemon juice.", "Thoroughly wash 12 half-pint canning jars and their lids. If you plan to process the jam for shelf-stable storage, prepare a water bath and sterilize the jars as described in ourcanning guide. Set several metal spoons on a plate in the freezer. Scrape plum mixture into a wide jamming pot.", "Heat plum mixture over medium-high heat, stirring as needed to prevent scorching, until jam starts to foam, about 15 minutes. Continue to cook, stirring minimally and lowering the heat if necessary to prevent scorching, until foaming has subsided, about 15 minutes longer; scrape any foam off jam surface with a stainless steel spoon as needed. Continue to cook after foaming has subsided until bubbling has slowed and jam looks glossy, 5 to 15 minutes; lower heat as necessary to prevent scorching.", "When plum quarters have broken down and jam has thickened, begin testing for doneness: Turn off heat and set a small spoonful of jam in the freezer for 5 minutes. Jam is ready once it holds together and doesn''t run off the spoon when tilted. If jam is too runny, return to heat and cook, stirring frequently and repeating the spoon test every 5 minutes, until jam passes test.", "Transfer jam into prepared canning jars and wipe any jam from the rims. To store in refrigerator, simply place lids on jars, screw on rings, and let cool completely at room temperature before refrigerating. To process jam for shelf-stable storage, bring water bath to a rolling boil. Place lids on jars and screw on rings until they are snug but not overly tight. Carefully lower jars into boiling water bath and process for 10 minutes. Remove jars and let cool completely at room temperature. Unprocessed jam can be refrigerated for a couple of months; Processed jam can be stored in a cool, dark place for up to 1 year."]', 5.00, 1, 'Serious Eats', 'https://www.seriouseats.com/red-plum-jam-recipe', 'https://www.seriouseats.com/thmb/Lj15IjNWN-2gbMrK_ZtGJFaHE_Y=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__recipes__images__2014__07__20140630-plum-jam-beignets-jennifer-latham-36175e87792a442b864acf3729e1a6ff.jpg', '96 servings', 'Prep15 mins', '', 'Total12 hrs 25 mins', '2025-09-27 08:37:07.580606', '2025-09-27 08:37:07.580606');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (836, 'Jalapeño Strawberry Jam', '[{"item": "4 cups crushed strawberries", "name": "crushed strawberries", "unit": "cups", "quantity": "4"}, {"item": "1 cup minced jalapeno peppers", "name": "minced jalapeno peppers", "unit": "cup", "quantity": "1"}, {"item": "¼ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "¼"}, {"item": "1 (2 ounce) package powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(2 ounce) package", "quantity": "1"}, {"item": "7 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "7"}]', '["Inspect eight 1/2-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Bring strawberries, jalapeños, lemon juice, and pectin to a boil in a large saucepan over high heat. Stir in sugar, return to a boil, and cook for 1 minute.", "Pack jam into hot, sterilized jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and let rest, several inches apart, for 12 to 24 hours. Press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 4.80, 267, 'AllRecipes', 'https://www.allrecipes.com/recipe/180695/jalapeno-strawberry-jam/', 'https://www.allrecipes.com/thmb/Ge-ISv1fzraQ6ZiqH3raIUYyGp8=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/6684668-78e6d5673bdd4d048c489c83b0977bfe.jpg', '64 servings', '', '', '', '2025-09-27 08:37:07.583988', '2025-09-27 08:37:07.583988');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (837, 'Raspberry Jam without Pectin', '[{"item": "4 cups mashed raspberries", "name": "mashed raspberries", "unit": "cups", "quantity": "4"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}, {"item": "1 tablespoon freshly squeezed lemon juice", "name": "freshly squeezed lemon juice", "unit": "tablespoon", "quantity": "1"}]', '["Gather the ingredients.", "Bring raspberries to a boil in a very large pot. Cook for 4 minutes. Add sugar and lemon juice; stir well. Bring back to a boil, stirring constantly. Cook for another 4 minutes.", "Remove pot from heat and divide jam into sterilized jars, leaving 1/4-inch of space at the top. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil, then carefully lower the jars into the pot using a holder, leaving a 2-inch space between the jars. Pour in more boiling water if necessary until the water level is at least 1 inch above the tops of the jars. Bring the water to a full boil, cover the pot, and process for 10 minutes.", "Remove the jars from the stockpot and let rest, several inches apart, for 8 hours. Store in a cool, dark area."]', 4.80, 74, 'AllRecipes', 'https://www.allrecipes.com/recipe/263781/raspberry-jam-without-pectin/', 'https://www.allrecipes.com/thmb/Z8-D46ifxRmSn5y_iCMw0B02pnY=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/263781-raspberry-jam-without-pectin-VAT-Beauty-2x1-c493e4b1525a4c0d984b4a7b39a58522.jpg', '56 servings', '', '', '', '2025-09-27 08:37:07.584998', '2025-09-27 08:37:07.584998');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (838, 'Strawberry Jam', '[{"item": "2 pounds fresh strawberries, hulled", "name": "fresh strawberries, hulled", "unit": "pounds", "quantity": "2"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}, {"item": "¼ cup lemon juice", "name": "lemon juice", "unit": "cup", "quantity": "¼"}]', '["Gather all ingredients.", "Crush strawberries in a wide bowl in batches until you have 4 cups of mashed strawberries.", "Combine mashed strawberries, sugar, and lemon juice in a heavy-bottomed saucepan; stir over low heat until sugar is dissolved.", "Increase heat to high, and bring the mixture to a full rolling boil. Continue to boil, stirring often, until the mixture reaches 220 degrees F (105 degrees C).", "Check doneness after 10 to 15 minutes by dropping a small spoonful of jam onto a frozen plate. Let sit for 1 to 2 minutes; if jam appears to gel, it is ready. Continue cooking if jam appears thin and runny.", "Transfer jam into hot sterile jars, filling to within 1/4 inch of the top. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill it halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder.", "Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and let rest, several inches apart, for 12 to 24 hours. Store in a cool, dark area.", "Serve and enjoy!"]', 4.70, 948, 'AllRecipes', 'https://www.allrecipes.com/recipe/38410/strawberry-jam/', 'https://www.allrecipes.com/thmb/AfOW4MxO3aU3Mn2KkPVg8Q4BxSw=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/AR-RM-38410-strawberry-jam-ddmfs-1x2-95b856cc434f474b85b49b6709901517.jpg', '40 servings', '', '', '', '2025-09-27 08:37:07.585907', '2025-09-27 08:37:07.585907');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (840, 'Guava Jam', '[{"item": "2 pounds ripe guavas, washed and trimmed", "name": "ripe guavas, washed and trimmed", "unit": "pounds", "quantity": "2"}, {"item": "4 cups white sugar, or as needed", "name": "white sugar, or as needed", "unit": "cups", "quantity": "4"}, {"item": "4 tablespoons lime juice, or as needed", "name": "lime juice, or as needed", "unit": "tablespoons", "quantity": "4"}, {"item": "1 (3 ounce) pouch liquid pectin", "name": "liquid pectin", "unit": "(3 ounce) pouch", "quantity": "1"}]', '["Cut each guava into 8 pieces. Place in a pot and fill with water until guavas are just covered; bring to a boil. Reduce heat and simmer until fruit is very soft, about 1 hour. Remove from the heal and let cool for 10 to 15 minutes.", "Strain contents of the pot through a fine mesh sieve into a 1-quart liquid measure, using a spatula to push fruit through the sieve so only seeds and skins remain. Take note, you should get about 4 cups fruit. Clean the pot and pour the fruit back in.", "For every cup of fruit, add 1 cup sugar and 1 tablespoon lime juice. Stirring constantly, bring to a simmer and continue to cook until sugar is dissolved and jam has thickened, 15 to 20 minutes. If the jam thickens on its own and drips very slowly off a spoon, do not add pectin. However, if it does not thicken up enough, add liquid pectin and simmer for 1 minute longer. Pull off the heat.", "While jam is cooking, inspect six 1/2-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Fill hot, sterilized jars to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and let rest, several inches apart, for 12 to 24 hours. Press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 4.50, 2, 'AllRecipes', 'https://www.allrecipes.com/recipe/284368/guava-jam/', 'https://www.allrecipes.com/thmb/BHJwvLbB_TcmQv7QORDYA3YxAXo=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/2522905-9f7ad22c795a41c6aeb9362cbd5203ee.jpg', '80 servings', '', '', '', '2025-09-27 08:37:18.973704', '2025-09-27 08:37:18.973704');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (880, 'Really fruity strawberry jam', '[{"name": "1 ¼kgfirm, ripe strawberryhulled and large ones halved", "unit": "", "amount": ""}, {"name": "1kgpack jam sugar(this is sugar wiht added pectin)", "unit": "", "amount": ""}, {"name": "juice 1lemon", "unit": "", "amount": ""}, {"name": "knobunsalted butter(optional)", "unit": "", "amount": ""}]', '["Layer the strawberries and sugar in a large bowl, finishing with a layer of sugar. Cover the bowl and leave for 24 hrs. Meanwhile, put a couple of saucers in the freezer to chill.", "Next day, the juices will have been drawn out from the fruit, leaving you with a bowl of berries in a sugary pink syrup. If this hasn’t happened, give the berries a quick stir, then leave for a few more hrs.", "Tip the mixture into a preserving pan (or a large pan with wide, sloping sides) and warm to dissolve the remaining sugar. Once dissolved, bring to the boil, stirring frequently until bubbling.", "Pour in the lemon juice, return to the boil, then boil hard for 10 mins, stirring occasionally. If the surface of the jam starts to look a little scummy, add the butter. Take from the heat, then drop a little jam onto a chilled saucer. Let it cool a little, then push your finger through the jam. If the jam parts, leaving wrinkled ripples, it is ready. (Turn to p23 for more detail on testing when jam is ready.) Allow the jam to cool for 30 mins, then pot into sterilised jars and, when the jam is cool, label the jars. Keeps for 1 year – store in the fridge once open."]', 5.00, 12, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/really-fruity-strawberry-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1067513_11-68538ab.jpg?resize=440,400', '3', '', 'PT15M', 'PT25M', '2025-09-27 08:40:00.76705', '2025-09-27 08:40:00.76705');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (843, 'Pineapple Mango Habanero Jam', '[{"item": "1 ½ pounds fresh mangos, cut into chunks", "name": "fresh mangos, cut into chunks", "unit": "pounds", "quantity": "1 ½"}, {"item": "1 pound fresh pineapple, cut into chunks", "name": "fresh pineapple, cut into chunks", "unit": "pound", "quantity": "1"}, {"item": "½ cup cider vinegar", "name": "cider vinegar", "unit": "cup", "quantity": "½"}, {"item": "4 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "4"}, {"item": "1 (3 ounce) pouch liquid pectin", "name": "liquid pectin", "unit": "(3 ounce) pouch", "quantity": "1"}, {"item": "4  habanero chiles - stemmed, halved, and seeded", "name": "habanero chiles - stemmed, halved, and seeded", "unit": "", "quantity": "4"}]', '["Inspect 8 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Combine mangos, pineapple, and cider vinegar in a stockpot over medium-high heat; simmer. Mash mangos and pineapple to a chunky consistency with a potato masher; stir in sugar until dissolved.", "Stir liquid pectin and habanero chile halves into the stockpot. Bring to a rolling boil, stirring frequently. Cook, stirring frequently, until jam thickens, about 2 minutes. Remove chile halves. Discard 6 chile halves; chop 2 halves and stir back into the stockpot.", "Pack jam into hot, sterilized jars, filling to within ¼ inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe the rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and let rest, several inches apart, for 12 to 24 hours. Press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 5.00, 6, 'AllRecipes', 'https://www.allrecipes.com/recipe/256924/pineapple-mango-habanero-jam/', 'https://www.allrecipes.com/thmb/G53z9svGFvx4lTQQGcrvdqWEADQ=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/9186919-220bdb6ee3b749118ce1f0dcfb61e358.jpg', '64 servings', '', '', '', '2025-09-27 08:37:27.067961', '2025-09-27 08:37:27.067961');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (844, 'Mango Jam', '[{"item": "2 pounds ripe mangoes", "name": "ripe mangoes", "unit": "pounds", "quantity": "2"}, {"item": "1 ½ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "1 ½"}, {"item": "¾ cup water", "name": "water", "unit": "cup", "quantity": "¾"}, {"item": "3  saffron threads (Optional)", "name": "saffron threads (Optional)", "unit": "", "quantity": "3"}]', '["Gather all ingredients.", "Make 4 to 5 slits in mangoes and microwave on High until soft, 1 to 2 minutes.", "Cool, then remove the peel and inner seed; place mango pulp in a large bowl. Use a fork or potato masher to mash the pulp well.", "Stir together sugar and water in a large saucepan over low heat, and bring to a boil. When the mixture begins boiling, increase heat to medium-high. Continue boiling until fine, soft threads form, 270 degrees F (135 degrees C).", "Stir in mango pulp, add saffron threads, and boil until the mixture thickens, about 5 minutes.", "Pour cooked jam into sterilized jars and seal according to canning directions."]', 4.70, 78, 'AllRecipes', 'https://www.allrecipes.com/recipe/142747/mango-jam/', 'https://www.allrecipes.com/thmb/lfAMSUNKQcloAr3_rsmJBZv1xuA=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/RM-142747-MangoJam-ddmfs-2x1-69243-30658f2346404e8c80197dd600754abc.jpg', '24 servings', '', '', '', '2025-09-27 08:37:27.071312', '2025-09-27 08:37:27.071312');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (881, 'Strawberry & Pimm''s jam', '[{"name": "1 ½kgstrawberrieshulled and halved if large", "unit": "", "amount": ""}, {"name": "1kgbag jam sugar(it has added pectin)", "unit": "", "amount": ""}, {"name": "juice 2 lemons", "unit": "", "amount": ""}, {"name": "juice 1 orange", "unit": "", "amount": ""}, {"name": "3 tbspPimm''sNo 1", "unit": "", "amount": ""}]', '["Put a few saucers into the freezer for later. Put the strawberries into a preserving pan (or your very largest saucepan), then mash roughly with a potato masher, or crush slightly between your fingers and thumbs to make them a little juicy. Stir in the sugar, then put the pan over a very low heat. Stir occasionally until the sugar has dissolved and the berries are surrounded with red syrup. Don’t let it boil. Once the sugar crystals have gone, stir in the lemon and orange juice.", "Turn up the heat. Once the jam reaches a foaming fast boil (this will read 105C on a preserving thermometer), set a timer for 10 mins. After 10 mins, put 1 tsp of the jam onto one of the frozen saucers, put into the fridge for 1 min, then push your finger through the jam. If the jam wrinkles, it is ready. If not, boil for another 2 mins and test again. As soon as the jam is ready, remove from the heat. Leave to cool for 30 mins, skim away any scum, then stir in the Pimm’s. Ladle into sterilised jars. Will keep in a cool, dark place for up to 1 year."]', 5.00, 7, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/soft-set-strawberry-pimms-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-1067509_11-e42ef2e.jpg?resize=440,400', '5', 'PT15M', 'PT20M', 'PT35M', '2025-09-27 08:40:00.769957', '2025-09-27 08:40:00.769957');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (882, 'Summer Strawberry Jam', '[{"item": "1 ⅓ cups crushed fresh strawberries", "name": "crushed fresh strawberries", "unit": "cups", "quantity": "1 ⅓"}, {"item": "1 ½ tablespoons fruit pectin (such as Ball Realfruit Classic Pectin)", "name": "fruit pectin (such as Ball Realfruit Classic Pectin)", "unit": "tablespoons", "quantity": "1 ½"}, {"item": "1 ⅔ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "1 ⅔"}]', '["Inspect two ½-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Place strawberries in an 8-quart saucepan; gradually stir in pectin. Set over high heat; stir constantly until mixture comes to a full rolling boil that cannot be stirred down. Stir in sugar until dissolved; return to a full rolling boil. Boil hard for 1 minute, stirring constantly. Off heat, skim foam if necessary.", "Pack jam into hot, sterilized jars, filling to within ¼ inch of the top. Run a clean knife or thin spatula around insides of jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and let rest, several inches apart, for 12 to 24 hours. Press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage. Store in a cool, dark area.", "To crush strawberries, wash, hull, then crush strawberries in a bowl, one layer at a time, using a potato masher until yields 1 ⅓ cups crushed berries."]', 4.90, 35, 'AllRecipes', 'https://www.allrecipes.com/recipe/217921/summer-strawberry-jam/', 'https://www.allrecipes.com/thmb/riRZq4HieWLRjILAZLkCxSPezWA=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1051216-30c89a6863314ff4bdd62b728cd42880.jpg', '32 servings', '', '', '', '2025-09-27 08:40:00.770777', '2025-09-27 08:40:00.770777');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (883, 'Low-Sugar Strawberry Jam', '[{"item": "1 ⅓ cups crushed strawberries (wash, hull, then crush one layer at a time using potato masher)", "name": "crushed strawberries (wash, hull, then crush one layer at a time using potato masher)", "unit": "cups", "quantity": "1 ⅓"}, {"item": "⅓ cup unsweetened fruit juice, thawed fruit juice concentrate, or water", "name": "unsweetened fruit juice, thawed fruit juice concentrate, or water", "unit": "cup", "quantity": "⅓"}, {"item": "1 ½ tablespoons Ball RealFruit Low or No-Sugar Needed Pectin", "name": "Ball RealFruit Low or No-Sugar Needed Pectin", "unit": "tablespoons", "quantity": "1 ½"}, {"item": "½ cup granulated sugar, sugar substitute, or honey (or less, depending on taste)", "name": "granulated sugar, sugar substitute, or honey (or less, depending on taste)", "unit": "cup", "quantity": "½"}]', '["Prepare waterbath canner, jars and lids according to manufacturers instructions, if preserving. Prepare and measure ingredients for recipe.", "Combine prepared fruit with fruit juice in a large saucepan. Gradually stir in Ball RealFruit Low or No-Sugar Needed Pectin. Bring mixture to a full rolling boil.", "Add sugar, sugar substitute, or honey, if using. Return mixture to a full rolling boil. Boil hard 1 minute, stirring constantly. Remove from heat. Skim foam if necessary.", "Ladle hot jam into hot jars, one at a time, leaving 1/4 inch headspace. Wipe rims. Center lids on jars. Apply bands and adjust to fingertip tight.", "Place filled jars in canner ensuring jars are covered by 1 to 2 inches of water. Place lid on canner. Bring water to gentle, steady boil.", "Process jars for 10 minutes, adjusting for altitude. Turn off heat, remove lid and let jars stand for 5 minutes.", "Remove jars and cool. Check lids for seal after 24 hours. Lid should not flex up and down when center is pressed. Clean and store according to manufacturers instructions."]', 4.90, 22, 'AllRecipes', 'https://www.allrecipes.com/recipe/217924/low-sugar-no-sugar-strawberry-jam/', 'https://www.allrecipes.com/thmb/YCIwel94WDwXW6mXkBYvCBiMaLo=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/976448-f2f3146968904c43a627f4a4a6bce0de.jpg', '32 servings', '', '', '', '2025-09-27 08:40:00.771921', '2025-09-27 08:40:00.771921');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (845, 'Nectarine Jam', '[{"item": "4 pounds nectarines - peeled, pitted, and chopped", "name": "nectarines - peeled, pitted, and chopped", "unit": "pounds", "quantity": "4"}, {"item": "2 tablespoons lemon juice", "name": "lemon juice", "unit": "tablespoons", "quantity": "2"}, {"item": "3 cups sugar", "name": "sugar", "unit": "cups", "quantity": "3"}, {"item": "1 (1.75 ounce) package fruit pectin", "name": "fruit pectin", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "3 drops almond extract, or to taste", "name": "almond extract, or to taste", "unit": "drops", "quantity": "3"}]', '["Inspect five (½-pint) jars for cracks and rings for rust; discard any defective ones. Immerse the jars and rings in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Measure finely chopped, peeled nectarines; you should have 4 1/2 cups. Add to a heavy pot and immediately mix with lemon juice so they don''t turn brown.", "Combine 1/4 cup sugar and fruit pectin in a small bowl. Add to nectarines in the pot; stir well. Slowly bring mixture to a full rolling boil that does not stop bubbling when stirred. Add remaining 2 ¾ cups sugar and stir to dissolve, making sure to scrape over the bottom of the pot. Boil for exactly 1 minute, stirring constantly.", "Remove from heat and stir in almond extract.", "Pack jam into the prepared jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe the rims with a moist paper towel to remove any spills. Top with the lids and tightly screw on the rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart. Let rest for 24 hours without moving the jars.", "Gently press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 4.60, 10, 'AllRecipes', 'https://www.allrecipes.com/recipe/265910/nectarine-jam/', 'https://www.allrecipes.com/thmb/tRf6nfgQL9d_yE3n0Q7Fal5FExk=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/265910-nectarine-jam-antoinettbennettsbcglobalnet-4x3-1-001bd67824754bab9a754ef4513640bd.jpg', '40 servings', '', '', '', '2025-09-27 08:37:35.9927', '2025-09-27 08:37:35.9927');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (884, 'Strawberry Freezer Jam', '[{"item": "4 cups halved fresh strawberries", "name": "halved fresh strawberries", "unit": "cups", "quantity": "4"}, {"item": "1 ½ cups granulated sugar", "name": "granulated sugar", "unit": "cups", "quantity": "1 ½"}, {"item": "1 (1.75 ounce) package dry pectin", "name": "dry pectin", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "¼ cup water", "name": "water", "unit": "cup", "quantity": "¼"}]', '["Gather all ingredients.", "Crush strawberries using a potato masher in a large bowl. (Alternatively, pulse strawberries in a food processor until just finely chopped but not pureed.) Drain strawberries through a fine mesh strainer; reserve juice for another use. Transfer 2 cups crushed strawberries to a large bowl (reserve any extra strawberries for another use); add sugar, and stir until evenly combined. Let stand at room temperature for 10 minutes, stirring occasionally.", "Meanwhile, stir pectin and water together in a small saucepan until combined. Bring to a boil over medium-high heat; boil, stirring constantly, until a thick paste forms and pectin is fully dissolved, about 2 minutes.", "Stir boiling pectin-water mixture into strawberry mixture in large bowl; stir constantly for 3 minutes. To check if jam is set, coat back of a spoon with jam; run a finger through it. If jam remains separated, it is properly set. If jam runs back together, it is not set; stir until set, 3 more minutes. Pour into 2 sterilized pint-size jars or other storage containers, leaving a 1/2-inch space at the top to allow for expansion. Place tops on containers, and leave at room temperature for 24 hours.", "Place in refrigerator for at least 1 hour before serving, or freeze until ready to use or up to 3 months. Enjoy!"]', 4.70, 167, 'AllRecipes', 'https://www.allrecipes.com/recipe/51835/strawberry-freezer-jam/', 'https://www.allrecipes.com/thmb/rAo9oRHvL9iiDSgdu7_RNBG49_w=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/51835-strawberry-freezer-jam-ddmfs-5627-hero-1x2-ac8c6cc42ffd4b5998f299a5f9bba4b9.jpg', '32 servings', '', '', '', '2025-09-27 08:40:00.772755', '2025-09-27 08:40:00.772755');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (846, 'Ultimate Seville orange marmalade', '[{"name": "1.3kgSeville  orange", "unit": "", "amount": ""}, {"name": "2lemonsjuice only", "unit": "", "amount": ""}, {"name": "2.6kgpreserving or  granulated sugar", "unit": "", "amount": ""}]', '["Put the whole oranges and lemon juice in a large preserving pan and cover with 2 litres/4 pints water - if it does not cover the fruit, use a smaller pan. If necessary weight the oranges with a heat-proof plate to keep them submerged. Bring to the boil, cover and simmer very gently for around 2 hours, or until the peel can be easily pierced with a fork.", "Warm half the sugar in a very low oven. Pour off the cooking water from the oranges into a jug and tip the oranges into a bowl. Return cooking liquid to the pan. Allow oranges to cool until they are easy to handle, then cut in half. Scoop out all the pips and pith and add to the reserved orange liquid in the pan. Bring to the boil for 6 minutes, then strain this liquid through a sieve into a bowl and press the pulp through with a wooden spoon - it is high in pectin so gives marmalade a good set.", "Pour half this liquid into a preserving pan. Cut the peel, with a sharp knife, into fine shreds. Add half the peel to the liquid in the preserving pan with the warm sugar. Stir over a low heat until all the sugar has dissolved, for about 10 minutes, then bring to the boil and bubble rapidly for 15- 25 minutes until setting point is reached.", "Take pan off the heat and skim any scum from the surface. (To dissolve any excess scum, drop a small knob of butter on to the surface, and gently stir.) Leave the marmalade to stand in the pan for 20 minutes to cool a little and allow the peel to settle; then pot in sterilised jars, seal and label. Repeat from step 3 for second batch, warming the other half of the sugar first."]', 5.00, 113, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/ultimate-seville-orange-marmalade', 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/marmalade-6fb7d04.jpg?resize=500,454', '4', 'PT1H30M', 'PT2H30M', 'PT4H', '2025-09-27 08:37:51.875412', '2025-09-27 08:37:51.875412');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (847, 'Habanero Pineapple Jam', '[{"item": "1 (20 ounce) can crushed pineapple", "name": "crushed pineapple", "unit": "(20 ounce) can", "quantity": "1"}, {"item": "6 ½ cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "6 ½"}, {"item": "1 ½ cups apple cider vinegar", "name": "apple cider vinegar", "unit": "cups", "quantity": "1 ½"}, {"item": "½ teaspoon salt", "name": "salt", "unit": "teaspoon", "quantity": "½"}, {"item": "1  red bell pepper, minced", "name": "red bell pepper, minced", "unit": "", "quantity": "1"}, {"item": "12  habanero peppers - stems and membranes removed, minced", "name": "habanero peppers - stems and membranes removed, minced", "unit": "", "quantity": "12"}, {"item": "1 cup shredded carrots", "name": "shredded carrots", "unit": "cup", "quantity": "1"}, {"item": "¾ cup powdered fruit pectin, or more to taste", "name": "powdered fruit pectin, or more to taste", "unit": "cup", "quantity": "¾"}]', '["Drain pineapple well, reserving 1/2 cup pineapple juice.", "Inspect eight 1/2-pint jars for cracks and rings for rust, discarding any defective ones. Wash new, unused lids and rings in warm soapy water. Place jars and lids on a rack in a large pot of boiling water; sterilize for about 5 minutes. Remove jars from water and place on a kitchen towel or wooden board.", "Mix sugar, apple cider vinegar, and salt together in an 8-quart pot and cook over medium heat to dissolve the sugar. Bring to a boil and add pineapple, reserved pineapple juice, minced bell pepper, habanero peppers, and carrots. Bring back to a rapid boil for about 5 minutes, stirring constantly.", "Add powdered pectin, 1 tablespoon at a time, mixing well after each addition. Let jam boil for 5 minutes, stirring constantly. Remove from heat and ladle into sterilized jars, leaving 1/4 inch at the top. Wipe off any spillage around the rims of the jars using a damp paper towel.", "Screw on tops and rings. Place the sealed jars back in boiling water for 5 to 10 minutes. Remove jars from boiling water and place on a kitchen towel or wooden board. Allow to cool completely, 12 to 24 hours. The tops of your jars should pop as they start to cool and seal."]', 5.00, 8, 'AllRecipes', 'https://www.allrecipes.com/recipe/269195/habanero-pineapple-jam/', 'https://www.allrecipes.com/thmb/hAngOM5J96p73XmTpcozTu2pGQQ=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1044375-7416456666364c699ce72e044a48abd0.jpg', '64 servings', '', '', '', '2025-09-27 08:37:51.877702', '2025-09-27 08:37:51.877702');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (848, 'Red Raspberry-Orange Jam', '[{"item": "3 cups crushed ripe raspberries", "name": "crushed ripe raspberries", "unit": "cups", "quantity": "3"}, {"item": "3  oranges", "name": "oranges", "unit": "", "quantity": "3"}, {"item": "  lemon, juiced", "name": "lemon, juiced", "unit": "", "quantity": ""}, {"item": "4 cups white sugar, divided", "name": "white sugar, divided", "unit": "cups", "quantity": "4"}, {"item": "1 (1.75 ounce) package fruit pectin (such as Sure-Jell® Pectin Light for less or no sugar)", "name": "fruit pectin (such as Sure-Jell® Pectin Light for less or no sugar)", "unit": "(1.75 ounce) package", "quantity": "1"}]', '["Inspect 7 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Add raspberries to a blender or food processor; blend until smooth. Pour into a large saucepan.", "Cut off orange zest and finely chop. Add to the saucepan with the raspberries. Segment oranges and remove all the white pith, seeds, and fibrous membranes. Cut orange filets into small pieces; you should have 2 cups. Combine orange pieces with lemon juice in a bowl and mix. Add orange pieces and 1/4 cup sugar to the saucepan with the raspberries.", "Stir in pectin and slowly bring to a full rolling boil. Add remaining sugar and stir to dissolve. Return to a full rolling boil on high heat and boil for exactly 1 minute, stirring constantly. Skim off any foam with a ladle or a large spoon.", "Remove from heat and ladle jam into the prepared jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any spills. Top with lids and tightly screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart. Let rest for 24 hours without moving the jars. Gently press the center of each lid with a finger to ensure the lid does not move up or down. Remove the rings for storage and store in a cool, dark area."]', 5.00, 1, 'AllRecipes', 'https://www.allrecipes.com/recipe/265911/red-raspberry-orange-jam/', 'https://www.allrecipes.com/thmb/P1ncOU1gNrbjh-swJWZrWkNdnXw=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/5493703-f3cd284adadd473ab1c9aa32813415a0.jpg', '112 servings', '', '', '', '2025-09-27 08:37:51.879378', '2025-09-27 08:37:51.879378');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (849, 'Strawberry Orange Creamsicle Jam', '[{"item": "3 cups crushed strawberries", "name": "crushed strawberries", "unit": "cups", "quantity": "3"}, {"item": "⅔ cup frozen orange juice concentrate", "name": "frozen orange juice concentrate", "unit": "cup", "quantity": "⅔"}, {"item": "¼ cup orange liqueur", "name": "orange liqueur", "unit": "cup", "quantity": "¼"}, {"item": "6 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "6"}, {"item": "1 (3 ounce) pouch liquid fruit pectin", "name": "liquid fruit pectin", "unit": "(3 ounce) pouch", "quantity": "1"}, {"item": "1 ½ teaspoons vanilla extract", "name": "vanilla extract", "unit": "teaspoons", "quantity": "1 ½"}, {"item": "1  orange, zested", "name": "orange, zested", "unit": "", "quantity": "1"}, {"item": "7  half pint canning jars with lids and rings", "name": "half pint canning jars with lids and rings", "unit": "", "quantity": "7"}]', '["Combine strawberries, orange juice concentrate, and orange liqueur in an 8 to 10-quart pot. Gradually stir sugar into mixture until dissolved. Bring mixture to a boil, continually stirring. Add pectin, vanilla extract, and orange zest; return to a boil and boil for 1 minute. Remove from heat and skim off foam using a metal spoon.", "Sterilize the jars and lids in boiling water for at least 5 minutes. Pack jam into the hot, sterilized jars, filling the jars to within 1/4 inch of the top. Run a knife or a thin spatula around the insides of the jars after they have been filled to remove any air bubbles. Wipe the rims of the jars with a moist paper towel to remove any food residue. Top with lids, and screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower jars into the boiling water using a holder. Leave a 2-inch space between the jars. Pour in more boiling water if necessary to bring the water level to at least 1 inch above the tops of the jars. Bring the water to a rolling boil, cover the pot, and process for 5 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool. Once cool, press the top of each lid with a finger, ensuring that the seal is tight (lid does not move up or down at all). Store in a cool, dark area."]', 4.40, 14, 'AllRecipes', 'https://www.allrecipes.com/recipe/233755/strawberry-orange-creamsicle-jam/', 'https://www.allrecipes.com/thmb/3EKukiY_ZFqwGSUu0CzhDmsgj1M=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/1038578-b3b8ed44fca349f08aa9ac2d70daac03.jpg', '112 servings', '', '', '', '2025-09-27 08:37:51.880552', '2025-09-27 08:37:51.880552');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (852, 'Fresh Papaya Jam', '[{"item": "5 cups mashed ripe papaya", "name": "mashed ripe papaya", "unit": "cups", "quantity": "5"}, {"item": "¼ cup orange juice", "name": "orange juice", "unit": "cup", "quantity": "¼"}, {"item": "1 ⅓ (1.75 ounce) packages dry pectin", "name": "dry pectin", "unit": "(1.75 ounce) packages", "quantity": "1 ⅓"}, {"item": "5 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "5"}]', '["Stir together papaya, orange juice, and pectin in a very large pot over medium-high heat until it begins to boil. Stir constantly as the mixture heats. Once boiling, stir in the sugar, and return to a boil, stirring constantly. Once the jam has returned to a boil, start a timer, and boil for exactly 1 minute.", "Ladle the hot jam into the hot, sterilized canning jars and seal with lids and rings. Allow to cool to room temperature, and refrigerate any jars that do not seal."]', 4.60, 22, 'AllRecipes', 'https://www.allrecipes.com/recipe/162151/fresh-papaya-jam/', 'https://www.allrecipes.com/thmb/7S4pdumTM3QcWZfKQL5E8M0jZIQ=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/Fresh-Papaya-Jam-2x1-1-2000-d570b3dc3b594baab60418088a160ea5.jpg', '64 servings', '', '', '', '2025-09-27 08:38:01.788175', '2025-09-27 08:38:01.788175');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (853, 'Peach & cardamom jam', '[{"name": "1.2kg ripepeaches(about 12), halved and stoned", "unit": "", "amount": ""}, {"name": "1lemonzested and juiced", "unit": "", "amount": ""}, {"name": "15cardamom podscracked, seeds removed and reserved", "unit": "", "amount": ""}, {"name": "800gcaster sugar", "unit": "", "amount": ""}]', '["Sterilise three 400g jars (see tip, below) and put a saucer in the freezer to chill (you will use this later to test whether the jam has set).", "Cut each peach half into six to eight pieces, then tip the fruit into a large saucepan set over a medium- low heat along with the lemon zest and juice. Bring the mixture to a gentle simmer and bubble for 10 mins –the peaches will turn juicy and start to soften.", "Crush the cardamom seeds with a pestle and mortar and add these to the peach mixture along with the sugar. Reduce the heat to low and stir until the sugar has dissolved. Once dissolved, turn up the heat to medium-high and bubble for 15-20 mins, stirring occasionally to prevent the jam from catching, until the jam is set and has reached 105C on a sugar thermometer. If you don’t have a thermometer, spoon a little of the jam onto the chilled saucer, leave it for 30 seconds, then draw a line through it with your finger – if it wrinkles and doesn’t flood the gap, it’s ready. If not, boil for 2 mins more, then check again.", "Leave to cool for 10-15 mins to allow a more even distribution of peach chunks. You may find that a little foam rises to the top; if so, simply stir this back into the jam. Use a funnel to help you fill the jars or do this with a ladle, being very careful as the jam will still be extremely hot. Seal with the lids, then store in a cool, dark place. Unopened jars will keep for up to a year. Once opened, keep the jam in the fridge and eat within a month."]', 5.00, 6, 'BBC Good Food', 'https://www.bbcgoodfood.com/recipes/peach-cardamom-jam', 'https://images.immediate.co.uk/production/volatile/sites/30/2021/08/Peach-jam-3ea7070.jpg?resize=768,713', '3', 'PT10M', 'PT30M', 'PT40M', '2025-09-27 08:38:28.195827', '2025-09-27 08:38:28.195827');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (854, 'Spiked Peach Jam with Ginger', '[{"item": "4 ½ cups finely chopped, peeled peaches", "name": "finely chopped, peeled peaches", "unit": "cups", "quantity": "4 ½"}, {"item": "3 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "3"}, {"item": "1 teaspoon ground ginger", "name": "ground ginger", "unit": "teaspoon", "quantity": "1"}, {"item": "1 (1.75 ounce) package light pectin (such as Sure-Jell)", "name": "light pectin (such as Sure-Jell)", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "⅓ cup amaretto liqueur, or more to taste", "name": "amaretto liqueur, or more to taste", "unit": "cup", "quantity": "⅓"}]', '["Place peaches in a glass or plastic container with a lid; immediately stir in lemon juice to prevent browning. Stir in sugar and ginger until well combined. Cover the bowl; set aside in a cool place, 8 hours to overnight.", "Inspect 5 half-pint jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Transfer peaches and all accumulated liquid to a large pot; stir in pectin and slowly bring mixture to a full rolling boil that does not stop bubbling when stirred. Cook for 1 minute, stirring constantly.", "Off heat, stir in amaretto liqueur.", "Pack hot jam into the prepared jars, filling to within ¼ inch of tops. Run a clean knife or thin spatula around insides of jars to remove any air bubbles. Wipe rims with a moist paper towel to remove any residue. Top with lids and tightly screw on rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water; bring to a boil and lower jars 2 inches apart into boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove jars from the stockpot; place on a cloth-covered or wood surface, several inches apart. Rest for 24 hours without moving. Gently press center of each lid with a finger to ensure the lid does not move up or down. Remove rings for storage and store in a cool, dark area."]', 4.90, 8, 'AllRecipes', 'https://www.allrecipes.com/recipe/265386/spiked-peach-jam-with-ginger/', 'https://www.allrecipes.com/thmb/jzAp-PZ4eN1lSBAkvJ4sZ8qllD4=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/9354098-82173b5f0e6f402395b2cc548907f1ba.jpg', '40 servings', '', '', '', '2025-09-27 08:38:28.199401', '2025-09-27 08:38:28.199401');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (855, 'Ginger Peach Jam', '[{"item": "4 ½ cups fresh peaches - peeled, pitted, and chopped", "name": "fresh peaches - peeled, pitted, and chopped", "unit": "cups", "quantity": "4 ½"}, {"item": "¼ cup finely chopped crystallized ginger", "name": "finely chopped crystallized ginger", "unit": "cup", "quantity": "¼"}, {"item": "1 (1.75 ounce) package powdered fruit pectin", "name": "powdered fruit pectin", "unit": "(1.75 ounce) package", "quantity": "1"}, {"item": "6 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "6"}, {"item": "½ teaspoon butter", "name": "butter", "unit": "teaspoon", "quantity": "½"}]', '["Inspect four pint-sized jars for cracks and rings for rust, discarding any defective ones. Immerse in simmering water for at least 5 minutes or until jam is ready. Wash new, unused lids and rings in warm soapy water.", "At the same time, bring peaches, ginger, and pectin to a boil in a large saucepan over medium heat. Add sugar and butter; cook and stir until sugar is dissolved. Stir constantly and return to a boil, about 1 minute. Remove from the heat and skim off any foam with a spoon.", "Pack peach mixture into hot, sterilized jars, filling to within 1/4 inch of the top. Run a clean knife or thin spatula around the insides of the jars to remove any air bubbles. Wipe the rims with a moist paper towel to remove any residue. Top with lids and screw rings on tightly.", "Place a rack into the bottom of a large stockpot and fill halfway with water. Bring to a boil over high heat, then carefully lower jars 2 inches apart into the boiling water using a holder. Pour in more boiling water to cover jars by at least 1 inch. Bring to a rolling boil, cover, and process for 10 minutes.", "Remove the jars from the stockpot and let rest on a cloth-covered or wood surface, several inches apart, until cool.", "Once cool, press the top of each lid with a finger, ensuring that the seal is tight and the lid does not move up or down at all. Store in a cool, dark area."]', 4.80, 80, 'AllRecipes', 'https://www.allrecipes.com/recipe/218529/ginger-peach-jam/', 'https://www.allrecipes.com/thmb/B3F08-EL2iVRp9stbiIZvlBhgvU=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/8378377-41a3f8f52d9e492aa065fe177642118d.jpg', '128 servings', '', '', '', '2025-09-27 08:38:28.200572', '2025-09-27 08:38:28.200572');
INSERT INTO public.recipes (id, title, ingredients, instructions, rating, review_count, source, source_url, image_url, servings, prep_time, cook_time, total_time, created_date, scraped_date) VALUES (856, 'Fresh Spiced Peach Jam', '[{"item": "6 cups peeled and chopped fresh peaches", "name": "peeled and chopped fresh peaches", "unit": "cups", "quantity": "6"}, {"item": "3 cups white sugar", "name": "white sugar", "unit": "cups", "quantity": "3"}, {"item": "3 tablespoons lemon juice", "name": "lemon juice", "unit": "tablespoons", "quantity": "3"}, {"item": "½ teaspoon ground cinnamon", "name": "ground cinnamon", "unit": "teaspoon", "quantity": "½"}, {"item": "½ teaspoon ground nutmeg", "name": "ground nutmeg", "unit": "teaspoon", "quantity": "½"}, {"item": "½ teaspoon ground allspice", "name": "ground allspice", "unit": "teaspoon", "quantity": "½"}]', '["Immerse the canning jars in simmering water until jam is ready. Wash new, unused lids and rings in warm soapy water.", "Mix together peaches, sugar, lemon juice, cinnamon, nutmeg, and allspice in a large pot. Bring to a boil; cook, stirring occasionally, until peaches are soft, about 15 minutes. Remove from heat.", "Mash peaches with an immersion blender or potato masher to desired size and texture. Return the pot to heat; continue cooking jam until thickened, about 10 minutes more.", "Pack jam into hot jars, filling to within 1/4 inch of the top. Wipe the rims with a clean, damp cloth. Top with the lids and screw on the rings.", "Place a rack in the bottom of a large stockpot and fill halfway with water. Bring to a boil and lower in jars using a holder, placing them 2 inches apart. Pour in more boiling water to cover the jars by at least 1 inch. Bring water to a rolling boil, cover the pot, and process for 10 minutes.", "Remove the jars from the stockpot and place onto a cloth-covered or wood surface, several inches apart, until cool, about 12 hours. Press the top of each lid with a finger, ensuring that the lid does not move up or down and seal is tight. Remove the rings for storage and store in a cool, dark area."]', 4.80, 27, 'AllRecipes', 'https://www.allrecipes.com/recipe/254182/fresh-spiced-peach-jam/', 'https://www.allrecipes.com/thmb/eI0Akl_IvqJq5Dp__qyP31ualjo=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/9362668-ec6a18f4a1bd4640bdeee31645c54e6e.jpg', '60 servings', '', '', '', '2025-09-27 08:38:28.201376', '2025-09-27 08:38:28.201376');
